	id_30021 = {30021, "龙骧骑", 1, "1", "龙骧骑爱他的宝马胜过一切，每个月都会带着他的宝马去做毛发护理，完事以后还要亲自为鳞甲打蜡。", "zhan_bing_longxiangqi.png", "head_longxiang.png", "quan_bin_longxiangqi.png", "quan_bin_longxiangqi.png", nil, 32, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5110, nil, 7390, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30021, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|5900,1|30|5602", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30022 = {30022, "白马义从", 1, "1", "白马义从在战场上是风一样的存在，你或许只是感觉到一阵风吹过，身上就多了一条伤痕。", "zhan_bing_baimayicong.png", "head_baimayicong.png", "quan_bin_baimayicong.png", "quan_bin_baimayicong.png", nil, 32, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5060, nil, 7370, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30022, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|5600,1|30|3602", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30023 = {30023, "重骑兵", 1, "1", "重骑兵无论是人还是马都装上了重甲，这导致他的速度也不快，你要知道，马一旦失去平衡倒在地上，要把它扶起来可就没那么容易了。", "zhan_bing_zhongqibing.png", "head_zhongqibing.png", "quan_bin_zhongqibing.png", "quan_bin_zhongqibing.png", nil, 32, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5010, nil, 7060, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30023, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|5600,1|30|3302", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30024 = {30024, "禁卫兵", 1, "1", "禁卫兵走到哪都会带着他的大铁锤，他会用他的大铁锤战斗、摘香蕉、必要时候还可以当作板凳来用！", "zhan_jiang_jinweijun.png", "head_jinweijun.png", "quan_bin_jinweijun.png", "quan_bin_jinweijun.png", nil, 32, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5190, nil, 7080, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30024, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|6100,1|30|5702", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30025 = {30025, "大戟士", 1, "1", "大戟士的任务通常都是守卫一些重要的人物，敌人常常会在击败他后被他一身耀眼的装备而吸引，重要人物则可以趁机逃跑。", "zhan_bing_dajishi.png", "head_dajishi.png", "quan_bin_dajishi.png", "quan_bin_dajishi.png", nil, 32, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5260, nil, 7330, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30025, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|1200,1|30|1102", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30026 = {30026, "先登死士", 1, "1", "先登死士有一门绝技：他可以在城墙上射满弓箭，然后踩着这些弓箭登墙。但是，如果弓箭射到一半不够的话，他恐怕就得在墙上过夜了。", "zhan_jiang_xiandengsishi.png", "head_xiandengsishi.png", "quan_bin_xiandengsishi.png", "quan_bin_xiandengsishi.png", nil, 32, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5160, nil, 7230, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30026, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|6000,1|30|5802", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30027 = {30027, "刺客", 1, "1", "刺客的一袭绿衣和杂草般的头型与茅房后的草丛融为一体，这使得他的刺杀目标防不胜防。", "zhan_bing_cike.png", "head_cike.png", "quan_bin_cike.png", "quan_bin_cike.png", nil, 32, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5290, nil, 7260, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30027, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|7800,1|30|3602", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30028 = {30028, "象兵", 1, "1", "这只大象每天都胆颤心惊的，因为每当它的主人使出下劈的时候就要当心自己的象牙被削掉。", "zhan_bing_xiangbing.png", "head_xiangbing.png", "quan_bin_xiangbing.png", "quan_bin_xiangbing.png", nil, 32, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5150, nil, 7120, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30028, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|5900,1|30|3302", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30029 = {30029, "乐师", 1, "1", "她的打扮曾经风靡一时，几乎所有的琵琶女都效仿她。到了后来，还有一首诗专门为她而写，有一句什么来着，“犹抱琵琶半遮面”？", "zhan_jiang_wujiang7.png", "head_wujiang7.png", "quan_jiang_wujiang7.png", "quan_jiang_wujiang7.png", nil, 32, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5090, nil, 7370, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30029, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|3400,1|30|3202", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30030 = {30030, "医师", 1, "1", "十分粗心的医师妹子，经常会忘记自己手里拿的到底是毛笔还是针筒。", "zhan_jiang_wenguan6.png", "head_wenguan6.png", "quan_jiang_wenguan6.png", "quan_jiang_wenguan6.png", nil, 32, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6160, nil, 7050, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30030, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|4900,1|30|1002", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30031 = {30031, "舞姬", 1, "1", "舞姬每天只做两件事情，一件是对友军唱”请把我的歌带回你的家“，一件是对敌人唱”请把你的怒气留下！“。", "bin_wuji.png", "head_wuji.png", "quan_bin_wuji.png", "quan_bin_wuji.png", nil, 32, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5080, nil, 7030, "999|0,999|0,999|0", 400, 125, 2, 5500, 5500, 5500, 10, 0, 0, 0, 0, nil, nil, 1250, 100, 0, 0, 0, 0, nil, nil, 2, nil, nil, 2, nil, 30031, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|3200,1|30|3602", 1, 200, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 1, nil, nil, nil, nil, nil, },
	id_30032 = {30032, "重枪兵", 1, "1", "重枪兵在一次偶然的事件中得到了一把会吸血的长枪。从此以后，他的屋子里就再也没出现过蚊子。", "zhan_bing_changqiang.png", "head_changqiang.png", "quan_bin_changqiangbing.png", "quan_bin_changqiangbing.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7390, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30032, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|5701,1|30|1203", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30033 = {30033, "战鼓兵", 1, "1", "战鼓兵有一项绝技，就是可以左手右手同时打出不同的鼓调，据说他的老师姓周。", "zhan_jiang_qigubing.png", "head_qigubing.png", "quan_bin_qigubing.png", "quan_bin_qigubing.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5090, nil, 7130, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30033, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|3201,1|30|3403", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30034 = {30034, "高级刺客", 1, "1", "高级刺客有太多的仇人，所以他决定，以后刺杀之前先把目标弄晕，绝不再让任何一个人看到他的脸。", "zhan_bing_cike.png", "head_cike.png", "quan_bin_cike.png", "quan_bin_cike.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5290, nil, 7080, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30034, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|7801,1|30|3603", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30035 = {30035, "妖术师", 1, "1", "妖术师总说“我们妖术师再妖但也是人啊！”，于是人们改口叫她人妖术师。", "zhan_jiang_wushushi.png", "head_wushushi.png", "quan_bin_wushushi.png", "quan_bin_wushushi.png", nil, 33, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6100, nil, 7190, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30035, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|1101,1|30|3403", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30036 = {30036, "长弓手", 1, "1", "长弓手喜欢一切长长的东西，长长的弓弦，长长的手臂，长长的靴子还有长长的大马尾辫。", "zhan_jiang_gongbing.png", "head_gongbing.png", "quan_bin_gongbing.png", "quan_bin_gongbing.png", nil, 33, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5170, nil, 7230, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30036, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|101,1|30|3603", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30037 = {30037, "狂战女神", 1, "1", "狂战女神的魅力在战场上表现的淋漓尽致，他只要说一句“呵呵”就会有数不尽的士兵们为她卖命。", "zhan_jiang_jianwunvshen.png", "head_jianwunvshen.png", "quan_bin_jianwunvshen.png", "quan_bin_jianwunvshen.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5180, nil, 7150, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30037, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|3601,1|30|1103", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30038 = {30038, "皇廷戟士", 1, "1", "皇廷戟士无论是在什么战斗中都会冲到最前面，他的英姿早已迷的那些宫女们花枝招展了。", "zhan_bing_dajishi.png", "head_dajishi.png", "quan_bin_dajishi.png", "quan_bin_dajishi.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5260, nil, 7290, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30038, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|3601,1|30|1103", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30039 = {30039, "贪狼侠客", 1, "1", "贪狼侠客会把飞镖放在一起摩擦产生巨大的静电，带有静电的飞镖会连在一起作为近战用的兵器，所谓进可攻退可守。", "zhan_bing_feibiaoke.png", "head_feibiao.png", "quan_bin_feibiao.png", "quan_bin_feibiao.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5250, nil, 7400, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30039, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|1101,1|30|9603", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30040 = {30040, "疾风轻骑", 1, "1", "疾风轻骑有一匹多愁善感的马，每当跑得很快的时候马就会流下眼泪。马：“我只是跑太快沙子进眼睛里了！”", "zhan_bing_qibing.png", "head_qibing.png", "quan_bin_qibing.png", "quan_bin_qibing.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7290, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30040, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|5601,1|30|3403", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30041 = {30041, "高级影卫", 1, "1", "高级影卫最近迷上了种些带毒的花花草草，他那两把影刃用来做割草刀真是最适合不过了。", "zhan_jiang_yingwei.png", "head_yingwei.png", "quan_bin_yingwei.png", "quan_bin_yingwei.png", nil, 33, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5270, nil, 7280, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30041, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|1101,1|30|7803", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
	id_30042 = {30042, "重装弩兵", 1, "1", "重装弩兵早已习惯了长时间的拉锯战，为了确保弹药充足，他把箭矢藏在了盔甲的每一个部位。", "zhan_jiang_qiangnu.png", "head_qiangnu.png", "quan_bin_feibiao.png", "quan_bin_qiangnubing.png", nil, 33, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5070, nil, 7170, "999|0,999|0,999|0", 500, 150, 2, 6000, 6000, 6000, 20, 0, 0, 5, 5, nil, nil, 1500, 200, 0, 0, 50, 50, nil, nil, 3, nil, nil, 3, nil, 30042, 0, 1000, 5000, 10000, 500, "500", nil, "1|20|3501,1|30|1103", 1, 300, nil, 0, nil, nil, 50, nil, nil, 100, 1, 0, 0, 0, nil, nil, 0, nil, nil, 2, nil, nil, nil, nil, nil, },
