	id_50018 = {50018, "董旻", 1, "1", "董卓之弟，随董卓趁乱进京，后董卓迁都长安，任命董旻为左将军，封鄠侯，统领部队，为其爪牙。", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_wenguan1.png", "quan_jiang_wenguan1.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6120, nil, 7150, "999|0,999|0,999|0", 600, 600, 2, 6300, 6000, 7100, 60, nil, nil, 14, 15, nil, nil, 4000, 600, nil, nil, 142, 157, nil, nil, 4, nil, nil, 4, nil, 10123, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|5601,2|4|403,2|5|9302", 1, 300, "10211,10212", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50019 = {50019, "董承", 1, "1", "汉献帝的岳父。曾任东汉车骑将军。董卓乱京时，董承送献帝回洛阳。曹操专权后，董承密谋除掉曹操，将献帝的密诏带给刘备。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_wujiang5.png", "quan_jiang_wujiang5.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6040, nil, 7270, "999|0,999|0,999|0", 700, 600, 2, 7000, 7000, 5900, 60, nil, nil, 15, 15, nil, nil, 4000, 600, nil, nil, 150, 150, nil, nil, 4, nil, nil, 4, nil, 10124, 2, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|4801,2|4|3603,2|5|9302", 1, 300, "10221,10222", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50020 = {50020, "李榷", 1, "1", "原为董卓的部将。董卓被杀后伙同李傕、张济、樊稠等人，占领长安，杀死王允等人，把持朝廷大权。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6050, nil, 7290, "999|0,999|0,999|0", 700, 600, 2, 7100, 7500, 5400, 60, nil, nil, 15, 14, nil, nil, 4000, 600, nil, nil, 150, 142, nil, nil, 4, nil, nil, 4, nil, 10125, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|3301,2|4|7803,2|5|9302", 1, 300, "10231,10232", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50021 = {50021, "郭汜", 1, "1", "原为董卓的部将。董卓被杀后，采贾诩之谋，与原董卓部将，杀死王允等人，占领长安，把持朝廷大权。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6050, nil, 7290, "999|0,999|0,999|0", 700, 600, 2, 6900, 7500, 5700, 60, nil, nil, 15, 14, nil, nil, 4000, 600, nil, nil, 150, 142, nil, nil, 4, nil, nil, 4, nil, 10126, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|7801,2|4|5803,2|5|9302", 1, 300, "10241,10242", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
