	id_50023 = {50023, "曹豹", 1, "1", "先为徐州牧陶谦的部将。后归顺刘备。其女嫁给了吕布。因与张飞结怨后叛刘归顺于吕布。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6030, nil, 7160, "999|0,999|0,999|0", 600, 600, 2, 7000, 6700, 5400, 60, nil, nil, 15, 15, nil, nil, 4000, 600, nil, nil, 150, 150, nil, nil, 4, nil, nil, 4, nil, 10128, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|1201,2|4|3603,2|5|9302", 1, 300, "10261,10262", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50024 = {50024, "成廉", 1, "1", "吕布八健将之一，被张辽统领。在濮阳打败曹操，却被乐进一箭射死。", "zhan_jiang_wenguan4.png", "head_wenguan4.png", "quan_jiang_wenguan4.png", "quan_jiang_wenguan4.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6100, nil, 7040, "999|0,999|0,999|0", 600, 600, 2, 6000, 5700, 7300, 60, nil, nil, 14, 15, nil, nil, 4000, 600, nil, nil, 142, 157, nil, nil, 4, nil, nil, 4, nil, 10129, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|1201,2|4|4203,2|5|9302", 1, 300, "10271,10272", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50025 = {50025, "张邈", 1, "1", "汉末群雄之一，家中富裕，疏财仗义，广交朋友，曾参与讨伐董卓。本来与曹操是好友，经陈宫劝说后，背叛曹操。", "zhan_jiang_wenguan2.png", "head_wenguan2.png", "quan_jiang_wenguan2.png", "quan_jiang_wenguan2.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6110, nil, 7170, "999|0,999|0,999|0", 600, 600, 2, 6200, 5900, 7000, 60, nil, nil, 14, 15, nil, nil, 4000, 600, nil, nil, 142, 157, nil, nil, 4, nil, nil, 4, nil, 10130, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|3601,2|4|5303,2|5|9302", 1, 300, "10281,10282", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50026 = {50026, "牛辅", 1, "1", "董卓的女婿，牛辅被吕布打败，于是带着金银珠宝出逃，部署胡赤儿等人谋财害命，于途中将其斩首送往长安。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_wujiang5.png", "quan_jiang_wujiang5.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6050, nil, 7280, "999|0,999|0,999|0", 600, 600, 2, 6700, 7300, 5400, 60, nil, nil, 15, 14, nil, nil, 4000, 600, nil, nil, 150, 142, nil, nil, 4, nil, nil, 4, nil, 10131, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|7801,2|4|3703,2|5|9302", 1, 300, "10291,10292", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50027 = {50027, "阎象", 1, "1", "袁术的主簿。袁术要称帝时，他引用周文王虽拥有三分之二的天下还向殷称臣的故事进行劝谏。却未被采纳。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_wujiang5.png", "quan_jiang_wujiang5.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6050, nil, 7290, "999|0,999|0,999|0", 600, 600, 2, 6700, 7100, 5000, 60, nil, nil, 15, 14, nil, nil, 4000, 600, nil, nil, 150, 142, nil, nil, 4, nil, nil, 4, nil, 10132, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|1101,2|4|7803,2|5|9302", 1, 300, "10301,10302", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50028 = {50028, "乐就", 1, "1", "袁术麾下之将。袁术攻打曹操之陈时，担任联络的任务。袁术军不幸战败，他也在寿春被曹操军逮捕并遭到斩首。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6030, nil, 7070, "999|0,999|0,999|0", 600, 600, 2, 7000, 6700, 5400, 60, nil, nil, 15, 15, nil, nil, 4000, 600, nil, nil, 150, 150, nil, nil, 4, nil, nil, 4, nil, 10133, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|3601,2|4|3903,2|5|9302", 1, 300, "10311,10312", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50029 = {50029, "李丰", 1, "1", "袁术部将，随袁术进攻曹操之陈时，遭到曹操亲自抵御。与梁刚、乐就、陈纪一同防守寿春。后被曹操攻破，四人皆被杀。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_wenguan3.png", "quan_jiang_wenguan3.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6110, nil, 7260, "999|0,999|0,999|0", 600, 600, 2, 6000, 5700, 6800, 60, nil, nil, 14, 15, nil, nil, 4000, 600, nil, nil, 142, 157, nil, nil, 4, nil, nil, 4, nil, 10134, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|3401,2|4|5703,2|5|9302", 1, 300, "10321,10322", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50030 = {50030, "张勋", 1, "1", "袁术麾下的将领。袁术称帝，张勋被封为大将军。受袁术所命攻打吕布，因己方中离间之计而败退。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6020, nil, 7340, "999|0,999|0,999|0", 600, 600, 2, 6800, 6500, 5300, 60, nil, nil, 15, 15, nil, nil, 4000, 600, nil, nil, 150, 150, nil, nil, 4, nil, nil, 4, nil, 10135, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|5301,2|4|3803,2|5|9302", 1, 300, "10331,10332", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
	id_50031 = {50031, "陈纪", 1, "1", "袁术部将，曾任九江太守。后在曹袁寿春之战中战败，被曹操斩首。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6030, nil, 7020, "999|0,999|0,999|0", 600, 675, 2, 6100, 5800, 6900, 80, nil, nil, 28, 31, nil, nil, 4500, 800, nil, nil, 190, 210, nil, nil, 4, nil, nil, 4, nil, 10136, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|1201,2|4|5603,2|5|9302", 1, 300, "10341,10342", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 8, nil, nil, nil, nil, nil, },
	id_50032 = {50032, "陈兰", 1, "1", "原为袁术部下。袁术称帝后，以陈兰为副将，作为讨伐吕布的七军之一。后来陈兰因袁术奢侈太过而与雷薄逃入嵩山。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_wenguan3.png", "quan_jiang_wenguan3.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6120, nil, 7100, "999|0,999|0,999|0", 500, 675, 2, 5700, 5400, 7000, 80, nil, nil, 28, 31, nil, nil, 4500, 800, nil, nil, 190, 210, nil, nil, 4, nil, nil, 4, nil, 10137, 4, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|4701,2|4|1103,2|5|9302", 1, 300, "10351,10352", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 8, nil, nil, nil, nil, nil, },
	id_50033 = {50033, "胡车儿", 1, "1", "张绣的偏将，据说其“力能负五百斤，日行七百里”。张绣与曹操作战时，胡车儿把典韦灌醉并盗取了典韦的双戟，致使典韦死于宛城之战。", "zhan_jiang_wujiang4.png", "head_wujiang4.png", "quan_jiang_wujiang4.png", "quan_jiang_wujiang4.png", nil, 41, nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6030, nil, 7130, "999|0,999|0,999|0", 600, 600, 2, 6500, 7000, 5100, 60, nil, nil, 15, 14, nil, nil, 4000, 600, nil, nil, 150, 142, nil, nil, 4, nil, nil, 4, nil, 10138, 2, 1000, 5000, 10000, 500, "500", nil, "2|2|200,2|3|1101,2|4|7803,2|5|9302", 1, 300, "10361,10362", nil, nil, nil, 30, nil, nil, 100, nil, nil, 15, nil, nil, nil, 10, nil, nil, 6, nil, nil, nil, nil, nil, },
