	id_60059 = {60059, "许攸", 1, "1", "本为袁绍帐下谋士，后背袁投曹，睿智多谋。并为曹操设下偷袭袁绍军屯粮之所乌巢的计策，袁绍因此而大败于官渡。", "zhan_jiang_xuyou.png", "head_xuyou.png", "quan_jiang_zhangzhao.png", "quan_jiang_xuyou.png", nil, 16, nil, 0, 0, 0, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202160, nil, 202170, "999|0,999|0,999|0", 900, 6000, 2, 8200, 8100, 8100, 1250, 0, 0, 312, 312, nil, nil, 7000, 1500, 0, 0, 375, 375, nil, nil, 6, nil, nil, 6, nil, 10059, 1, 1000, 5000, 10000, 500, "500", "301,3201,4703,9302,1205,", nil, 1, 0, "551,571,591,592", 1, 10059, nil, 30, nil, "0|60059,1|61059,2|62059,3|63059,4|64059", 100, nil, 0, 15, 0, nil, nil, 10, nil, nil, 15, "150|0|0|0,50|50|0|0,50|0|60017|1", nil, "一二三\n四五六", "8,10,12,14", nil, },
