	id_60141 = {60141, "郭图", 1, "1", "袁绍帐下谋士。官渡之战时力主趁机偷袭曹营，在此计失败后为免于责罚而归罪于率军偷袭曹营的张郃、高览，致使二人背袁投曹。", "zhan_jiang_wenguan5.png", "head_wenguan5.png", "quan_jiang_wenguan5.png", "quan_jiang_wenguan5.png", nil, 16, nil, 0, 0, 0, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203800, nil, 203810, "999|0,999|0,999|0", 500, 4800, 2, 7200, 6600, 5300, 690, 0, 0, 172, 172, nil, nil, 7000, 1500, 0, 0, 375, 375, nil, nil, 6, nil, nil, 6, nil, 10141, 4, 1000, 5000, 10000, 500, "500", "200,7801,1103,9302,,", nil, 1, 0, "10391,10392", 1, 10141, nil, 30, nil, "0|60141,1|61141,2|62141,3|63141,4|64141", 100, nil, 0, 15, 0, nil, nil, 10, nil, nil, 15, "150|0|0|0,50|50|0|0,50|0|60017|1", nil, "一二三\n四五六", "8,10,12,14", nil, },
