	id_10003 = {10003, "郭嘉", "郭嘉", 1, "1", "为曹操统一中国北方立下了功勋，史书上称他“才策谋略，世之奇士”。", "zhan_jiang_guojia.png", "head_guojia.png", "quan_jiang_guojia.png", "quan_jiang_guojia.png", "nuqi_guojia.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1040, nil, 1050, "999|0,999|0,999|0", 1300, 2, 9500, 8300, 9900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10003, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|1101,2|4|3603,2|5|9302,2|6|30900,2|7|6107", "31,32,232,33,34", nil, },
	id_10004 = {10004, "曹操", "曹操", 1, "1", "东汉末年著名政治家、军事家、文学家、书法家。三国中曹魏政权的缔造者。其子曹丕称帝后，追尊其为魏武帝。", "zhan_jiang_caocao.png", "head_caocao.png", "quan_jiang_caocao.png", "quan_jiang_caocao.png", "nuqi_caocao.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1060, nil, 1070, "999|0,999|0,999|0", 1300, 2, 10000, 8500, 9500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10004, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|401,2|3|6101,2|4|6803,2|5|9302,2|6|30400,2|7|2307", "12,31,471,41,42", nil, },
