	id_10018 = {10018, "貂蝉", "貂蝉", 1, "2", "王允的义女，国色天香，有倾国倾城之貌，“闭月羞花”中的闭月即为貂蝉。由王允授意施行连环计，借吕布之手除掉了董卓。", "zhan_jiang_diaochan.png", "head_diaochan.png", "quan_jiang_wenguan6.png", "quan_jiang_diaochan.png", "nuqi_diaochan.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1340, nil, 1350, "999|0,999|0,999|0", 1200, 2, 8300, 8100, 9100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10018, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|3601,2|4|3703,2|5|9302,2|6|4905,2|7|31800", "51,161,332,181,182", nil, },
	id_10019 = {10019, "华佗", "华佗", 1, "1", "著名医学家，与董奉、张仲景并称为“建安三神医”。医术全面，尤擅外科，精于手术，被后人称为“外科圣手”、“外科鼻祖”。", "zhan_jiang_huatuo.png", "head_huatuo.png", "quan_jiang_zhangzhao.png", "quan_jiang_huatuo.png", "nuqi_huatuo.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1360, nil, 1370, "999|0,999|0,999|0", 1200, 2, 8200, 7300, 9400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10019, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|3601,2|4|3403,2|5|9302,2|6|4205,2|7|3307", "61,171,282,191,192", nil, },
	id_10020 = {10020, "左慈", "左慈", 1, "1", "东汉末方士，精通五经，懂占星，精于奇门遁甲，可御使鬼神。黄天魔教的创始者之一，立志复活黄天魔皇扰乱天下曾使用道术戏弄于曹操。传说丹鼎派道术是从他一脉相传。", "zhan_jiang_zuoci.png", "head_zuoci.png", "quan_jiang_zuoci.png", "quan_jiang_zuoci.png", "nuqi_zuoci.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1380, nil, 1390, "999|0,999|0,999|0", 1200, 2, 9400, 8200, 9900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10020, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|3601,2|4|8901,2|5|9302,2|6|31600,2|7|10209", "201,202,93,203,204", nil, },
	id_10021 = {10021, "于吉", "于吉", 1, "1", "著名道士，黄天魔教的创始者之一，立志复活黄天魔皇扰乱天下，精通奇门遁甲，鬼神莫测。道教经典《太平经》的作者。", "zhan_jiang_yuji.png", "head_yuji.png", "quan_jiang_wenguan2.png", "quan_jiang_yuji.png", "nuqi_yuji.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1400, nil, 1410, "999|0,999|0,999|0", 1200, 2, 9300, 8200, 9800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10021, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|1101,2|4|10005,2|5|9302,2|6|31600,2|7|10109", "211,212,93,213,214", nil, },
	id_10022 = {10022, "典韦", "典韦", 1, "1", "相貌魁梧，臂力过人。张绣背叛曹操，典韦独挡叛军，击杀多人，但终因寡不敌众而战死。", "zhan_jiang_dianwei.png", "head_dianwei.png", "quan_jiang_manchong.png", "quan_jiang_dianwei.png", "nuqi_dianwei.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1420, nil, 1430, "999|0,999|0,999|0", 1100, 2, 8500, 9800, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10022, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|4001,2|4|5703,2|5|9302,2|6|5905,2|7|1107", "221,222,432,252,223", nil, },
	id_10023 = {10023, "徐晃", "徐晃", 1, "1", "“五子良将”之一。在曹操手下多立功勋，关羽围困樊城，徐晃率军长驱之入，一举击退蜀军。", "zhan_jiang_xuhuang.png", "head_xuhuang.png", "quan_jiang_lejin.png", "quan_jiang_xuhuang.png", "nuqi_xuhuang.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1440, nil, 1450, "999|0,999|0,999|0", 1200, 2, 9400, 9400, 7900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10023, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|1101,2|4|5803,2|5|9302,2|6|1805,2|7|3807", "221,231,601,232,233", nil, },
	id_10024 = {10024, "曹仁", "曹仁", 1, "1", "曹操从弟。三国时期曹魏名将。官至大司马，封陈侯，谥曰忠侯。", "zhan_jiang_caoren.png", "head_caoren.png", "quan_jiang_wujiang4.png", "quan_jiang_caoren.png", "nuqi_caoren.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1460, nil, 1470, "999|0,999|0,999|0", 1100, 2, 9100, 9000, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10024, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|4401,2|4|6903,2|5|9302,2|6|1405,2|7|6107", "101,241,432,242,243", nil, },
	id_10025 = {10025, "许褚", "许褚", 1, "1", "对曹操忠心耿耿，数次在危难中救曹操脱险。因为他十分勇猛，所以有“虎痴”的绰号。", "zhan_jiang_xuchu.png", "head_xuchu.png", "quan_jiang_ganning.png", "quan_jiang_xuchu.png", "nuqi_xuchu.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1480, nil, 1490, "999|0,999|0,999|0", 1100, 2, 8600, 9800, 6900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10025, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|1201,2|4|1103,2|5|9302,2|6|3505,2|7|5807", "231,251,252,253,254", nil, },
	id_10026 = {10026, "甄姬", "甄姬", 1, "2", "中国三国时期魏文帝曹丕的正室，魏明帝曹叡之母。本为袁熙之妻，曹操攻陷邺城后成为曹丕的妻室。", "zhan_jiang_zhenji.png", "head_zhenji.png", "quan_jiang_zhenji.png", "quan_jiang_zhenji.png", "nuqi_zhenji.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1500, nil, 1510, "999|0,999|0,999|0", 1200, 2, 7900, 8100, 9100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10026, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|4901,2|4|7703,2|5|9302,2|6|3805,2|7|5707", "32,261,282,262,263", nil, },
	id_10027 = {10027, "张郃", "张郃", 1, "1", "曹魏的“五子良将”之一。曹魏建立后加封为征西车骑将军。张郃戎马一生，以用兵巧变、善列营阵闻名，连诸葛亮都很忌惮他。", "zhan_jiang_zhanghe.png", "head_zhanghe.png", "quan_jiang_wujiang3.png", "quan_jiang_zhanghe.png", "nuqi_zhanghe.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1520, nil, 1530, "999|0,999|0,999|0", 1200, 2, 9400, 9500, 7700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10027, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|5601,2|4|3603,2|5|9302,2|6|1105,2|7|3507", "52,271,601,272,273", nil, },
	id_10028 = {10028, "姜维", "姜维", 1, "1", "三国时期蜀汉名将。“文能提笔安天下，武能上马定乾坤”即为姜维。", "zhan_jiang_jiangwei.png", "head_jiangwei.png", "quan_jiang_lidian.png", "quan_jiang_jiangwei.png", "nuqi_jiangwei.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1540, nil, 1550, "999|0,999|0,999|0", 1300, 2, 9200, 9000, 9500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10028, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|1101,2|4|3703,2|5|9302,2|6|2005,2|7|5907", "71,281,282,283,284", nil, },
	id_10029 = {10029, "魏延", "魏延", 1, "1", "三国时期蜀汉名将。因屡立战功而深受刘备信任，蜀汉建立后升为镇北将军。", "zhan_jiang_weiyan.png", "head_weiyan.png", "quan_jiang_ceshi.png", "quan_jiang_weiyan.png", "nuqi_weiyan.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1560, nil, 1570, "999|0,999|0,999|0", 1100, 2, 9000, 9400, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10029, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|1101,2|4|5903,2|5|9302,2|6|1805,2|7|3307", "62,72,252,291,292", nil, },
	id_10030 = {10030, "黄忠", "黄忠", 1, "1", "蜀国五虎大将之一，有勇有谋，老当益壮，拥有百步穿杨的箭术，百发百中。", "zhan_jiang_huangzhong.png", "head_huangzhong.png", "quan_jiang_huangzhong.png", "quan_jiang_huangzhong.png", "nuqi_huangzhong.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1580, nil, 1590, "999|0,999|0,999|0", 1100, 2, 8800, 9600, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10030, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|3601,2|4|7803,2|5|9302,2|6|3505,2|7|8501", "301,302,303,304,305", nil, },
	id_10031 = {10031, "徐庶", "徐庶", 1, "1", "与司马徽、诸葛亮等人为友。先曾仕官于刘备，后因曹操囚禁其母而不得不弃备投操，临行前向刘备推荐诸葛亮之才。", "zhan_jiang_xushu.png", "head_xushu.png", "quan_jiang_liru.png", "quan_jiang_xushu.png", "nuqi_xushu.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1600, nil, 1610, "999|0,999|0,999|0", 1100, 2, 8800, 7500, 9600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10031, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|1101,2|4|4503,2|5|9302,2|6|4705,2|7|2007", "201,311,332,312,313", nil, },
	id_10032 = {10032, "刘备", "刘备", 1, "1", "三国时期蜀汉开国皇帝。他为人谦和、礼贤下士，素以仁德为世人称赞，是三国时期著名的政治家。", "zhan_jiang_liubei.png", "head_liubei.png", "quan_jiang_wenguan3.png", "quan_jiang_liubei.png", "nuqi_liubei.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1620, nil, 1630, "999|0,999|0,999|0", 1200, 2, 8700, 8800, 9200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10032, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|5701,2|4|3603,2|5|9302,2|6|30500,2|7|1907", "321,322,73,323,324", nil, },
	id_10033 = {10033, "黄月英", "黄月英", 1, "2", "诸葛亮之妻，多才多艺，熟读兵书，上知天文，下知地理，文韬武略，足智多谋。", "zhan_jiang_huangyueying.png", "head_huangyueying.png", "quan_jiang_huangyueying.png", "quan_jiang_huangyueying.png", "nuqi_huangyueying.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1640, nil, 1650, "999|0,999|0,999|0", 1100, 2, 8100, 7000, 9300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10033, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|4401,2|4|3303,2|5|9302,2|6|5105,2|7|3607", "301,331,332,333,334", nil, },
	id_10034 = {10034, "太史慈", "太史慈", 1, "1", "三国时期东吴名将。弓马熟练，箭法精良。为人磊落坦荡，尽义守信，智勇双全。", "zhan_jiang_taishici.png", "head_taishici.png", "quan_jiang_wujiang2.png", "quan_jiang_taishici.png", "nuqi_taishici.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1660, nil, 1670, "999|0,999|0,999|0", 1100, 2, 9200, 9500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10034, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|3601,2|4|1103,2|5|9302,2|6|9705,2|7|9607", "102,281,341,342,343", nil, },
	id_10035 = {10035, "孙坚", "孙坚", 1, "1", "三国时东吴著名将领，容貌不凡，性格豪爽阔达，吴国霸业奠基人，军事家孙武后裔，被后世尊称为武烈皇帝。", "zhan_jiang_sunjian.png", "head_sunjian.png", "quan_jiang_sunjian.png", "quan_jiang_sunjian.png", "nuqi_sunjian.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1680, nil, 1690, "999|0,999|0,999|0", 1100, 2, 9100, 9200, 7800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10035, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|9501,2|4|4503,2|5|9302,2|6|1205,2|7|1407", "351,352,341,353,354", nil, },
	id_10036 = {10036, "孙权", "孙权", 1, "1", "三国时期吴国的开国皇帝，任贤用能、挽救了江东危局，保住了父兄基业。", "zhan_jiang_sunquan.png", "head_sunquan.png", "quan_jiang_wujiang3.png", "quan_jiang_sunquan.png", "nuqi_sunquan.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1700, nil, 1710, "999|0,999|0,999|0", 1100, 2, 8900, 8500, 9100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10036, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|6001,2|4|1903,2|5|9302,2|6|30600,2|7|4907", "151,361,362,363,364", nil, },
	id_10037 = {10037, "孙尚香", "孙尚香", 1, "2", "孙尚香为三国时期吴国人，后嫁与刘备。其自幼喜好武艺，手下侍女皆带刀具，常以与人击剑为乐，且容姿甚美。", "zhan_jiang_sunshangxiang.png", "head_sunshangxiang.png", "quan_jiang_sunshangxiang.png", "quan_jiang_sunshangxiang.png", "nuqi_sunshangxiang.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1720, nil, 1730, "999|0,999|0,999|0", 1100, 2, 8400, 9300, 7800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10037, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|7801,2|4|7703,2|5|9302,2|6|3005,2|7|1107", "321,352,393,371,372", nil, },
	id_10038 = {10038, "小乔", "小乔", 1, "2", "三国时期世人皆知的美女。东吴名将周瑜的妻子。国色天香，资貌绝伦。传说与其姊大乔均为绝世美女。", "zhan_jiang_xiaoqiao.png", "head_xiaoqiao.png", "quan_jiang_xiaoqiao.png", "quan_jiang_xiaoqiao.png", "nuqi_xiaoqiao.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1740, nil, 1750, "999|0,999|0,999|0", 1100, 2, 7900, 7200, 9200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10038, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|5701,2|4|9503,2|5|9302,2|6|7705,2|7|9207", "112,381,393,382,383", nil, },
	id_10039 = {10039, "大乔", "大乔", 1, "2", "与小乔并称为“江东二乔”，据传为绝世美女。有沉鱼落雁之容，知书达理，通音律，晓诗文。孙策之妻、孙权之嫂。", "zhan_jiang_daqiao.png", "head_daqiao.png", "quan_jiang_daqiao.png", "quan_jiang_daqiao.png", "nuqi_daqiao.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1760, nil, 1770, "999|0,999|0,999|0", 1100, 2, 7700, 7800, 8500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10039, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|3601,2|4|5703,2|5|9302,2|6|7705,2|7|4907", "392,391,393,394,395", nil, },
	id_10040 = {10040, "文丑", "文丑", 1, "1", "河北七上将之一。孔融曾以其与颜良提醒曹操小心袁绍部队。磐河一战，文丑连败数将，差点擒获公孙瓒，与赵云大战未分胜负。", "zhan_jiang_wenchou.png", "head_wenchou.png", "quan_jiang_zhangbao.png", "quan_jiang_wenchou.png", "nuqi_wenchou.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1780, nil, 1790, "999|0,999|0,999|0", 1100, 2, 9000, 9500, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10040, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|4201,2|4|3603,2|5|9302,2|6|5805,2|7|5607", "401,402,403,404,405", nil, },
	id_10041 = {10041, "颜良", "颜良", 1, "1", "袁绍部将，河北七上将之一。骁勇善战，与文丑一同作战时，有万夫莫敌之势。孔融曾道“颜良、文丑，勇冠三军。”", "zhan_jiang_yanliang.png", "head_yanliang.png", "quan_jiang_sunjian.png", "quan_jiang_yanliang.png", "nuqi_yanliang.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1800, nil, 1810, "999|0,999|0,999|0", 1100, 2, 9000, 9600, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10041, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|4301,2|4|1103,2|5|9302,2|6|5905,2|7|3907", "401,411,412,413,414", nil, },
	id_10042 = {10042, "张角", "张角", 1, "1", "“张角军”的领袖，太平道的创始人。以宗教救世为己任，自称“天公将军”，率领群众发动起义，史称“张角起义”。", "zhan_jiang_zhangjiao.png", "head_zhangjiao.png", "quan_jiang_wenguan4.png", "quan_jiang_zhangjiao.png", "nuqi_zhangjiao.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1820, nil, 1830, "999|0,999|0,999|0", 1100, 2, 8800, 7700, 9500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10042, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|3601,2|4|3303,2|5|9302,2|6|6605,2|7|7907", "202,211,452,421,422", nil, },
	id_10043 = {10043, "华雄", "华雄", 1, "1", "董卓帐下一名虎将。身长九尺，虎体狼腰，豹头猿臂。在汜水关先后斩杀多名大将，最后被关羽所杀，这就是著名的“温酒斩华雄”。", "zhan_jiang_huaxiong.png", "head_huaxiong.png", "quan_jiang_huaxiong.png", "quan_jiang_huaxiong.png", "nuqi_huaxiong.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1840, nil, 1850, "999|0,999|0,999|0", 1100, 2, 8800, 9200, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10043, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|1101,2|4|5603,2|5|9302,2|6|1205,2|7|3307", "402,431,432,433,434", nil, },
	id_10044 = {10044, "陈宫", "陈宫", 1, "1", "吕布帐下谋士。性情刚直，足智多谋，对吕布忠心不二。吕布战败后，随吕布等一同被曹操所擒，决意赴死。", "zhan_jiang_chengong.png", "head_chengong.png", "quan_jiang_chengong.png", "quan_jiang_chengong.png", "nuqi_chengong.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1860, nil, 1870, "999|0,999|0,999|0", 1100, 2, 7300, 7500, 8700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10044, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|3801,2|4|5303,2|5|9302,2|6|5705,2|7|7807", "162,411,522,441,442", nil, },
	id_10045 = {10045, "董卓", "董卓", 1, "1", "生于富贵豪强之家，性格粗野凶狠有谋略。体魄健壮，臂力过人，善骑射。进京倒行逆施，迫害忠良，甚至操控政权。", "zhan_jiang_dongzhuo.png", "head_dongzhuo.png", "quan_jiang_dongzhuo.png", "quan_jiang_dongzhuo.png", "nuqi_dongzhuo.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1880, nil, 1890, "999|0,999|0,999|0", 1000, 2, 8500, 9000, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10045, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|1201,2|4|4803,2|5|9302,2|6|30700,2|7|1707", "431,451,452,453,454", nil, },
	id_10046 = {10046, "蔡文姬", "蔡文姬", 1, "2", "东汉大文学家蔡邕的女儿，是中国历史上著名的才女和文学家，精于天文数理，既博学能文，又善诗赋，兼长辩才与音律。", "zhan_jiang_caiwenji.png", "head_caiwenji.png", "quan_jiang_caiwenji.png", "quan_jiang_caiwenji.png", "nuqi_caiwenji.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1900, nil, 1910, "999|0,999|0,999|0", 1100, 2, 8000, 7700, 8700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10046, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|9401,2|4|3603,2|5|9302,2|6|7705,2|7|4907", "331,403,731,461,462", nil, },
	id_10047 = {10047, "曹丕", "曹丕", 1, "1", "三国时期著名的政治家、文学家，曹魏的开国皇帝。魏武帝曹操与卞夫人的长子。与其父曹操、其弟曹植并称为“三曹”。", "zhan_jiang_caopei.png", "head_caopei.png", "quan_jiang_wujiang4.png", "quan_jiang_caopei.png", "nuqi_caopei.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1920, nil, 1930, "999|0,999|0,999|0", 1000, 2, 8700, 8000, 8800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10047, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|3801,2|4|9003,2|5|9302,2|6|3305,2|7|3607", "222,261,471,472,473", nil, },
	id_10048 = {10048, "孟获", "孟获", 1, "1", "三国时期南中一带少数民族首领，曾经起兵反叛蜀汉，后来被诸葛亮七擒七纵并降服。", "zhan_jiang_menghuo.png", "head_menghuo.png", "quan_jiang_xusheng.png", "quan_jiang_menghuo.png", "nuqi_menghuo.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1940, nil, 1950, "999|0,999|0,999|0", 1000, 2, 8300, 9300, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10048, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|6901,2|4|4003,2|5|9302,2|6|5805,2|7|1107", "91,481,493,482,483", nil, },
	id_10049 = {10049, "祝融", "祝融", 1, "2", "火神祝融氏后裔，南蛮王孟获之妻。武艺高强，善使飞刀，在诸葛亮七擒七纵孟获之后，随孟获投降蜀汉。", "zhan_jiang_zhurong.png", "head_zhurong.png", "quan_jiang_wujiang11.png", "quan_jiang_zhurong.png", "nuqi_zhurong.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1960, nil, 1970, "999|0,999|0,999|0", 1000, 2, 8200, 9000, 7600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10049, 2, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|4401,2|4|1103,2|5|9302,2|6|5905,2|7|6607", "491,492,493,494,495", nil, },
	id_10050 = {10050, "黄盖", "黄盖", 1, "1", "赤壁之战时，黄盖施以苦肉计前往曹营诈降，并趁机以火攻大破曹操的军队，是赤壁之战主要功臣之一。", "zhan_jiang_huanggai.png", "head_huanggai.png", "quan_jiang_huanggai.png", "quan_jiang_huanggai.png", "nuqi_huanggai.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1980, nil, 1990, "999|0,999|0,999|0", 1100, 2, 8700, 9400, 6700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10050, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|3501,2|4|5803,2|5|9302,2|6|4205,2|7|3207", "501,502,722,503,504", nil, },
	id_10051 = {10051, "鲁肃", "鲁肃", 1, "1", "中国东汉末年杰出战略家，著名政治家、外交家。他曾为孙权提出鼎足江东的战略规划，因此得到孙权的赏识。", "zhan_jiang_lusu.png", "head_lusu.png", "quan_jiang_liubiao.png", "quan_jiang_lusu.png", "nuqi_lusu.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2000, nil, 2010, "999|0,999|0,999|0", 1100, 2, 8100, 7700, 9500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10051, 3, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|4901,2|4|3603,2|5|9302,2|6|3705,2|7|6307", "131,501,113,511,512", nil, },
	id_10052 = {10052, "陈登", "陈登", 1, "1", "为人爽朗，性格沈静，智谋过人，少年时有扶世济民之志，并且博览群书，学识渊博。内政军事皆是良才。", "zhan_jiang_chendeng.png", "head_chendeng.png", "quan_jiang_lidian.png", "quan_jiang_chendeng.png", "nuqi_chendeng.png", "15", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2020, nil, 2030, "999|0,999|0,999|0", 1100, 2, 8200, 7300, 9300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10052, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|3601,2|4|4403,2|5|9302,2|6|3405,2|7|5907", "212,521,522,523,524", nil, },
	id_10053 = {10053, "高顺", "高顺", 1, "1", "三国中吕布帐下中郎将。为人清白有威严，不好饮酒，忠于吕布。所统率的部队精锐非常，号称“陷阵营”。被曹操所俘之后拒不投降。", "zhan_jiang_gaoshun.png", "head_gaoshun.png", "quan_jiang_gaoshun.png", "quan_jiang_gaoshun.png", "nuqi_gaoshun.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2040, nil, 2050, "999|0,999|0,999|0", 1100, 2, 8900, 9200, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10053, 4, 1000, 5000, 10000, 500, 500, nil, "2|2|901,2|3|3901,2|4|5803,2|5|9302,2|6|3505,2|7|5207", "412,531,522,532,533", nil, },
	id_10054 = {10054, "张春华", "张春华", 1, "2", "年少的时候就德才兼备，智识过人。晋宣帝司马懿之妻，晋景帝司马师、晋文帝司马昭的母亲。", "zhan_jiang_zhangchunhua.png", "head_zhangchunhua.png", "quan_jiang_zhangchunhua.png", "quan_jiang_zhangchunhua.png", nil, "14", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2060, nil, 2070, "999|0,999|0,999|0", 1000, 2, 7000, 7200, 7800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 10054, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|1001,2|3|3201,2|4|4403,2|5|9302,2|6|305", "311,541,591,542", nil, },
	id_10055 = {10055, "文聘", "文聘", 1, "1", "守御荆城之际，多次阻遏关羽之师，立下莫大之功，成为曹公倚为屏障的大将之一，威名远播。", "zhan_jiang_wenpin.png", "head_wenpin.png", "quan_jiang_wujiang4.png", "quan_jiang_wenpin.png", nil, "14", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2080, nil, 2090, "999|0,999|0,999|0", 1000, 2, 8600, 8200, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 10055, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|201,2|3|5301,2|4|1103,2|5|9302,2|6|3805", "551,552,573,553", nil, },
	id_10056 = {10056, "满宠", "满宠", 1, "1", "三国时期曹魏名将，官至太尉。初在曹操手下任许县县令，掌管司法，以执法严格著称；曾参与赤壁之战。", "zhan_jiang_manchong.png", "head_manchong.png", "quan_jiang_manchong.png", "quan_jiang_manchong.png", nil, "14", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2100, nil, 2110, "999|0,999|0,999|0", 1000, 2, 8400, 6600, 8400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 10056, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|201,2|3|4001,2|4|3203,2|5|9302,2|6|3305", "561,562,573,564", nil, },
	id_10057 = {10057, "李典", "李典", 1, "1", "深明大义，不与人争功，崇尚学习与高贵儒雅，尊重博学之士。李典有长者之风，官至破虏将军。", "zhan_jiang_lidian.png", "head_lidian.png", "quan_jiang_lidian.png", "quan_jiang_lidian.png", nil, "14", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2120, nil, 2130, "999|0,999|0,999|0", 1000, 2, 8600, 8200, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 10057, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|301,2|3|1101,2|4|1203,2|5|9302,2|6|7805", "571,572,573,574", nil, },
	id_10058 = {10058, "乐进", "乐进", 1, "1", "曹操“五子良将”之一。容貌短小，但胆识过人，性格刚烈，随曹多年，南征北讨，战功无数。", "zhan_jiang_lejin.png", "head_lejin.png", "quan_jiang_lejin.png", "quan_jiang_lejin.png", "nuqi_lejin.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2140, nil, 2150, "999|0,999|0,999|0", 1100, 2, 9000, 9000, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10058, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|201,2|3|1101,2|4|5303,2|5|9302,2|6|5605,2|7|5807", "531,601,253,581,582", nil, },
	id_10059 = {10059, "许攸", "许攸", 1, "1", "本为袁绍帐下谋士，后背袁投曹，睿智多谋。并为曹操设下偷袭袁绍军屯粮之所乌巢的计策，袁绍因此而大败于官渡。", "zhan_jiang_xuyou.png", "head_xuyou.png", "quan_jiang_zhangzhao.png", "quan_jiang_xuyou.png", nil, "14", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2160, nil, 2170, "999|0,999|0,999|0", 900, 2, 7700, 7700, 7700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 10059, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|301,2|3|3201,2|4|4703,2|5|9302,2|6|1205", "551,571,591,592", nil, },
	id_10060 = {10060, "于禁", "于禁", 1, "1", "曹操“五子良将”之一。曾于张绣造反时讨伐青州兵，同时为迎击敌军而固守营垒，因此曹操称赞他可与古代名将相比。", "zhan_jiang_yujin.png", "head_yujin.png", "quan_jiang_wujiang4.png", "quan_jiang_yujin.png", "nuqi_yujin.png", "15", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2180, nil, 2190, "999|0,999|0,999|0", 1100, 2, 9200, 8800, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10060, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|201,2|3|5901,2|4|1103,2|5|9302,2|6|4805,2|7|5707", "241,601,253,602,603", nil, },
	id_10061 = {10061, "荀攸", "荀攸", 1, "1", "东汉末年曹操的五谋臣之一，荀彧从子，被曹操称为“谋主”，擅长灵活多变的克敌战术和军事策略。辅佐曹操统一北方。", "zhan_jiang_xunyou.png", "head_xunyou.png", "quan_jiang_liru.png", "quan_jiang_xunyou.png", nil, "14", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2200, nil, 2210, "999|0,999|0,999|0", 1000, 2, 7400, 6700, 9300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 10061, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|301,2|3|1101,2|4|4003,2|5|9302,2|6|5105", "552,572,801,611", nil, },
	id_10062 = {10062, "曹洪", "曹洪", 1, "1", "曹操之侄。曹操追袭董卓荥阳为徐荣所败失马，曹洪舍命献马并救护曹操，使曹操免于厄难。", "zhan_jiang_caohong.png", "head_caohong.png", "quan_jiang_caohong.png", "quan_jiang_caohong.png", nil, "14", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2220, nil, 2230, "999|0,999|0,999|0", 1000, 2, 8100, 9000, 6300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 10062, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|201,2|3|9601,2|4|3203,2|5|9302,2|6|7805", "621,691,622", nil, },
