	id_10092 = {10092, "辛宪英", "辛宪英", 1, "2", "辛宪英聪朗有才鉴，足不出户但对政治有极强的洞察力，父兄极其子遇到大事都会找她商榷，她每言必中。", "zhan_jiang_caojie.png", "head_caojie.png", "quan_jiang_xinxianying.png", "quan_jiang_caojie.png", nil, "13", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6100, nil, 7460, "999|0,999|0,999|0", 700, 2, 6300, 5600, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 10092, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|301,2|3|5701,2|4|4703,2|5|9302", "10011,10012", nil, },
	id_10093 = {10093, "孔秀", "孔秀", 1, "1", "曹操手下东岭关守将，东岭关为关羽携嫂过五关之第一关，孔秀因阻挡关羽过关被斩。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "13", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6030, nil, 7070, "999|0,999|0,999|0", 700, 2, 6900, 7400, 5400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 10093, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|200,2|3|3301,2|4|1203,2|5|9302", "10021,10022", nil, },
	id_10094 = {10094, "韩福", "韩福", 1, "1", "曹操部下任洛阳太守。关羽千里走单骑途中在洛阳设计偷袭，最后被关羽所斩杀。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "13", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6050, nil, 7290, "999|0,999|0,999|0", 600, 2, 7000, 6700, 5400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 10094, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|200,2|3|7801,2|4|5803,2|5|9302", "10031,10032", nil, },
	id_10095 = {10095, "卞喜", "卞喜", 1, "1", "原是张角余党，后投曹操。善使流星锤。在镇国寺设下伏兵欲谋害千里寻兄的关羽，最后被关羽斩杀。", "zhan_jiang_wenguan2.png", "head_wenguan2.png", "quan_jiang_wenguan2.png", "quan_jiang_wenguan2.png", nil, "13", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6100, nil, 7110, "999|0,999|0,999|0", 600, 2, 6100, 5800, 6900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 10095, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|200,2|3|3601,2|4|3403,2|5|9302", "10041,10042", nil, },
