	id_30001 = {30001, "黄巾刀兵", "黄巾刀兵", 1, "1", "最普通的黄巾刀兵，因为入黄巾军必须要把头发剃光，所以称自己为光头军，但是不明就里的外人却叫他们黄巾刀兵，这使得他们非常不满。", "zhan_jiang_huangjinjun.png", "head_huangjinjun.png", "quan_bin_huangjinjun.png", "quan_bin_huangjinjun.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5000, nil, 800, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30001, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3300,1|30|402", nil, nil, },
	id_30002 = {30002, "飞镖侠客", "飞镖侠客", 1, "1", "想要知道飞镖侠客是如何在简陋的军营中也可以梳一头漂亮的马尾辫吗？答案就在她那些飞镖的洞洞里。", "zhan_bing_feibiaoke.png", "head_feibiao.png", "quan_bin_feibiao.png", "quan_bin_feibiao.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5250, nil, 7010, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30002, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1100,1|30|9002", nil, nil, },
	id_30003 = {30003, "旗鼓兵", "旗鼓兵", 1, "1", "旗鼓兵头顶的大象耳朵不仅仅是装饰，她时常说“自从我戴上了这个帽子以后，感觉打出的鼓声都变得响亮许多了呢！”", "zhan_jiang_qigubing.png", "head_qigubing.png", "quan_bin_qigubing.png", "quan_bin_qigubing.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5090, nil, 7090, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30003, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3200,1|30|3402", nil, nil, },
	id_30004 = {30004, "巫术师", "巫术师", 1, "1", "巫术师们喜欢做的事情就是云游四方，路见不平念咒相助，虽然有时候非常迷糊分不清敌我。", "zhan_jiang_wushushi.png", "head_wushushi.png", "quan_bin_wushushi.png", "quan_bin_wushushi.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6100, nil, 7270, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30004, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1100,1|30|3402", nil, nil, },
	id_30005 = {30005, "强弩小兵", "强弩小兵", 1, "1", "强弩兵每当扫射一阵以后就要拨开挡住眼睛的盔翎。他称之为“冷却”。", "zhan_jiang_qiangnu.png", "head_qiangnu.png", "quan_bin_qiangnubing.png", "quan_bin_qiangnubing.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5070, nil, 7180, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30005, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|7800,1|30|1102", nil, nil, },
	id_30006 = {30006, "剑舞女神", "剑舞女神", 1, "1", "剑舞女神在军营中是真正的女神，据说她非常爱干净，跟普通的士兵们说不到两句话就会去洗澡。", "zhan_jiang_jianwunvshen.png", "head_jianwunvshen.png", "quan_bin_jianwunvshen.png", "quan_bin_jianwunvshen.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5180, nil, 7360, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30006, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3600,1|30|1102", nil, nil, },
	id_30007 = {30007, "长枪兵", "长枪兵", 1, "1", "长枪兵最喜欢的地方就是城市的大门，最喜欢做的事情就是冲着来往的粮草队说“站住！”，然后用长枪戳啊戳。", "zhan_bing_changqiang.png", "head_changqiang.png", "quan_bin_changqiangbing.png", "quan_bin_changqiangbing.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7070, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30007, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5700,1|30|1202", nil, nil, },
	id_30008 = {30008, "飞熊军", "飞熊军", 1, "1", "别看他一副凶神恶煞的样子，实际上他致力于动物保护事业，记住，没有买卖！就没有杀戮！", "zhan_bing_feixiong.png", "head_feixiong.png", "quan_bin_feixiongjun.png", "quan_bin_feixiongjun.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5140, nil, 7290, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30008, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1200,1|30|1102", nil, nil, },
	id_30009 = {30009, "藤甲兵", "藤甲兵", 1, "1", "曾经有一名藤甲兵，他受到了一句诅咒“你若藤甲，便是晴天”。于是，有一天，他在路上走着走着，就烧起来了。", "zhan_jiang_tengjiabing.png", "head_tengjiabing.png", "quan_bin_tengjiabing.png", "quan_bin_tengjiabing.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5280, nil, 7070, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30009, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|6100,1|30|3302", nil, nil, },
	id_30010 = {30010, "强弩兵", "强弩兵", 1, "1", "强弩兵每当扫射一阵以后就要拨开挡住眼睛的盔翎。他称之为“冷却”。", "zhan_jiang_qiangnu.png", "head_qiangnu.png", "quan_bin_qiangnubing.png", "quan_bin_qiangnubing.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5070, nil, 7110, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30010, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3500,1|30|1102", nil, nil, },
	id_30011 = {30011, "弓兵", "弓兵", 1, "1", "谁说弓兵不善近战？胆敢靠近他的人，小心身上被他的纤纤玉足戳上几个大洞！", "zhan_jiang_gongbing.png", "head_gongbing.png", "quan_bin_gongbing.png", "quan_bin_gongbing.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5170, nil, 7110, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30011, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|100,1|30|3602", nil, nil, },
	id_30012 = {30012, "飞镖客", "飞镖客", 1, "1", "想要知道飞镖侠客是如何在简陋的军营中也可以梳一头漂亮的马尾辫吗？答案就在她那些飞镖的洞洞里。", "zhan_bing_feibiaoke.png", "head_feibiao.png", "quan_bin_feibiao.png", "quan_bin_feibiao.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5250, nil, 7010, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30012, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|9000,1|30|1102", nil, nil, },
	id_30013 = {30013, "影卫", "影卫", 1, "1", "影卫从小就使用水果训练暗杀技巧，导致他现在看到水果就会情不自禁地扑上去。“哼！人家只是看看里面是不是炸弹！”", "zhan_jiang_yingwei.png", "head_yingwei.png", "quan_bin_yingwei.png", "quan_bin_yingwei.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5270, nil, 7410, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30013, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1100,1|30|7802", nil, nil, },
	id_30014 = {30014, "刀车兵", "刀车兵", 1, "1", "刀车最大的用途其实是搜刮，只要推着它冲进敌阵一会儿再出来，那些刀上就会挂满了各种装备，还有内衣。", "bin_daoche.png", "head_daoche.png", "quan_bin_daoche.png", "quan_bin_daoche.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5300, nil, 7440, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30014, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1200,1|30|802", nil, nil, },
	id_30015 = {30015, "霹雳车", "霹雳车", 1, "1", "全新一代的全自动霹雳车！可以根据设定自动发射火弹、垃圾堆、小鸟等物品！", "bin_piliche.png", "head_piliche.png", "quan_bin_piliche.png", "quan_bin_piliche.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5030, nil, 7010, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30015, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3500,1|30|5902", nil, nil, },
	id_30016 = {30016, "神机弩炮", "神机弩炮", 1, "1", "不要以为那个像肋骨一样的东西只是装饰，必要时刻它们会全部打开然后射出成千上网的箭矢！", "bin_shenjinupao.png", "head_shenjinupao.png", "quan_bin_shenjinupao.png", "quan_bin_shenjinupao.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5100, nil, 7200, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30016, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1100,1|30|5902", nil, nil, },
	id_30017 = {30017, "骑兵", "骑兵", 1, "1", "骑兵都信奉单身原则，因为他们相信，这辈子骑的战马就是上辈子的亲密伴侣。", "zhan_bing_qibing.png", "head_qibing.png", "quan_bin_qibing.png", "quan_bin_qibing.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7210, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30017, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5600,1|30|502", nil, nil, },
	id_30018 = {30018, "虎豹骑", "虎豹骑", 1, "1", "据说没有人能够抵挡虎豹骑的攻势，看到她冲过来的样子，大多数人都会选择拥抱！", "zhan_bing_hubaoqi.png", "head_hubaoqi.png", "quan_bin_hubaoqi.png", "quan_bin_hubaoqi.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5220, nil, 7300, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30018, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5600,1|30|6202", nil, nil, },
	id_30019 = {30019, "西凉铁骑兵", "西凉铁骑兵", 1, "1", "每一名西凉铁骑都将吕布作为他们的终极目标，可以说是吕布的最狂热粉丝。", "zhan_jiang_xiliangtieqi.png", "head_xiliangtieqi.png", "quan_bin_xiliangtieqi.png", "quan_bin_xiliangtieqi.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5040, nil, 7020, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30019, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3500,1|30|5602", nil, nil, },
	id_30020 = {30020, "斧骑兵", "斧骑兵", 1, "1", "斧骑兵对斧子的热爱已经到了痴迷的程度，恨不得在自己的全身上下都装满斧子，后来他发明了一种鞋，叫做“冰刀”鞋。", "zhan_jiang_fuqibing.png", "head_fuqibing.png", "quan_bin_fuqibing.png", "quan_bin_fuqibing.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5130, nil, 7380, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30020, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5600,1|30|1202", nil, nil, },
	id_30021 = {30021, "龙骧骑", "龙骧骑", 1, "1", "龙骧骑爱他的宝马胜过一切，每个月都会带着他的宝马去做毛发护理，完事以后还要亲自为鳞甲打蜡。", "zhan_bing_longxiangqi.png", "head_longxiang.png", "quan_bin_longxiangqi.png", "quan_bin_longxiangqi.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5110, nil, 7390, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30021, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5900,1|30|5602", nil, nil, },
	id_30022 = {30022, "白马义从", "白马义从", 1, "1", "白马义从在战场上是风一样的存在，你或许只是感觉到一阵风吹过，身上就多了一条伤痕。", "zhan_bing_baimayicong.png", "head_baimayicong.png", "quan_bin_baimayicong.png", "quan_bin_baimayicong.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5060, nil, 7370, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30022, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5600,1|30|3602", nil, nil, },
	id_30023 = {30023, "重骑兵", "重骑兵", 1, "1", "重骑兵无论是人还是马都装上了重甲，这导致他的速度也不快，你要知道，马一旦失去平衡倒在地上，要把它扶起来可就没那么容易了。", "zhan_bing_zhongqibing.png", "head_zhongqibing.png", "quan_bin_zhongqibing.png", "quan_bin_zhongqibing.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5010, nil, 7060, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30023, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5600,1|30|3302", nil, nil, },
	id_30024 = {30024, "禁卫兵", "禁卫兵", 1, "1", "禁卫兵走到哪都会带着他的大铁锤，他会用他的大铁锤战斗、摘香蕉、必要时候还可以当作板凳来用！", "zhan_jiang_jinweijun.png", "head_jinweijun.png", "quan_bin_jinweijun.png", "quan_bin_jinweijun.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5190, nil, 7080, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30024, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|6100,1|30|5702", nil, nil, },
	id_30025 = {30025, "大戟士", "大戟士", 1, "1", "大戟士的任务通常都是守卫一些重要的人物，敌人常常会在击败他后被他一身耀眼的装备而吸引，重要人物则可以趁机逃跑。", "zhan_bing_dajishi.png", "head_dajishi.png", "quan_bin_dajishi.png", "quan_bin_dajishi.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5260, nil, 7330, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30025, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1200,1|30|1102", nil, nil, },
	id_30026 = {30026, "先登死士", "先登死士", 1, "1", "先登死士有一门绝技：他可以在城墙上射满弓箭，然后踩着这些弓箭登墙。但是，如果弓箭射到一半不够的话，他恐怕就得在墙上过夜了。", "zhan_jiang_xiandengsishi.png", "head_xiandengsishi.png", "quan_bin_xiandengsishi.png", "quan_bin_xiandengsishi.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5160, nil, 7230, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30026, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|6000,1|30|5802", nil, nil, },
	id_30027 = {30027, "刺客", "刺客", 1, "1", "刺客的一袭绿衣和杂草般的头型与茅房后的草丛融为一体，这使得他的刺杀目标防不胜防。", "zhan_bing_cike.png", "head_cike.png", "quan_bin_cike.png", "quan_bin_cike.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5290, nil, 7260, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30027, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|7800,1|30|3602", nil, nil, },
	id_30028 = {30028, "象兵", "象兵", 1, "1", "这只大象每天都胆颤心惊的，因为每当它的主人使出下劈的时候就要当心自己的象牙被削掉。", "zhan_bing_xiangbing.png", "head_xiangbing.png", "quan_bin_xiangbing.png", "quan_bin_xiangbing.png", nil, "32", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5150, nil, 7120, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30028, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5900,1|30|3302", nil, nil, },
	id_30029 = {30029, "乐师", "乐师", 1, "1", "她的打扮曾经风靡一时，几乎所有的琵琶女都效仿她。到了后来，还有一首诗专门为她而写，有一句什么来着，“犹抱琵琶半遮面”？", "zhan_jiang_wujiang7.png", "head_wujiang7.png", "quan_jiang_wujiang7.png", "quan_jiang_wujiang7.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5090, nil, 7370, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30029, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3400,1|30|3202", nil, nil, },
	id_30030 = {30030, "医师", "医师", 1, "1", "十分粗心的医师妹子，经常会忘记自己手里拿的到底是毛笔还是针筒。", "zhan_jiang_wenguan6.png", "head_wenguan6.png", "quan_jiang_wenguan6.png", "quan_jiang_wenguan6.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6160, nil, 7050, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30030, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|4900,1|30|1002", nil, nil, },
	id_30031 = {30031, "舞姬", "舞姬", 1, "1", "舞姬每天只做两件事情，一件是对友军唱”请把我的歌带回你的家“，一件是对敌人唱”请把你的怒气留下！“。", "bin_wuji.png", "head_wuji.png", "quan_bin_wuji.png", "quan_bin_wuji.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5080, nil, 7030, "999|0,999|0,999|0", 400, 2, 5500, 5500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30031, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3200,1|30|3602", nil, nil, },
	id_30032 = {30032, "重枪兵", "重枪兵", 1, "1", "重枪兵在一次偶然的事件中得到了一把会吸血的长枪。从此以后，他的屋子里就再也没出现过蚊子。", "zhan_bing_changqiang.png", "head_changqiang.png", "quan_bin_changqiangbing.png", "quan_bin_changqiangbing.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7390, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30032, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5701,1|30|1203", nil, nil, },
	id_30033 = {30033, "战鼓兵", "战鼓兵", 1, "1", "战鼓兵有一项绝技，就是可以左手右手同时打出不同的鼓调，据说他的老师姓周。", "zhan_jiang_qigubing.png", "head_qigubing.png", "quan_bin_qigubing.png", "quan_bin_qigubing.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5090, nil, 7130, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30033, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3201,1|30|3403", nil, nil, },
	id_30034 = {30034, "高级刺客", "高级刺客", 1, "1", "高级刺客有太多的仇人，所以他决定，以后刺杀之前先把目标弄晕，绝不再让任何一个人看到他的脸。", "zhan_bing_cike.png", "head_cike.png", "quan_bin_cike.png", "quan_bin_cike.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5290, nil, 7080, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30034, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|7801,1|30|3603", nil, nil, },
	id_30035 = {30035, "妖术师", "妖术师", 1, "1", "妖术师总说“我们妖术师再妖但也是人啊！”，于是人们改口叫她人妖术师。", "zhan_jiang_wushushi.png", "head_wushushi.png", "quan_bin_wushushi.png", "quan_bin_wushushi.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6100, nil, 7190, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30035, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|3403", nil, nil, },
	id_30036 = {30036, "长弓手", "长弓手", 1, "1", "长弓手喜欢一切长长的东西，长长的弓弦，长长的手臂，长长的靴子还有长长的大马尾辫。", "zhan_jiang_gongbing.png", "head_gongbing.png", "quan_bin_gongbing.png", "quan_bin_gongbing.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5170, nil, 7230, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30036, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|101,1|30|3603", nil, nil, },
	id_30037 = {30037, "狂战女神", "狂战女神", 1, "1", "狂战女神的魅力在战场上表现的淋漓尽致，他只要说一句“呵呵”就会有数不尽的士兵们为她卖命。", "zhan_jiang_jianwunvshen.png", "head_jianwunvshen.png", "quan_bin_jianwunvshen.png", "quan_bin_jianwunvshen.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5180, nil, 7150, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30037, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3601,1|30|1103", nil, nil, },
	id_30038 = {30038, "皇廷戟士", "皇廷戟士", 1, "1", "皇廷戟士无论是在什么战斗中都会冲到最前面，他的英姿早已迷的那些宫女们花枝招展了。", "zhan_bing_dajishi.png", "head_dajishi.png", "quan_bin_dajishi.png", "quan_bin_dajishi.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5260, nil, 7290, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30038, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3601,1|30|1103", nil, nil, },
	id_30039 = {30039, "贪狼侠客", "贪狼侠客", 1, "1", "贪狼侠客会把飞镖放在一起摩擦产生巨大的静电，带有静电的飞镖会连在一起作为近战用的兵器，所谓进可攻退可守。", "zhan_bing_feibiaoke.png", "head_feibiao.png", "quan_bin_feibiao.png", "quan_bin_feibiao.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5250, nil, 7400, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30039, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|9603", nil, nil, },
	id_30040 = {30040, "疾风轻骑", "疾风轻骑", 1, "1", "疾风轻骑有一匹多愁善感的马，每当跑得很快的时候马就会流下眼泪。马：“我只是跑太快沙子进眼睛里了！”", "zhan_bing_qibing.png", "head_qibing.png", "quan_bin_qibing.png", "quan_bin_qibing.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7290, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30040, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|3403", nil, nil, },
	id_30041 = {30041, "高级影卫", "高级影卫", 1, "1", "高级影卫最近迷上了种些带毒的花花草草，他那两把影刃用来做割草刀真是最适合不过了。", "zhan_jiang_yingwei.png", "head_yingwei.png", "quan_bin_yingwei.png", "quan_bin_yingwei.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5270, nil, 7280, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30041, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|7803", nil, nil, },
	id_30042 = {30042, "重装弩兵", "重装弩兵", 1, "1", "重装弩兵早已习惯了长时间的拉锯战，为了确保弹药充足，他把箭矢藏在了盔甲的每一个部位。", "zhan_jiang_qiangnu.png", "head_qiangnu.png", "quan_bin_feibiao.png", "quan_bin_qiangnubing.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5070, nil, 7170, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30042, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3501,1|30|1103", nil, nil, },
	id_30043 = {30043, "战熊重骑", "战熊重骑", 1, "1", "这头熊是他很久以前从捕熊者手上救下来的。可惜的是，小可爱的爪子已经被陷阱弄断了一根。", "zhan_bing_feixiong.png", "head_feixiong.png", "quan_bin_feixiongjun.png", "quan_bin_feixiongjun.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5140, nil, 7160, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30043, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3301,1|30|1103", nil, nil, },
	id_30044 = {30044, "虎豹骑士", "虎豹骑士", 1, "1", "虎豹骑士从小便与老虎生活在一起，一起吃饭，一起睡觉，长大以后才会教她人类的语言。她最喜欢的一个词语就是”母老虎“。", "zhan_bing_hubaoqi.png", "head_hubaoqi.png", "quan_bin_hubaoqi.png", "quan_bin_hubaoqi.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5220, nil, 7070, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30044, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|6203", nil, nil, },
	id_30045 = {30045, "刀车重步", "刀车重步", 1, "1", "刀车重步最恨被别人当作兵器架对待，不过也因此总是能收集到一些神兵利器或者奇怪的道具。", "bin_daoche.png", "head_daoche.png", "quan_bin_daoche.png", "quan_bin_daoche.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5300, nil, 7290, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30045, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1201,1|30|803", nil, nil, },
	id_30046 = {30046, "狂斧铁骑", "狂斧铁骑", 1, "1", "狂斧铁骑经常会因为冲得太快而整个人飞出去。他为了在飞出去以后还能杀敌，毅然在头顶安了两个角。", "zhan_jiang_fuqibing.png", "head_fuqibing.png", "quan_bin_fuqibing.png", "quan_bin_fuqibing.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5040, nil, 7260, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30046, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|1203", nil, nil, },
	id_30047 = {30047, "龙骧战骑", "龙骧战骑", 1, "1", "龙骧战骑对战马的爱护已经到了痴迷的程度，而且一直是单身。大家都怀疑他头盔和马头盔上的宝石是情侣款。", "zhan_bing_longxiangqi.png", "head_longxiang.png", "quan_bin_longxiangqi.png", "quan_bin_longxiangqi.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5110, nil, 7240, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30047, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5901,1|30|5602", nil, nil, },
	id_30048 = {30048, "神风骑", "神风骑", 1, "1", "神风骑会在放大招的时候高举兵器大喊”赐给我力量吧！天神！“，然后一道雷就落了下来。", "zhan_bing_baimayicong.png", "head_baimayicong.png", "quan_bin_baimayicong.png", "quan_bin_baimayicong.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5060, nil, 7390, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30048, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|3603", nil, nil, },
	id_30049 = {30049, "皇廷重甲", "皇廷重甲", 1, "1", "皇廷重甲自从吃上皇粮以后将护甲上都涂满了金漆，因为他相信，真金不怕火炼！", "zhan_jiang_tengjiabing.png", "head_tengjiabing.png", "quan_bin_tengjiabing.png", "quan_bin_tengjiabing.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5280, nil, 7300, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30049, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|6101,1|30|3302", nil, nil, },
	id_30050 = {30050, "雷霆车", "雷霆车", 1, "1", "它的制造者不知从哪穿越回来，发明了全自动雷霆车，不但可以自己装弹发射，还可以使弹药带电。", "bin_piliche.png", "head_piliche.png", "quan_bin_piliche.png", "quan_bin_piliche.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5030, nil, 7140, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30050, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3501,1|30|5903", nil, nil, },
	id_30051 = {30051, "皇廷重骑", "皇廷重骑", 1, "1", "皇廷重骑无论什么时候都是在战场上最显眼的一个，追他的女孩子从长安一直排到了成都。", "zhan_bing_zhongqibing.png", "head_zhongqibing.png", "quan_bin_zhongqibing.png", "quan_bin_zhongqibing.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5010, nil, 7060, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30051, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|3303", nil, nil, },
	id_30052 = {30052, "圆武禁卫", "圆武禁卫", 1, "1", "他手中那锤子上布满了引线，只要点着就可以将锤子点火。所以，最好趁火还没烧大的时候把锤子丢出去。", "zhan_jiang_jinweijun.png", "head_jinweijun.png", "quan_bin_jinweijun.png", "quan_bin_jinweijun.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5190, nil, 7290, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30052, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|6101,1|30|5703", nil, nil, },
	id_30053 = {30053, "先登重弓", "先登重弓", 1, "1", "先登重弓最擅长的就是爬上城墙以后把重重的箭矢丢下去砸人，但是经常会砸到一些花花草草，弄得一片狼藉。", "zhan_jiang_xiandengsishi.png", "head_xiandengsishi.png", "quan_bin_xiandengsishi.png", "quan_bin_xiandengsishi.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5160, nil, 7340, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30053, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|6001,1|30|5803", nil, nil, },
	id_30054 = {30054, "神风弩炮", "神风弩炮", 1, "1", "神风弩炮装有喷气推动装置，到了达官贵人们手里以后多数变成了兜风飚车的工具。", "bin_shenjinupao.png", "head_shenjinupao.png", "quan_bin_shenjinupao.png", "quan_bin_shenjinupao.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5100, nil, 7100, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30054, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|5903", nil, nil, },
	id_30055 = {30055, "南蛮战象", "南蛮战象", 1, "1", "南蛮战象生来性格暴躁，一有点不舒服，比如主人的屁股太硬了什么的，就会用头顶装有的刺扎住人。", "zhan_bing_xiangbing.png", "head_xiangbing.png", "quan_bin_xiangbing.png", "quan_bin_xiangbing.png", nil, "33", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5150, nil, 7400, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30055, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5901,1|30|3303", nil, nil, },
	id_30056 = {30056, "军乐师", "军乐师", 1, "1", "军乐师从不驻足于任何一个军营，只是四处游历，为那些寂寞难耐，不，思念家乡的士兵们带来最美的一曲。", "zhan_jiang_wujiang7.png", "head_wujiang7.png", "quan_jiang_wujiang7.png", "quan_jiang_wujiang7.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5090, nil, 7060, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30056, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3401,1|30|3203", nil, nil, },
	id_30057 = {30057, "高级医师", "高级医师", 1, "1", "高级医师最崇拜的人就是华佗，华佗的名字在她身边随处可见，比如绷带还有石膏上。", "zhan_jiang_wenguan6.png", "head_wenguan6.png", "quan_jiang_wenguan6.png", "quan_jiang_wenguan6.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6160, nil, 7310, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30057, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|4901,1|30|1003", nil, nil, },
	id_30058 = {30058, "花月舞姬", "花月舞姬", 1, "1", "花月舞姬最善在舞中杀人，翩翩起舞中在裙下掏出飞刀，飞刀被华服映照得如同花瓣一般，防不胜防。", "bin_wuji.png", "head_wuji.png", "quan_bin_wuji.png", "quan_bin_wuji.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5080, nil, 7420, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30058, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3201,1|30|3603", nil, nil, },
	id_30059 = {30059, "风行者", "风行者", 1, "1", "驭风行者由于所修习法术的一些特殊原因，做了一顶很高的头冠，就是为了不让狂风吹乱他的秀发。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_wenguan3.png", "quan_jiang_wenguan3.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5240, nil, 7170, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30059, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3401,1|30|3603", nil, nil, },
	id_30060 = {30060, "炎火术士", "炎火术士", 1, "1", "每到吃晚饭的时候，炎火术士就会看着厨房里绑着的食材们心想，今天是来个烤乳猪，还是整个小肥牛火锅呢？", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_wenguan1.png", "quan_jiang_wenguan1.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5200, nil, 7270, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30060, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3401,1|30|1103", nil, nil, },
	id_30061 = {30061, "冰雪策士", "冰雪策士", 1, "1", "冰雪策士高傲无比，常人难以亲近，因此他在军营中也得到了”冰山美人“的美称，虽然他其实并不美。", "zhan_jiang_wenguan4.png", "head_wenguan4.png", "quan_jiang_wenguan4.png", "quan_jiang_wenguan4.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5310, nil, 7030, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30061, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3401,1|30|3303", nil, nil, },
	id_30062 = {30062, "魔女", "魔女", 1, "1", "魔女准备匕首的原因就是不想是用自己天生强大的灵力，因为每当她使用雷电法术的时候，那一头柔顺靓丽的长发就会整个竖起来。", "zhan_jiang_wujiang10.png", "head_wujiang10.png", "quan_jiang_wujiang10.png", "quan_jiang_wujiang10.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5120, nil, 7370, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30062, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|1203", nil, nil, },
	id_30063 = {30063, "驭风行者", "驭风行者", 1, "1", "风行者很喜欢装深沉，比如把他自己打扮成一棵树的样子以后天天在嘴里念叨”树欲静而风不止…“。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_wenguan3.png", "quan_jiang_wenguan3.png", nil, "32", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5240, nil, 7340, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 2, nil, nil, 2, nil, 30063, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3402,1|30|3603", nil, nil, },
	id_30064 = {30064, "烈焰术士", "烈焰术士", 1, "1", "烈焰术士在一次施展大型火焰法术的时候不小心烧掉了自己的眉毛，弄得他以后每天只能画眉，嘘，不要提醒他眉毛画歪了。", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_wenguan1.png", "quan_jiang_wenguan1.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5200, nil, 7270, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30064, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3402,1|30|1103", nil, nil, },
	id_30065 = {30065, "沧浪策士", "沧浪策士", 1, "1", "即使是在每年长安最热的时候，沧浪策士也能穿着华服悠然自得走在大街上，那眼神仿佛在说”哼，人类真是脆弱的生物。“", "zhan_jiang_wenguan4.png", "head_wenguan4.png", "quan_jiang_wenguan4.png", "quan_jiang_wenguan4.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5310, nil, 7030, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30065, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3402,1|30|3303", nil, nil, },
	id_30066 = {30066, "妖艳魔女", "妖艳魔女", 1, "1", "她的两把匕首一把叫正极，一把叫负极，凡是胆敢靠近她的臭男人都会被这两把匕首雷得里焦外嫩。", "zhan_jiang_wujiang10.png", "head_wujiang10.png", "quan_jiang_wujiang10.png", "quan_jiang_wujiang10.png", nil, "33", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5120, nil, 7370, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 3, nil, nil, 3, nil, 30066, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1102,1|30|1203", nil, nil, },
