	id_30073 = {30073, "蓝版重枪兵", "重枪兵", 1, "1", "重枪兵在一次偶然的事件中得到了一把会吸血的长枪。从此以后，他的屋子里就再也没出现过蚊子。", "zhan_bing_changqiang.png", "head_changqiang.png", "quan_bin_changqiangbing.png", "quan_bin_changqiangbing.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7390, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30073, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5701,1|30|1202,1|45|6103,1|60|9301", nil, nil, },
	id_30074 = {30074, "蓝版战鼓兵", "战鼓兵", 1, "1", "战鼓兵有一项绝技，就是可以左手右手同时打出不同的鼓调，据说他的老师姓周。", "zhan_jiang_qigubing.png", "head_qigubing.png", "quan_bin_qigubing.png", "quan_bin_qigubing.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5090, nil, 7130, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30074, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3201,1|30|3402,1|45|5703,1|60|9301", nil, nil, },
	id_30075 = {30075, "蓝版高级刺客", "高级刺客", 1, "1", "高级刺客有太多的仇人，所以他决定，以后刺杀之前先把目标弄晕，绝不再让任何一个人看到他的脸。", "zhan_bing_cike.png", "head_cike.png", "quan_bin_cike.png", "quan_bin_cike.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5290, nil, 7080, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30075, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|7801,1|30|3602,1|45|1103,1|60|9301", nil, nil, },
	id_30076 = {30076, "蓝版妖术师", "妖术师", 1, "1", "妖术师总说“我们妖术师再妖但也是人啊！”，于是人们改口叫她人妖术师。", "zhan_jiang_wushushi.png", "head_wushushi.png", "quan_bin_wushushi.png", "quan_bin_wushushi.png", nil, "34", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 6100, nil, 7190, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30076, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|3402,1|45|5903,1|60|9301", nil, nil, },
	id_30077 = {30077, "蓝版长弓手", "长弓手", 1, "1", "长弓手喜欢一切长长的东西，长长的弓弦，长长的手臂，长长的靴子还有长长的大马尾辫。", "zhan_jiang_gongbing.png", "head_gongbing.png", "quan_bin_gongbing.png", "quan_bin_gongbing.png", nil, "34", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5170, nil, 7230, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30077, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|101,1|30|3602,1|45|1103,1|60|9301", nil, nil, },
	id_30078 = {30078, "蓝版狂战女神", "狂战女神", 1, "1", "狂战女神的魅力在战场上表现的淋漓尽致，他只要说一句“呵呵”就会有数不尽的士兵们为她卖命。", "zhan_jiang_jianwunvshen.png", "head_jianwunvshen.png", "quan_bin_jianwunvshen.png", "quan_bin_jianwunvshen.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5180, nil, 7150, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30078, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3601,1|30|9402,1|45|9503,1|60|9301", nil, nil, },
	id_30079 = {30079, "蓝版皇廷戟士", "皇廷戟士", 1, "1", "皇廷戟士无论是在什么战斗中都会冲到最前面，他的英姿早已迷的那些宫女们花枝招展了。", "zhan_bing_dajishi.png", "head_dajishi.png", "quan_bin_dajishi.png", "quan_bin_dajishi.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5260, nil, 7290, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30079, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3601,1|30|1102,1|45|6003,1|60|9301", nil, nil, },
	id_30080 = {30080, "蓝版贪狼侠客", "贪狼侠客", 1, "1", "贪狼侠客会把飞镖放在一起摩擦产生巨大的静电，带有静电的飞镖会连在一起作为近战用的兵器，所谓进可攻退可守。", "zhan_bing_feibiaoke.png", "head_feibiao.png", "quan_bin_feibiao.png", "quan_bin_feibiao.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5250, nil, 7400, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30080, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|9602,1|45|3503,1|60|9301", nil, nil, },
	id_30081 = {30081, "蓝版疾风轻骑", "疾风轻骑", 1, "1", "疾风轻骑有一匹多愁善感的马，每当跑得很快的时候马就会流下眼泪。马：“我只是跑太快沙子进眼睛里了！”", "zhan_bing_qibing.png", "head_qibing.png", "quan_bin_qibing.png", "quan_bin_qibing.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5050, nil, 7290, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30081, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3401,1|30|5602,1|45|3603,1|60|9301", nil, nil, },
	id_30082 = {30082, "蓝版高级影卫", "高级影卫", 1, "1", "高级影卫最近迷上了种些带毒的花花草草，他那两把影刃用来做割草刀真是最适合不过了。", "zhan_jiang_yingwei.png", "head_yingwei.png", "quan_bin_yingwei.png", "quan_bin_yingwei.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5270, nil, 7280, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30082, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1101,1|30|7802,1|45|3503,1|60|9301", nil, nil, },
	id_30083 = {30083, "蓝版重装弩兵", "重装弩兵", 1, "1", "重装弩兵早已习惯了长时间的拉锯战，为了确保弹药充足，他把箭矢藏在了盔甲的每一个部位。", "zhan_jiang_qiangnu.png", "head_qiangnu.png", "quan_bin_feibiao.png", "quan_bin_qiangnubing.png", nil, "34", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5070, nil, 7170, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30083, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3501,1|30|1102,1|45|9003,1|60|9301", nil, nil, },
	id_30084 = {30084, "蓝版战熊重骑", "战熊重骑", 1, "1", "这头熊是他很久以前从捕熊者手上救下来的。可惜的是，小可爱的爪子已经被陷阱弄断了一根。", "zhan_bing_feixiong.png", "head_feixiong.png", "quan_bin_feixiongjun.png", "quan_bin_feixiongjun.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5140, nil, 7160, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30084, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|3301,1|30|1102,1|45|5803,1|60|9301", nil, nil, },
	id_30085 = {30085, "蓝版虎豹骑士", "虎豹骑士", 1, "1", "虎豹骑士从小便与老虎生活在一起，一起吃饭，一起睡觉，长大以后才会教她人类的语言。她最喜欢的一个词语就是”母老虎“。", "zhan_bing_hubaoqi.png", "head_hubaoqi.png", "quan_bin_hubaoqi.png", "quan_bin_hubaoqi.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5220, nil, 7070, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30085, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|6202,1|45|1103,1|60|9301", nil, nil, },
	id_30086 = {30086, "蓝版刀车重步", "刀车重步", 1, "1", "刀车重步最恨被别人当作兵器架对待，不过也因此总是能收集到一些神兵利器或者奇怪的道具。", "bin_daoche.png", "head_daoche.png", "quan_bin_daoche.png", "quan_bin_daoche.png", nil, "34", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5300, nil, 7290, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30086, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|1201,1|30|802,1|45|5903,1|60|9301", nil, nil, },
	id_30087 = {30087, "蓝版狂斧铁骑", "狂斧铁骑", 1, "1", "狂斧铁骑经常会因为冲得太快而整个人飞出去。他为了在飞出去以后还能杀敌，毅然在头顶安了两个角。", "zhan_jiang_fuqibing.png", "head_fuqibing.png", "quan_bin_fuqibing.png", "quan_bin_fuqibing.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5040, nil, 7260, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30087, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|1202,1|45|5903,1|60|9301", nil, nil, },
	id_30088 = {30088, "蓝版龙骧战骑", "龙骧战骑", 1, "1", "龙骧战骑对战马的爱护已经到了痴迷的程度，而且一直是单身。大家都怀疑他头盔和马头盔上的宝石是情侣款。", "zhan_bing_longxiangqi.png", "head_longxiang.png", "quan_bin_longxiangqi.png", "quan_bin_longxiangqi.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5110, nil, 7240, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30088, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5901,1|30|9402,1|45|5603,1|60|9301", nil, nil, },
	id_30089 = {30089, "蓝版神风骑", "神风骑", 1, "1", "神风骑会在放大招的时候高举兵器大喊”赐给我力量吧！天神！“，然后一道雷就落了下来。", "zhan_bing_baimayicong.png", "head_baimayicong.png", "quan_bin_baimayicong.png", "quan_bin_baimayicong.png", nil, "34", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 5060, nil, 7390, "999|0,999|0,999|0", 900, 2, 7500, 7500, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 30089, nil, 1000, 5000, 10000, 500, 500, nil, "1|20|5601,1|30|3602,1|45|9003,1|60|9301", nil, nil, },
