	id_40001 = {40001, "经验熊猫", "经验熊猫", 1, "1", "伴随乱世出现的奇异熊猫，全身都是迷人的金色。可用于武将强化，提供5000武将经验。", "zhan_jiang_jingyan_xiongmao.png", "head_jingyan_xiongmao.png", "quan_jiang_ceshi.png", "quan_jiang_jingyan_xiongmao.png", nil, "41", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2940, nil, 8420, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 1, nil, nil, 1, nil, 40001, nil, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40002 = {40002, "无敌版关羽", "关羽", 1, "1", "三国时期蜀汉名将，“五虎上将”之首。与刘备张飞为结义兄弟，曾围襄阳、擒于禁、斩庞德、威震华夏。被后人崇为“武圣”。", "zhan_jiang_guanyu.png", "head_guanyu.png", "quan_jiang_wujiang1.png", "quan_jiang_guanyu.png", "nuqi_guanyu.png", "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 1100, nil, 1110, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 40002, 2, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40003 = {40003, "无敌版张飞", "张飞", 1, "1", "早年与刘备、关羽桃园结义。他性如烈火，嫉恶如仇，长坂坡当阳桥头上一声吼，吓退曹操数十万大军。", "zhan_jiang_zhangfei.png", "head_zhangfei.png", "quan_jiang_zhangfei.png", "quan_jiang_zhangfei.png", "nuqi_zhangfei.png", "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 1120, nil, 1130, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 40003, 2, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40004 = {40004, "无敌版刘备", "刘备", 1, "1", "三国时期蜀汉开国皇帝。他为人谦和、礼贤下士，素以仁德为世人称赞，是三国时期著名的政治家。", "zhan_jiang_liubei.png", "head_liubei.png", "quan_jiang_wenguan3.png", "quan_jiang_liubei.png", "nuqi_liubei.png", "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 1620, nil, 1630, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 40004, 2, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40005 = {40005, "无敌版曹操", "曹操", 1, "1", "东汉末年著名政治家、军事家、文学家、书法家。三国中曹魏政权的缔造者。其子曹丕称帝后，追尊其为魏武帝。", "zhan_jiang_caocao.png", "head_caocao.png", "quan_jiang_caocao.png", "quan_jiang_caocao.png", "nuqi_caocao.png", "41", nil, nil, nil, nil, 3, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 1060, nil, 1070, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 40005, 1, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40006 = {40006, "无敌版夏侯惇", "夏侯惇", 1, "1", "曹魏开国元勋。夏侯惇一生虽多在军旅，但仍不忘治学。一生不置产业，至死家无余财。", "zhan_jiang_xiahoudun.png", "head_xiahoudun.png", "quan_jiang_xiahoudun.png", "quan_jiang_xiahoudun.png", "nuqi_xiahoudun.png", "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 1080, nil, 1090, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 40006, 1, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40007 = {40007, "无敌版吕布", "吕布", 1, "1", "字奉先，持方天画戟，勇猛无双，天生神力。曾在虎牢关大战刘、关、张三人联手，一人独斗曹操军六员大将，公认的三国第一猛将。", "zhan_jiang_lvbu.png", "head_lvbu.png", "quan_jiang_lvbu.png", "quan_jiang_lvbu.png", "nuqi_lvbu.png", "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 1300, nil, 1310, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 40007, 4, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40008 = {40008, "无敌版备用1", "无敌版备用1", 1, "1", "曹操部下，他得知关羽杀了韩福，想谋害关羽，最后被关羽斩杀。", "zhan_jiang_wenguan5.png", "head_wenguan5.png", "quan_jiang_wenguan5.png", "quan_jiang_wenguan5.png", nil, "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 6110, nil, 7410, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 40008, 1, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_40009 = {40009, "无敌版备用2", "无敌版备用2", 1, "1", "孙权之女，孙鲁班之妹，朱据之妻。为人谦和，心思单纯。被其姐算计报复，遭到孙峻的冤杀。", "zhan_jiang_sunluyu.png", "head_sunluyu.png", "quan_jiang_wujiang7.png", "quan_jiang_sunluyu.png", nil, "41", nil, nil, nil, nil, 3, 9, 7, nil, nil, 108200, nil, nil, nil, nil, 6020, nil, 7370, "999|0,999|0,999|0", 500, 2, 6000, 6000, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 4, nil, nil, 4, nil, 40009, 3, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
