	id_50075 = {50075, "副本版于禁", "于禁", 1, "1", "曹操“五子良将”之一。曾于张绣造反时讨伐青州兵，同时为迎击敌军而固守营垒，因此曹操称赞他可与古代名将相比。", "zhan_jiang_yujin.png", "head_yujin.png", "quan_jiang_wujiang4.png", "quan_jiang_yujin.png", "nuqi_yujin.png", "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2180, nil, 2190, "999|0,999|0,999|0", 1100, 2, 9200, 8800, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10060, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|201,2|3|5901,2|4|1103,2|5|9302,2|6|4805,2|7|5707", "241,601,253,602,603", nil, },
	id_50076 = {50076, "副本版张郃", "张郃", 1, "1", "曹魏的“五子良将”之一。曹魏建立后加封为征西车骑将军。张郃戎马一生，以用兵巧变、善列营阵闻名，连诸葛亮都很忌惮他。", "zhan_jiang_zhanghe.png", "head_zhanghe.png", "quan_jiang_wujiang3.png", "quan_jiang_zhanghe.png", "nuqi_zhanghe.png", "41", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 1520, nil, 1530, "999|0,999|0,999|0", 1200, 2, 9400, 9500, 7700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10027, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|5601,2|4|3603,2|5|9302,2|6|1105,2|7|3507", "52,271,601,272,273", nil, },
	id_50077 = {50077, "副本版夏侯渊", "夏侯渊", 1, "1", "夏侯渊，字妙才，沛国谯人，东汉末年名将，擅长千里奔袭作战，官至征西将军，封博昌亭侯。率军驻凉州，逐马超、破韩遂、灭宋建、横扫羌、氐，虎步关右。", "zhan_jiang_xiahouyuan.png", "head_xiahouyuan.png", "quan_jiang_ceshi.png", "quan_jiang_xiahouyuan.png", "nuqi_xiahouyuan.png", "41", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 2740, nil, 2750, "999|0,999|0,999|0", 1200, 2, 9200, 9200, 7700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 5, nil, nil, 5, nil, 10175, 1, 1000, 5000, 10000, 500, 500, nil, "2|2|101,2|3|5601,2|4|3503,2|5|9302,2|6|6005,2|7|7807", "1751,1752,1753,1754,1755", nil, },
