	id_60012 = {60012, "橙卡陆逊", "陆逊", 1, "1", "三国时期东吴名将，历任吴国大都督、上大将军、丞相。陆逊在夷陵击败刘备所率蜀汉军，一战成名。", "zhan_jiang_luxun_1.png", "head_luxun_1.png", "quan_jiang_zhangzhao.png", "quan_jiang_luxun_1.png", "nuqi_luxun_1.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201220, nil, 201230, "999|0,999|0,999|0", 1300, 2, 10000, 8400, 10300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10012, 3, 1000, 5000, 10000, 500, 500, "901,3601,3803,9302,31200,30800", "2|0|4500,2|3|9301,2|5|3704,2|5|12709", "111,121,113,122,123", nil, },
	id_60013 = {60013, "橙卡甘宁", "甘宁", 1, "1", "少年有为，智勇双全，战功显赫。仗义疏财。深得士卒拥戴，被吴主孙权赏识，被孙权封为西陵太守，常胜将军。", "zhan_jiang_ganning_1.png", "head_ganning_1.png", "quan_jiang_ganning.png", "quan_jiang_ganning_1.png", "nuqi_ganning_1.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201240, nil, 201250, "999|0,999|0,999|0", 1200, 2, 9800, 10400, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10013, 3, 1000, 5000, 10000, 500, 500, "1001,3301,1203,9302,5605,1407", "2|0|4200,2|3|9301,2|5|4304,2|5|15809", "131,132,341,362,133", nil, },
	id_60014 = {60014, "橙卡孙策", "孙策", 1, "1", "三国时期吴国的奠基者之一。绰号“小霸王”。为继承父亲孙坚的遗业而屈事袁术，后脱离袁术，统一江东。", "zhan_jiang_sunce_1.png", "head_sunce_1.png", "quan_jiang_gaoshun.png", "quan_jiang_sunce_1.png", "nuqi_sunce_1.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201260, nil, 201270, "999|0,999|0,999|0", 1300, 2, 10300, 10200, 8500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10014, 3, 1000, 5000, 10000, 500, 500, "101,1101,5003,9302,3505,2009", "2|0|4300,2|3|9301,2|5|13904,2|5|16009", "141,142,362,143,144", nil, },
	id_60015 = {60015, "橙卡吕蒙", "吕蒙", 1, "1", "曾被封为虎威将军，曾经乘名将关羽北伐曹魏、荆州空虚之时，偷袭荆州成功，使东吴国土面积大增。", "zhan_jiang_lvmeng_1.png", "head_lvmeng_1.png", "quan_jiang_wujiang5.png", "quan_jiang_lvmeng_1.png", "nuqi_lvmeng_1.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201280, nil, 201290, "999|0,999|0,999|0", 1200, 2, 9900, 8700, 10100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10015, 3, 1000, 5000, 10000, 500, 500, "901,4201,3803,9302,2205,3607", "2|0|3700,2|3|9301,2|5|4304,2|5|14509", "121,151,341,152,153", nil, },
	id_60016 = {60016, "橙卡吕布", "吕布", 1, "1", "字奉先，持方天画戟，勇猛无双，天生神力。曾在虎牢关大战刘、关、张三人联手，一人独斗曹操军六员大将，公认的三国第一猛将。", "zhan_jiang_lvbu.png", "head_lvbu.png", "quan_jiang_lvbu.png", "quan_jiang_lvbu.png", "nuqi_lvbu.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201300, nil, 201310, "999|0,999|0,999|0", 1200, 2, 10000, 10800, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10016, 4, 1000, 5000, 10000, 500, 500, "101,31000,1103,9302,1505,3509", "2|0|4200,2|3|9301,2|5|3704,2|5|1809", "161,162,452,163,164", nil, },
	id_60017 = {60017, "橙卡贾诩", "贾诩", 1, "1", "著名谋士。奇谋百出，算无遗策，甚至曾令善于用兵的曹操几度败绩。曾先后担任三国军阀李傕、张绣、曹操的谋士。", "zhan_jiang_jiaxu_1.png", "head_jiaxu_1.png", "quan_jiang_wenguan5.png", "quan_jiang_jiaxu_1.png", "nuqi_jiaxu_1.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201320, nil, 201330, "999|0,999|0,999|0", 1200, 2, 9600, 8100, 10500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10017, 4, 1000, 5000, 10000, 500, 500, "101,3401,5303,9302,4105,3307", "2|0|4300,2|3|9301,2|5|4204,2|5|12609", "21,171,452,172,173", nil, },
	id_60018 = {60018, "橙卡貂蝉", "貂蝉", 1, "2", "王允的义女，国色天香，有倾国倾城之貌，“闭月羞花”中的闭月即为貂蝉。由王允授意施行连环计，借吕布之手除掉了董卓。", "zhan_jiang_diaochan_1.png", "head_diaochan_1.png", "quan_jiang_wenguan6.png", "quan_jiang_diaochan_1.png", "nuqi_diaochan_1.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201340, nil, 201350, "999|0,999|0,999|0", 1200, 2, 8800, 8600, 9600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10018, 4, 1000, 5000, 10000, 500, 500, "1001,3601,3703,9302,4905,31800", "2|0|4200,2|3|9301,2|5|3804,2|5|40200", "51,161,332,181,182", nil, },
	id_60019 = {60019, "橙卡华佗", "华佗", 1, "1", "著名医学家，与董奉、张仲景并称为“建安三神医”。医术全面，尤擅外科，精于手术，被后人称为“外科圣手”、“外科鼻祖”。", "zhan_jiang_huatuo_1.png", "head_huatuo_1.png", "quan_jiang_zhangzhao.png", "quan_jiang_huatuo_1.png", "nuqi_huatuo_1.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201360, nil, 201370, "999|0,999|0,999|0", 1200, 2, 8600, 7800, 10000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10019, 4, 1000, 5000, 10000, 500, 500, "1001,3601,3403,9302,4205,3307", "2|0|4300,2|3|9301,2|5|4404,2|5|14909", "61,171,282,191,192", nil, },
	id_60020 = {60020, "橙卡左慈", "左慈", 1, "1", "东汉末方士，精通五经，懂占星，精于奇门遁甲，可御使鬼神。黄天魔教的创始者之一，立志复活黄天魔皇扰乱天下曾使用道术戏弄于曹操。传说丹鼎派道术是从他一脉相传。", "zhan_jiang_zuoci_1.png", "head_zuoci_1.png", "quan_jiang_zuoci.png", "quan_jiang_zuoci_1.png", "nuqi_zuoci_1.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201380, nil, 201390, "999|0,999|0,999|0", 1200, 2, 9900, 8500, 10600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10020, 4, 1000, 5000, 10000, 500, 500, "1001,3601,8901,9302,31600,10209", "2|0|3800,2|3|9301,2|5|14304,2|5|16109", "201,202,93,203,204", nil, },
	id_60021 = {60021, "橙卡于吉", "于吉", 1, "1", "著名道士，黄天魔教的创始者之一，立志复活黄天魔皇扰乱天下，精通奇门遁甲，鬼神莫测。道教经典《太平经》的作者。", "zhan_jiang_yuji.png", "head_yuji.png", "quan_jiang_wenguan2.png", "quan_jiang_yuji.png", "nuqi_yuji.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201400, nil, 201410, "999|0,999|0,999|0", 1200, 2, 9800, 8500, 10500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10021, 4, 1000, 5000, 10000, 500, 500, "1001,1101,10005,9302,31600,10109", "2|3|9301,2|5|9304,2|5|9309", "211,212,93,213,214", nil, },
	id_60022 = {60022, "橙卡典韦", "典韦", 1, "1", "相貌魁梧，臂力过人。张绣背叛曹操，典韦独挡叛军，击杀多人，但终因寡不敌众而战死。", "zhan_jiang_dianwei.png", "head_dianwei.png", "quan_jiang_manchong.png", "quan_jiang_dianwei.png", "nuqi_dianwei.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201420, nil, 201430, "999|0,999|0,999|0", 1100, 2, 9100, 10400, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10022, 1, 1000, 5000, 10000, 500, 500, "1001,4001,5703,9302,5905,1107", "2|3|9301,2|5|9304,2|5|9309", "221,222,432,252,223", nil, },
	id_60023 = {60023, "橙卡徐晃", "徐晃", 1, "1", "“五子良将”之一。在曹操手下多立功勋，关羽围困樊城，徐晃率军长驱之入，一举击退蜀军。", "zhan_jiang_xuhuang.png", "head_xuhuang.png", "quan_jiang_lejin.png", "quan_jiang_xuhuang.png", "nuqi_xuhuang.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201440, nil, 201450, "999|0,999|0,999|0", 1200, 2, 10000, 9900, 8300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10023, 1, 1000, 5000, 10000, 500, 500, "1001,1101,5803,9302,1805,3807", "2|3|9301,2|5|9304,2|5|9309", "221,231,601,232,233", nil, },
	id_60024 = {60024, "橙卡曹仁", "曹仁", 1, "1", "曹操从弟。三国时期曹魏名将。官至大司马，封陈侯，谥曰忠侯。", "zhan_jiang_caoren.png", "head_caoren.png", "quan_jiang_wujiang4.png", "quan_jiang_caoren.png", "nuqi_caoren.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201460, nil, 201470, "999|0,999|0,999|0", 1100, 2, 9700, 9500, 8400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10024, 1, 1000, 5000, 10000, 500, 500, "1001,4401,6903,9302,1405,6107", "2|3|9301,2|5|9304,2|5|9309", "101,241,432,242,243", nil, },
