	id_60028 = {60028, "橙卡姜维", "姜维", 1, "1", "三国时期蜀汉名将。“文能提笔安天下，武能上马定乾坤”即为姜维。", "zhan_jiang_jiangwei.png", "head_jiangwei.png", "quan_jiang_lidian.png", "quan_jiang_jiangwei.png", "nuqi_jiangwei.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201540, nil, 201550, "999|0,999|0,999|0", 1300, 2, 9700, 9500, 10000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10028, 2, 1000, 5000, 10000, 500, 500, "901,1101,3703,9302,2005,5907", "2|3|9301,2|5|9301,2|5|15409", "71,281,282,283,284", nil, },
	id_60029 = {60029, "橙卡魏延", "魏延", 1, "1", "三国时期蜀汉名将。因屡立战功而深受刘备信任，蜀汉建立后升为镇北将军。", "zhan_jiang_weiyan_1.png", "head_weiyan_1.png", "quan_jiang_ceshi.png", "quan_jiang_weiyan_1.png", "nuqi_weiyan_1.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201560, nil, 201570, "999|0,999|0,999|0", 1100, 2, 9600, 9900, 8400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10029, 2, 1000, 5000, 10000, 500, 500, "101,1101,5903,9302,1805,3307", "2|0|3900,2|3|9301,2|5|3704,2|5|15009", "62,72,252,297,298", nil, },
	id_60030 = {60030, "橙卡黄忠", "黄忠", 1, "1", "蜀国五虎大将之一，有勇有谋，老当益壮，拥有百步穿杨的箭术，百发百中。", "zhan_jiang_huangzhong.png", "head_huangzhong.png", "quan_jiang_huangzhong.png", "quan_jiang_huangzhong.png", "nuqi_huangzhong.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201580, nil, 201590, "999|0,999|0,999|0", 1100, 2, 9500, 10200, 7900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10030, 2, 1000, 5000, 10000, 500, 500, "101,3601,7803,9302,3505,8501", "2|3|9301,2|5|9304,2|5|9309", "301,302,303,304,305", nil, },
	id_60031 = {60031, "橙卡徐庶", "徐庶", 1, "1", "与司马徽、诸葛亮等人为友。先曾仕官于刘备，后因曹操囚禁其母而不得不弃备投操，临行前向刘备推荐诸葛亮之才。", "zhan_jiang_xushu.png", "head_xushu.png", "quan_jiang_liru.png", "quan_jiang_xushu.png", "nuqi_xushu.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201600, nil, 201610, "999|0,999|0,999|0", 1100, 2, 9400, 7900, 10100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10031, 2, 1000, 5000, 10000, 500, 500, "901,1101,4503,9302,4705,2007", "2|3|9301,2|5|9304,2|5|9309", "201,311,332,312,313", nil, },
	id_60032 = {60032, "橙卡刘备", "刘备", 1, "1", "三国时期蜀汉开国皇帝。他为人谦和、礼贤下士，素以仁德为世人称赞，是三国时期著名的政治家。", "zhan_jiang_liubei.png", "head_liubei.png", "quan_jiang_wenguan3.png", "quan_jiang_liubei.png", "nuqi_liubei.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201620, nil, 201630, "999|0,999|0,999|0", 1200, 2, 9300, 9300, 9700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10032, 2, 1000, 5000, 10000, 500, 500, "901,5701,3603,9302,30500,1907", "2|3|9301,2|5|9304,2|5|9309", "321,322,73,323,324", nil, },
	id_60033 = {60033, "橙卡黄月英", "黄月英", 1, "2", "诸葛亮之妻，多才多艺，熟读兵书，上知天文，下知地理，文韬武略，足智多谋。", "zhan_jiang_huangyueying.png", "head_huangyueying.png", "quan_jiang_huangyueying.png", "quan_jiang_huangyueying.png", "nuqi_huangyueying.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201640, nil, 201650, "999|0,999|0,999|0", 1100, 2, 8700, 7400, 9800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10033, 2, 1000, 5000, 10000, 500, 500, "1001,4401,3303,9302,5105,3607", "2|3|9301,2|5|9304,2|5|9309", "301,331,332,333,334", nil, },
	id_60034 = {60034, "橙卡太史慈", "太史慈", 1, "1", "三国时期东吴名将。弓马熟练，箭法精良。为人磊落坦荡，尽义守信，智勇双全。", "zhan_jiang_taishici_1.png", "head_taishici_1.png", "quan_jiang_wujiang2.png", "quan_jiang_taishici_1.png", "nuqi_taishici_1.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201660, nil, 201670, "999|0,999|0,999|0", 1100, 2, 9800, 10000, 7900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10034, 3, 1000, 5000, 10000, 500, 500, "901,3601,1103,9302,9705,9607", "2|0|3700,2|3|9301,2|5|3804,2|5|3509", "102,281,341,347,348", nil, },
	id_60035 = {60035, "橙卡孙坚", "孙坚", 1, "1", "三国时东吴著名将领，容貌不凡，性格豪爽阔达，吴国霸业奠基人，军事家孙武后裔，被后世尊称为武烈皇帝。", "zhan_jiang_sunjian.png", "head_sunjian.png", "quan_jiang_sunjian.png", "quan_jiang_sunjian.png", "nuqi_sunjian.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201680, nil, 201690, "999|0,999|0,999|0", 1100, 2, 9700, 9700, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10035, 3, 1000, 5000, 10000, 500, 500, "901,9501,4503,9302,1205,1407", "2|3|9301,2|5|9304,2|5|9309", "351,352,341,353,354", nil, },
	id_60036 = {60036, "橙卡孙权", "孙权", 1, "1", "三国时期吴国的开国皇帝，任贤用能、挽救了江东危局，保住了父兄基业。", "zhan_jiang_sunquan.png", "head_sunquan.png", "quan_jiang_wujiang3.png", "quan_jiang_sunquan.png", "nuqi_sunquan.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201700, nil, 201710, "999|0,999|0,999|0", 1100, 2, 9500, 8900, 9500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10036, 3, 1000, 5000, 10000, 500, 500, "101,6001,1903,9302,30600,4907", "2|3|9301,2|5|9304,2|5|9309", "151,361,362,363,364", nil, },
	id_60037 = {60037, "橙卡孙尚香", "孙尚香", 1, "2", "孙尚香为三国时期吴国人，后嫁与刘备。其自幼喜好武艺，手下侍女皆带刀具，常以与人击剑为乐，且容姿甚美。", "zhan_jiang_sunshangxiang.png", "head_sunshangxiang.png", "quan_jiang_sunshangxiang.png", "quan_jiang_sunshangxiang.png", "nuqi_sunshangxiang.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201720, nil, 201730, "999|0,999|0,999|0", 1100, 2, 9000, 9800, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10037, 3, 1000, 5000, 10000, 500, 500, "101,7801,7703,9302,3005,1107", "2|3|9301,2|5|9304,2|5|9309", "321,352,393,371,372", nil, },
	id_60038 = {60038, "橙卡小乔", "小乔", 1, "2", "三国时期世人皆知的美女。东吴名将周瑜的妻子。国色天香，资貌绝伦。传说与其姊大乔均为绝世美女。", "zhan_jiang_xiaoqiao.png", "head_xiaoqiao.png", "quan_jiang_xiaoqiao.png", "quan_jiang_xiaoqiao.png", "nuqi_xiaoqiao.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201740, nil, 201750, "999|0,999|0,999|0", 1100, 2, 8500, 7600, 9700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10038, 3, 1000, 5000, 10000, 500, 500, "1001,5701,9503,9302,7705,9207", "2|3|9301,2|5|9304,2|5|9309", "112,381,393,382,383", nil, },
	id_60039 = {60039, "橙卡大乔", "大乔", 1, "2", "与小乔并称为“江东二乔”，据传为绝世美女。有沉鱼落雁之容，知书达理，通音律，晓诗文。孙策之妻、孙权之嫂。", "zhan_jiang_daqiao.png", "head_daqiao.png", "quan_jiang_daqiao.png", "quan_jiang_daqiao.png", "nuqi_daqiao.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201760, nil, 201770, "999|0,999|0,999|0", 1100, 2, 8300, 8200, 9000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10039, 3, 1000, 5000, 10000, 500, 500, "1001,3601,5703,9302,7705,4907", "2|3|9301,2|5|9304,2|5|9309", "392,391,393,394,395", nil, },
	id_60040 = {60040, "橙卡文丑", "文丑", 1, "1", "河北七上将之一。孔融曾以其与颜良提醒曹操小心袁绍部队。磐河一战，文丑连败数将，差点擒获公孙瓒，与赵云大战未分胜负。", "zhan_jiang_wenchou.png", "head_wenchou.png", "quan_jiang_zhangbao.png", "quan_jiang_wenchou.png", "nuqi_wenchou.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201780, nil, 201790, "999|0,999|0,999|0", 1100, 2, 9600, 10000, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10040, 4, 1000, 5000, 10000, 500, 500, "901,4201,3603,9302,5805,5607", "2|3|9301,2|5|9304,2|5|9309", "401,402,403,404,405", nil, },
	id_60041 = {60041, "橙卡颜良", "颜良", 1, "1", "袁绍部将，河北七上将之一。骁勇善战，与文丑一同作战时，有万夫莫敌之势。孔融曾道“颜良、文丑，勇冠三军。”", "zhan_jiang_yanliang.png", "head_yanliang.png", "quan_jiang_sunjian.png", "quan_jiang_yanliang.png", "nuqi_yanliang.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201800, nil, 201810, "999|0,999|0,999|0", 1100, 2, 9600, 10100, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10041, 4, 1000, 5000, 10000, 500, 500, "901,4301,1103,9302,5905,3907", "2|3|9301,2|5|9304,2|5|9309", "401,411,412,413,414", nil, },
	id_60042 = {60042, "橙卡张角", "张角", 1, "1", "“张角军”的领袖，太平道的创始人。以宗教救世为己任，自称“天公将军”，率领群众发动起义，史称“张角起义”。", "zhan_jiang_zhangjiao_1.png", "head_zhangjiao_1.png", "quan_jiang_wenguan4.png", "quan_jiang_zhangjiao_1.png", "nuqi_zhangjiao_1.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201820, nil, 201830, "999|0,999|0,999|0", 1100, 2, 9400, 8100, 10000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10042, 4, 1000, 5000, 10000, 500, 500, "901,3601,3303,9302,6605,7907", "2|0|4300,2|3|9301,2|5|3704,2|5|12409", "202,211,452,427,428", nil, },
	id_60043 = {60043, "橙卡华雄", "华雄", 1, "1", "董卓帐下一名虎将。身长九尺，虎体狼腰，豹头猿臂。在汜水关先后斩杀多名大将，最后被关羽所杀，这就是著名的“温酒斩华雄”。", "zhan_jiang_huaxiong.png", "head_huaxiong.png", "quan_jiang_huaxiong.png", "quan_jiang_huaxiong.png", "nuqi_huaxiong.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201840, nil, 201850, "999|0,999|0,999|0", 1100, 2, 9400, 9700, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10043, 4, 1000, 5000, 10000, 500, 500, "901,1101,5603,9302,1205,3307", "2|3|9301,2|5|9304,2|5|9309", "402,431,432,433,434", nil, },
	id_60044 = {60044, "橙卡陈宫", "陈宫", 1, "1", "吕布帐下谋士。性情刚直，足智多谋，对吕布忠心不二。吕布战败后，随吕布等一同被曹操所擒，决意赴死。", "zhan_jiang_chengong.png", "head_chengong.png", "quan_jiang_chengong.png", "quan_jiang_chengong.png", "nuqi_chengong.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201860, nil, 201870, "999|0,999|0,999|0", 1100, 2, 8500, 7900, 9200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10044, 4, 1000, 5000, 10000, 500, 500, "901,3801,5303,9302,5705,7807", "2|3|9301,2|5|9304,2|5|9309", "162,411,522,441,442", nil, },
	id_60045 = {60045, "橙卡董卓", "董卓", 1, "1", "生于富贵豪强之家，性格粗野凶狠有谋略。体魄健壮，臂力过人，善骑射。进京倒行逆施，迫害忠良，甚至操控政权。", "zhan_jiang_dongzhuo.png", "head_dongzhuo.png", "quan_jiang_dongzhuo.png", "quan_jiang_dongzhuo.png", "nuqi_dongzhuo.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201880, nil, 201890, "999|0,999|0,999|0", 1000, 2, 9100, 9400, 7900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10045, 4, 1000, 5000, 10000, 500, 500, "901,1201,4803,9302,30700,1707", "2|3|9301,2|5|9304,2|5|9309", "431,451,452,453,454", nil, },
	id_60046 = {60046, "橙卡蔡文姬", "蔡文姬", 1, "2", "东汉大文学家蔡邕的女儿，是中国历史上著名的才女和文学家，精于天文数理，既博学能文，又善诗赋，兼长辩才与音律。", "zhan_jiang_caiwenji.png", "head_caiwenji.png", "quan_jiang_caiwenji.png", "quan_jiang_caiwenji.png", "nuqi_caiwenji.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201900, nil, 201910, "999|0,999|0,999|0", 1100, 2, 8600, 8100, 9200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10046, 1, 1000, 5000, 10000, 500, 500, "1001,9401,3603,9302,7705,4907", "2|3|9301,2|5|9304,2|5|9309", "331,403,731,461,462", nil, },
	id_60047 = {60047, "橙卡曹丕", "曹丕", 1, "1", "三国时期著名的政治家、文学家，曹魏的开国皇帝。魏武帝曹操与卞夫人的长子。与其父曹操、其弟曹植并称为“三曹”。", "zhan_jiang_caopei.png", "head_caopei.png", "quan_jiang_wujiang4.png", "quan_jiang_caopei.png", "nuqi_caopei.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201920, nil, 201930, "999|0,999|0,999|0", 1000, 2, 9300, 8400, 9200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10047, 1, 1000, 5000, 10000, 500, 500, "1001,3801,9003,9302,3305,3607", "2|3|9301,2|5|9304,2|5|9309", "222,261,471,472,473", nil, },
	id_60048 = {60048, "橙卡孟获", "孟获", 1, "1", "三国时期南中一带少数民族首领，曾经起兵反叛蜀汉，后来被诸葛亮七擒七纵并降服。", "zhan_jiang_menghuo.png", "head_menghuo.png", "quan_jiang_xusheng.png", "quan_jiang_menghuo.png", "nuqi_menghuo.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201940, nil, 201950, "999|0,999|0,999|0", 1000, 2, 9000, 9800, 6900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10048, 2, 1000, 5000, 10000, 500, 500, "1001,6901,4003,9302,5805,1107", "2|3|9301,2|5|9304,2|5|9309", "91,481,493,482,483", nil, },
	id_60049 = {60049, "橙卡祝融", "祝融", 1, "2", "火神祝融氏后裔，南蛮王孟获之妻。武艺高强，善使飞刀，在诸葛亮七擒七纵孟获之后，随孟获投降蜀汉。", "zhan_jiang_zhurong.png", "head_zhurong.png", "quan_jiang_wujiang11.png", "quan_jiang_zhurong.png", "nuqi_zhurong.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201960, nil, 201970, "999|0,999|0,999|0", 1000, 2, 8900, 9400, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10049, 2, 1000, 5000, 10000, 500, 500, "901,4401,1103,9302,5905,6607", "2|3|9301,2|5|9304,2|5|9309", "491,492,493,494,495", nil, },
	id_60050 = {60050, "橙卡黄盖", "黄盖", 1, "1", "赤壁之战时，黄盖施以苦肉计前往曹营诈降，并趁机以火攻大破曹操的军队，是赤壁之战主要功臣之一。", "zhan_jiang_huanggai.png", "head_huanggai.png", "quan_jiang_huanggai.png", "quan_jiang_huanggai.png", "nuqi_huanggai.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201980, nil, 201990, "999|0,999|0,999|0", 1100, 2, 9300, 9900, 7000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10050, 3, 1000, 5000, 10000, 500, 500, "901,3501,5803,9302,4205,3207", "2|3|9301,2|5|9304,2|5|9309", "501,502,722,503,504", nil, },
	id_60051 = {60051, "橙卡鲁肃", "鲁肃", 1, "1", "中国东汉末年杰出战略家，著名政治家、外交家。他曾为孙权提出鼎足江东的战略规划，因此得到孙权的赏识。", "zhan_jiang_lusu.png", "head_lusu.png", "quan_jiang_liubiao.png", "quan_jiang_lusu.png", "nuqi_lusu.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202000, nil, 202010, "999|0,999|0,999|0", 1100, 2, 8800, 8100, 10000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10051, 3, 1000, 5000, 10000, 500, 500, "901,4901,3603,9302,3705,6307", "2|3|9301,2|5|9304,2|5|9309", "131,501,113,511,512", nil, },
	id_60052 = {60052, "橙卡陈登", "陈登", 1, "1", "为人爽朗，性格沈静，智谋过人，少年时有扶世济民之志，并且博览群书，学识渊博。内政军事皆是良才。", "zhan_jiang_chendeng.png", "head_chendeng.png", "quan_jiang_lidian.png", "quan_jiang_chendeng.png", "nuqi_chendeng.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202020, nil, 202030, "999|0,999|0,999|0", 1100, 2, 8700, 7700, 9800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10052, 4, 1000, 5000, 10000, 500, 500, "901,3601,4403,9302,3405,5907", "2|3|9301,2|5|9304,2|5|9309", "212,521,522,523,524", nil, },
	id_60053 = {60053, "橙卡高顺", "高顺", 1, "1", "三国中吕布帐下中郎将。为人清白有威严，不好饮酒，忠于吕布。所统率的部队精锐非常，号称“陷阵营”。被曹操所俘之后拒不投降。", "zhan_jiang_gaoshun.png", "head_gaoshun.png", "quan_jiang_gaoshun.png", "quan_jiang_gaoshun.png", "nuqi_gaoshun.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202040, nil, 202050, "999|0,999|0,999|0", 1100, 2, 9400, 9700, 7900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10053, 4, 1000, 5000, 10000, 500, 500, "901,3901,5803,9302,3505,5207", "2|3|9301,2|5|9304,2|5|9309", "412,531,522,532,533", nil, },
	id_60054 = {60054, "橙卡乐进", "乐进", 1, "2", "曹操“五子良将”之一。容貌短小，但胆识过人，性格刚烈，随曹多年，南征北讨，战功无数。", "zhan_jiang_lejin.png", "head_lejin.png", "quan_jiang_lejin.png", "quan_jiang_lejin.png", "nuqi_lejin.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202060, nil, 202070, "999|0,999|0,999|0", 1000, 2, 9500, 9400, 7800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10058, 1, 1000, 5000, 10000, 500, 500, "201,1101,5303,9302,5605,5807", nil, "531,601,253,581,582", nil, },
	id_60055 = {60055, "橙卡于禁", "于禁", 1, "1", "曹操“五子良将”之一。曾于张绣造反时讨伐青州兵，同时为迎击敌军而固守营垒，因此曹操称赞他可与古代名将相比。", "zhan_jiang_yujin.png", "head_yujin.png", "quan_jiang_wujiang4.png", "quan_jiang_yujin.png", "nuqi_yujin.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202080, nil, 202090, "999|0,999|0,999|0", 1000, 2, 9700, 9200, 7400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10060, 1, 1000, 5000, 10000, 500, 500, "201,5901,1103,9302,4805,5707", nil, "241,601,253,602,603", nil, },
	id_60056 = {60056, "橙卡周泰", "周泰", 1, "1", "曾经多次保护孙权免受敌军的攻击。孙权为表彰周泰为东吴出生入死的功绩，而赐他青罗伞盖。", "zhan_jiang_zhoutai.png", "head_zhoutai.png", "quan_jiang_lejin.png", "quan_jiang_zhoutai.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202100, nil, 202110, "999|0,999|0,999|0", 1000, 2, 9200, 9900, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10070, 3, 1000, 5000, 10000, 500, 500, "201,3201,1103,9302,5805,3707", nil, "502,701,702,761,703", nil, },
	id_60057 = {60057, "橙卡李典", "李典", 1, "1", "深明大义，不与人争功，崇尚学习与高贵儒雅，尊重博学之士。李典有长者之风，官至破虏将军。", "zhan_jiang_lidian.png", "head_lidian.png", "quan_jiang_lidian.png", "quan_jiang_lidian.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202120, nil, 202130, "999|0,999|0,999|0", 1000, 2, 9100, 8600, 6900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10057, 1, 1000, 5000, 10000, 500, 500, "301,1101,1203,9302,7805,", nil, "571,572,573,574", nil, },
	id_60058 = {60058, "橙卡乐进", "乐进", 1, "1", "曹操“五子良将”之一。容貌短小，但胆识过人，性格刚烈，随曹多年，南征北讨，战功无数。", "zhan_jiang_lejin.png", "head_lejin.png", "quan_jiang_lejin.png", "quan_jiang_lejin.png", "nuqi_lejin.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202060, nil, 202070, "999|0,999|0,999|0", 1100, 2, 9500, 9500, 7900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10058, 1, 1000, 5000, 10000, 500, 500, "201,1101,5303,9302,5605,5807", nil, "531,601,253,581,582", nil, },
	id_60059 = {60059, "橙卡许攸", "许攸", 1, "1", "本为袁绍帐下谋士，后背袁投曹，睿智多谋。并为曹操设下偷袭袁绍军屯粮之所乌巢的计策，袁绍因此而大败于官渡。", "zhan_jiang_xuyou.png", "head_xuyou.png", "quan_jiang_zhangzhao.png", "quan_jiang_xuyou.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202160, nil, 202170, "999|0,999|0,999|0", 900, 2, 8200, 8100, 8100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10059, 1, 1000, 5000, 10000, 500, 500, "301,3201,4703,9302,1205,", nil, "551,571,591,592", nil, },
	id_60060 = {60060, "橙卡于禁", "于禁", 1, "1", "曹操“五子良将”之一。曾于张绣造反时讨伐青州兵，同时为迎击敌军而固守营垒，因此曹操称赞他可与古代名将相比。", "zhan_jiang_yujin.png", "head_yujin.png", "quan_jiang_wujiang4.png", "quan_jiang_yujin.png", "nuqi_yujin.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202080, nil, 202090, "999|0,999|0,999|0", 1100, 2, 9700, 9300, 7500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10060, 1, 1000, 5000, 10000, 500, 500, "201,5901,1103,9302,4805,5707", nil, "241,601,253,602,603", nil, },
	id_60061 = {60061, "橙卡荀攸", "荀攸", 1, "1", "东汉末年曹操的五谋臣之一，荀彧从子，被曹操称为“谋主”，擅长灵活多变的克敌战术和军事策略。辅佐曹操统一北方。", "zhan_jiang_xunyou.png", "head_xunyou.png", "quan_jiang_liru.png", "quan_jiang_xunyou.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202200, nil, 202210, "999|0,999|0,999|0", 1000, 2, 7900, 7000, 9800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10061, 1, 1000, 5000, 10000, 500, 500, "301,1101,4003,9302,5105,", nil, "552,572,801,611", nil, },
	id_60062 = {60062, "橙卡曹洪", "曹洪", 1, "1", "曹操之侄。曹操追袭董卓荥阳为徐荣所败失马，曹洪舍命献马并救护曹操，使曹操免于厄难。", "zhan_jiang_caohong.png", "head_caohong.png", "quan_jiang_caohong.png", "quan_jiang_caohong.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202220, nil, 202230, "999|0,999|0,999|0", 1000, 2, 8600, 9400, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10062, 1, 1000, 5000, 10000, 500, 500, "201,9601,3203,9302,7805,", nil, "621,691,622", nil, },
	id_60063 = {60063, "橙卡关银萍", "关银萍", 1, "2", "美丽聪慧，是赵云的弟子、并随同诸葛亮平定南蛮，嫁给蜀国名臣李恢之子李遗。", "zhan_jiang_guanyinping.png", "head_guanyinping.png", "quan_jiang_guanyinping.png", "quan_jiang_guanyinping.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202240, nil, 202250, "999|0,999|0,999|0", 1000, 2, 8700, 9400, 7000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10063, 2, 1000, 5000, 10000, 500, 500, "301,1101,1203,9302,3605,", nil, "491,631,632,633", nil, },
	id_60064 = {60064, "橙卡张星彩", "张星彩", 1, "2", "张飞之女，品德和才情为诸葛亮所赞，后嫁给蜀国后主刘禅。", "zhan_jiang_zhangxingcai.png", "head_zhangxingcai.png", "quan_jiang_zhangxingcai.png", "quan_jiang_zhangxingcai.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202260, nil, 202270, "999|0,999|0,999|0", 1000, 2, 8600, 9400, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10064, 2, 1000, 5000, 10000, 500, 500, "301,3601,1103,9302,1205,", nil, "631,641,591,642", nil, },
	id_60065 = {60065, "橙卡马岱", "马岱", 1, "1", "三国时期蜀汉武将，马超的从弟。官至平北将军，陈仓侯。在诸葛亮病逝后受杨仪派遣斩杀了蜀将魏延。", "zhan_jiang_madai.png", "head_madai.png", "quan_jiang_madai.png", "quan_jiang_madai.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202280, nil, 202290, "999|0,999|0,999|0", 1000, 2, 8500, 9300, 6900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10065, 2, 1000, 5000, 10000, 500, 500, "201,3601,6003,9302,1105,", nil, "651,652,653,654", nil, },
	id_60066 = {60066, "橙卡周仓", "周仓", 1, "1", "本为张宝麾下之将，被刘备率领之义勇军降服后，与廖化一起成为关羽的部下，最后在关羽父子被斩之后自刎而死。", "zhan_jiang_zhoucang.png", "head_zhoucang.png", "quan_jiang_zhoucang.png", "quan_jiang_zhoucang.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202300, nil, 202310, "999|0,999|0,999|0", 900, 2, 8600, 8500, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10066, 2, 1000, 5000, 10000, 500, 500, "201,5901,3303,9302,1105,", nil, "661,662,653,663", nil, },
	id_60067 = {60067, "橙卡关兴", "关兴", 1, "1", "蜀汉武将，是名将关羽次子，关平之弟。自幼习武，弓马娴熟。长相酷似关羽，斩杀潘璋为父报仇，夺回青龙偃月刀。", "zhan_jiang_guanxing.png", "head_guanxing.png", "quan_jiang_wenpin.png", "quan_jiang_guanxing.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202320, nil, 202330, "999|0,999|0,999|0", 900, 2, 8900, 8700, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10067, 2, 1000, 5000, 10000, 500, 500, "201,3601,1103,9302,5605,", nil, "481,671,632,672", nil, },
	id_60068 = {60068, "橙卡关平", "关平", 1, "1", "名将关羽的长子，随关羽南征北战，屡立功劳。之后逐渐成长为一员大将，曾与庞德大战三十回合，不分胜负。", "zhan_jiang_guanping.png", "head_guanping.png", "quan_jiang_manchong.png", "quan_jiang_guanping.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202340, nil, 202350, "999|0,999|0,999|0", 900, 2, 8700, 8800, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10068, 2, 1000, 5000, 10000, 500, 500, "201,1101,5603,9302,3605,", nil, "681,682,632,683", nil, },
	id_60069 = {60069, "橙卡张苞", "张苞", 1, "1", "三国时期蜀臣，车骑将军张飞的长子。以家传的丈八蛇矛为武器，是一名虎将。", "zhan_jiang_zhangbao.png", "head_zhangbao.png", "quan_jiang_zhangbao.png", "quan_jiang_zhangbao.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202360, nil, 202370, "999|0,999|0,999|0", 1000, 2, 8900, 8800, 7400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10069, 2, 1000, 5000, 10000, 500, 500, "201,5601,1103,9302,1205,", nil, "641,651,691,692", nil, },
	id_60070 = {60070, "橙卡周泰", "周泰", 1, "1", "曾经多次保护孙权免受敌军的攻击。孙权为表彰周泰为东吴出生入死的功绩，而赐他青罗伞盖。", "zhan_jiang_zhoutai.png", "head_zhoutai.png", "quan_jiang_lejin.png", "quan_jiang_zhoutai.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202100, nil, 202110, "999|0,999|0,999|0", 1100, 2, 9200, 10000, 7300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10070, 3, 1000, 5000, 10000, 500, 500, "201,3201,1103,9302,5805,3707", nil, "502,701,702,761,703", nil, },
	id_60071 = {60071, "橙卡张昭", "张昭", 1, "1", "三国时期吴国重臣，善丹青。孙策创业，命张昭为长史、抚军中郎将，文武之事，全部委托给张昭。", "zhan_jiang_zhangzhao.png", "head_zhangzhao.png", "quan_jiang_zhangzhao.png", "quan_jiang_zhangzhao.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202400, nil, 202410, "999|0,999|0,999|0", 1000, 2, 7500, 7500, 8300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10071, 3, 1000, 5000, 10000, 500, 500, "301,3301,4303,9302,4205,", nil, "701,711,722,712", nil, },
	id_60072 = {60072, "橙卡凌统", "凌统", 1, "1", "凌操之子，官至偏将军。年少既有名望，为人有国士之风，多次战役中表现出色，官至偏将军。", "zhan_jiang_lingtong.png", "head_lingtong.png", "quan_jiang_lingtong.png", "quan_jiang_lingtong.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202420, nil, 202430, "999|0,999|0,999|0", 1000, 2, 8900, 8800, 7300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10072, 3, 1000, 5000, 10000, 500, 500, "201,1101,5603,9302,9605,", nil, "702,721,722,723", nil, },
	id_60073 = {60073, "橙卡步练师", "步练师", 1, "2", "吴大帝孙权之妃，在孙权众夫人中最受孙权的宠爱，生有二女：孙鲁班、孙鲁育。后追封为皇后，葬于蒋陵。", "zhan_jiang_bulianshi.png", "head_bulianshi.png", "quan_jiang_bulianshi.png", "quan_jiang_bulianshi.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202440, nil, 202450, "999|0,999|0,999|0", 900, 2, 7400, 7500, 8100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10073, 3, 1000, 5000, 10000, 500, 500, "301,1201,1103,9302,3205,", nil, "381,391,731,732", nil, },
	id_60074 = {60074, "橙卡诸葛瑾", "诸葛瑾", 1, "1", "吴国大臣，诸葛亮之兄，诸葛恪之父。胸怀宽广，温厚诚信，得到孙权的深深信赖，努力缓和蜀汉与东吴的关系。", "zhan_jiang_zhugejin.png", "head_zhugejin.png", "quan_jiang_liubiao.png", "quan_jiang_zhugejin.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202460, nil, 202470, "999|0,999|0,999|0", 1000, 2, 7900, 7300, 9400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10074, 3, 1000, 5000, 10000, 500, 500, "301,5701,4303,9302,3705,", nil, "711,741,801,742", nil, },
	id_60075 = {60075, "橙卡徐盛", "徐盛", 1, "1", "吴国将领。机智善谋。各大战役均有参加。后成为驻防吴国东侧的大将，曾献疑城之计，击退魏军。", "zhan_jiang_xusheng.png", "head_xusheng.png", "quan_jiang_xusheng.png", "quan_jiang_xusheng.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202480, nil, 202490, "999|0,999|0,999|0", 1100, 2, 9200, 9800, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10075, 3, 1000, 5000, 10000, 500, 500, "201,1101,3703,9302,4805,4307", nil, "132,361,761,751,752", nil, },
	id_60076 = {60076, "橙卡陆抗", "陆抗", 1, "1", "吴国名将，陆逊次子，孙策外孙。清白忠勤。孙皓为帝时，任镇军大将军。击退晋将羊祜进攻，并攻杀叛将西陵督步阐。", "zhan_jiang_lukang.png", "head_lukang.png", "quan_jiang_wujiang4.png", "quan_jiang_lukang.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202500, nil, 202510, "999|0,999|0,999|0", 1000, 2, 9500, 6700, 8400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10076, 3, 1000, 5000, 10000, 500, 500, "201,5601,1203,9302,5805,", nil, "671,721,761,762", nil, },
	id_60077 = {60077, "橙卡程普", "程普", 1, "1", "历仕孙坚、孙策、孙权三任君主。乐善好施。孙策死后，他与张昭等人共同辅佐孙权，功勋卓著。", "zhan_jiang_chengpu.png", "head_chengpu.png", "quan_jiang_caohong.png", "quan_jiang_chengpu.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202520, nil, 202530, "999|0,999|0,999|0", 1000, 2, 9000, 8500, 6900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10077, 3, 1000, 5000, 10000, 500, 500, "201,3601,5703,9302,2305,", nil, "681,771,772,773", nil, },
	id_60078 = {60078, "橙卡丁原", "丁原", 1, "1", "为人有远谋、武勇，善于骑射，作战时身先士卒。因不满董卓欲废少帝，被董卓记恨。后因董卓策反其义子吕布被谋害。", "zhan_jiang_dingyuan.png", "head_dingyuan.png", "quan_jiang_dingyuan.png", "quan_jiang_dingyuan.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202540, nil, 202550, "999|0,999|0,999|0", 900, 2, 8200, 9100, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10078, 4, 1000, 5000, 10000, 500, 500, "201,6001,3303,9302,5805,", nil, "783,781,782,784", nil, },
	id_60079 = {60079, "橙卡皇甫嵩", "皇甫嵩", 1, "1", "为人仁爱谨慎，尽忠职守，有谋略，有胆识。平张角贼居功至伟，乃是汉末救国救民的英雄人物。", "zhan_jiang_huangfusong.png", "head_huangfusong.png", "quan_jiang_huangfusong.png", "quan_jiang_huangfusong.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202560, nil, 202570, "999|0,999|0,999|0", 900, 2, 8500, 9000, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10079, 4, 1000, 5000, 10000, 500, 500, "201,5701,7803,9302,4205,", nil, "791,792,772,793", nil, },
	id_60080 = {60080, "橙卡李儒", "李儒", 1, "1", "董卓的女婿、首席谋士，为董卓所亲信，算无遗策，堪称智囊。董卓趁乱进京、说降吕布、废立皇帝等举动，均离不开其参谋之功。", "zhan_jiang_liru.png", "head_liru.png", "quan_jiang_liru.png", "quan_jiang_liru.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202580, nil, 202590, "999|0,999|0,999|0", 900, 2, 7600, 6900, 9400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10080, 4, 1000, 5000, 10000, 500, 500, "301,3601,4703,9302,1105,", nil, "521,791,801,802,803", nil, },
	id_60081 = {60081, "橙卡纪灵", "纪灵", 1, "1", "袁术手下大将，使三尖两刃刀，勇猛非常，深得袁术信任。征讨徐州时同关羽大战三十合未见胜负。", "zhan_jiang_jiling.png", "head_jiling.png", "quan_jiang_jiling.png", "quan_jiang_jiling.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202600, nil, 202610, "999|0,999|0,999|0", 900, 2, 8400, 8900, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10081, 4, 1000, 5000, 10000, 500, 500, "201,1101,3903,9302,3305,", nil, "682,811,772,812", nil, },
	id_60082 = {60082, "橙卡刘表", "刘表", 1, "1", "汉末群雄之一。身长八尺余，姿貌温厚伟壮，少时知名于世。为荆州牧时，刘表恩威并着，群民悦服。又开经立学，爱民养士。", "zhan_jiang_liubiao.png", "head_liubiao.png", "quan_jiang_liubiao.png", "quan_jiang_liubiao.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202620, nil, 202630, "999|0,999|0,999|0", 900, 2, 7700, 6700, 8100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10082, 4, 1000, 5000, 10000, 500, 500, "201,6101,5703,9302,4405,", nil, "781,811,842,821", nil, },
	id_60083 = {60083, "橙卡袁绍", "袁绍", 1, "1", "出身名门望族，其家族有“四世三公”之称，英俊威武。联合各路诸侯讨伐过残暴不仁的董卓。在战乱纷争的三国时期建立起自己的一方势力。", "zhan_jiang_yuanshao.png", "head_yuanshao.png", "quan_jiang_yuanshao.png", "quan_jiang_yuanshao.png", "nuqi_yuanshao.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202640, nil, 202650, "999|0,999|0,999|0", 1100, 2, 9300, 8000, 9500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10083, 4, 1000, 5000, 10000, 500, 500, "201,1101,5803,9302,1205,10007", nil, "271,451,452,831,832", nil, },
	id_60084 = {60084, "橙卡袁术", "袁术", 1, "1", "袁绍之弟。世家子弟。少年时即以侠气闻名，曾清除把持朝政的十常侍。占据一方成为诸侯之后，骄奢淫逸，暴敛横征，不得民心。", "zhan_jiang_yuanshu.png", "head_yuanshu.png", "quan_jiang_yuanshu.png", "quan_jiang_yuanshu.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202660, nil, 202670, "999|0,999|0,999|0", 900, 2, 8000, 7400, 8900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10084, 4, 1000, 5000, 10000, 500, 500, "201,3401,5903,9302,1205,", nil, "782,841,842,843", nil, },
	id_60085 = {60085, "橙卡张宝", "张宝", 1, "1", "张角起义的首领之一，张角的弟弟。随兄张角起义，号称“地公将军”。于阳城与朱儁军对抗中，使用法术一度取胜。", "zhan_jiang_zhangbao_1.png", "head_zhangbao_1.png", "quan_jiang_zhangbao_1.png", "quan_jiang_zhangbao_1.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202680, nil, 202690, "999|0,999|0,999|0", 900, 2, 7700, 7100, 9100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10085, 4, 1000, 5000, 10000, 500, 500, "301,5701,3403,9302,4705,", nil, "851,852,842,854", nil, },
	id_60086 = {60086, "橙卡曹昂", "曹昂", 1, "1", "曹操长子。聪明且性情谦和，为曹操所喜爱，随曹操出征张绣，曹昂为救曹操负责断後，与大将典韦一同战死於宛城。", "zhan_jiang_caoang.png", "head_caoang.png", "quan_jiang_caoang.png", "quan_jiang_caoang.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202700, nil, 202710, "999|0,999|0,999|0", 900, 2, 8600, 9100, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10086, 1, 1000, 5000, 10000, 500, 500, "200,3601,4803,9302,3905,", nil, "863,861,862,864", nil, },
	id_60087 = {60087, "橙卡曹节", "曹节", 1, "2", "汉献帝刘协的第二任皇后，曹操的女儿。曹丕胁迫刘协退位时，怒骂兄长，后随被封为山阳公的刘协到其封地，恩泽一方百姓。", "zhan_jiang_xinxianying.png", "head_xinxianying.png", "quan_jiang_bulianshi.png", "quan_jiang_xinxianying.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202720, nil, 202730, "999|0,999|0,999|0", 900, 2, 8700, 8200, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10087, 1, 1000, 5000, 10000, 500, 500, "300,1201,5203,9302,3205,", nil, "861,871,1111,872", nil, },
	id_60088 = {60088, "橙卡卞氏", "卞氏", 1, "2", "魏武宣皇后魏武帝曹操的正妻，曹丕之母。性格温婉善良，节俭律己。曹操赞其“怒不改容，喜不失节。”", "zhan_jiang_bianshi.png", "head_bianshi.png", "quan_jiang_wujiang7.png", "quan_jiang_bianshi.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202740, nil, 202750, "999|0,999|0,999|0", 900, 2, 7400, 7500, 8100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10088, 1, 1000, 5000, 10000, 500, 500, "301,3601,3403,9302,3805,", nil, "881,882,972,883", nil, },
	id_60089 = {60089, "橙卡王异", "王异", 1, "2", "东汉末年曹操益州刺史赵昂之妻，马超作乱凉州时，王异协助丈夫守城，与其夫赵昂同商谋略而出九条奇计。", "zhan_jiang_heshi.png", "head_heshi.png", "quan_jiang_wujiang7.png", "quan_jiang_heshi.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202760, nil, 202770, "999|0,999|0,999|0", 1000, 2, 7900, 6900, 8400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10089, 1, 1000, 5000, 10000, 500, 500, "301,1001,7703,9302,3605,", nil, "661,891,972,892", nil, },
	id_60090 = {60090, "橙卡郭氏", "郭氏", 1, "2", "三国曹魏的第一位皇后。其人甚有智谋，通舞艺，美姿貌。后嫁给曹丕。曹丕能够成为太子，谋略多为郭氏所献。", "zhan_jiang_guoshi.png", "head_guoshi.png", "quan_jiang_guoshi.png", "quan_jiang_guoshi.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202780, nil, 202790, "999|0,999|0,999|0", 900, 2, 7300, 7400, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10090, 1, 1000, 5000, 10000, 500, 500, "301,7701,4003,9302,1105,", nil, "652,871,901,902", nil, },
	id_60091 = {60091, "橙卡邹氏", "邹氏", 1, "2", "三国时期著名美女之一，张济之妻，张绣之嫂。因被曹操强行侵占，张绣愤而施计袭曹。", "zhan_jiang_zoushi.png", "head_zoushi.png", "quan_jiang_zoushi.png", "quan_jiang_zoushi.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202800, nil, 202810, "999|0,999|0,999|0", 700, 2, 7100, 6200, 7400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10091, 1, 1000, 5000, 10000, 500, 500, "301,4501,5703,9302,4905,", nil, "862,901,1111,911", nil, },
	id_60092 = {60092, "橙卡辛宪英", "辛宪英", 1, "2", "辛宪英聪朗有才鉴，足不出户但对政治有极强的洞察力，父兄极其子遇到大事都会找她商榷，她每言必中。", "zhan_jiang_caojie.png", "head_caojie.png", "quan_jiang_xinxianying.png", "quan_jiang_caojie.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202820, nil, 202830, "999|0,999|0,999|0", 700, 2, 6800, 5800, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10092, 1, 1000, 5000, 10000, 500, 500, "301,5701,4703,9302,,", nil, "10011,10012", nil, },
	id_60093 = {60093, "橙卡孔秀", "孔秀", 1, "1", "曹操手下东岭关守将，东岭关为关羽携嫂过五关之第一关，孔秀因阻挡关羽过关被斩。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202840, nil, 202850, "999|0,999|0,999|0", 700, 2, 7400, 7700, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10093, 1, 1000, 5000, 10000, 500, 500, "200,3301,1203,9302,,", nil, "10021,10022", nil, },
	id_60094 = {60094, "橙卡韩福", "韩福", 1, "1", "曹操部下任洛阳太守。关羽千里走单骑途中在洛阳设计偷袭，最后被关羽所斩杀。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202860, nil, 202870, "999|0,999|0,999|0", 600, 2, 7500, 7000, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10094, 1, 1000, 5000, 10000, 500, 500, "200,7801,5803,9302,,", nil, "10031,10032", nil, },
	id_60095 = {60095, "橙卡卞喜", "卞喜", 1, "1", "原是张角余党，后投曹操。善使流星锤。在镇国寺设下伏兵欲谋害千里寻兄的关羽，最后被关羽斩杀。", "zhan_jiang_wenguan2.png", "head_wenguan2.png", "quan_jiang_wenguan2.png", "quan_jiang_wenguan2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202880, nil, 202890, "999|0,999|0,999|0", 600, 2, 6600, 6000, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10095, 1, 1000, 5000, 10000, 500, 500, "200,3601,3403,9302,,", nil, "10041,10042", nil, },
	id_60096 = {60096, "橙卡王植", "王植", 1, "1", "曹操部下，他得知关羽杀了韩福，想谋害关羽，最后被关羽斩杀。", "zhan_jiang_wenguan5.png", "head_wenguan5.png", "quan_jiang_wenguan5.png", "quan_jiang_wenguan5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202900, nil, 202910, "999|0,999|0,999|0", 600, 2, 6700, 6100, 7300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10096, 1, 1000, 5000, 10000, 500, 500, "200,5101,3303,9302,,", nil, "10051,10052", nil, },
	id_60097 = {60097, "橙卡夏侯氏", "夏侯氏", 1, "2", "夏侯渊侄女。豆蔻之时，外出采桑，为张飞所得，被张飞求娶为正妻。所生二女皆为蜀汉后主刘禅皇后。", "zhan_jiang_xiahoushi.png", "head_xiahoushi.png", "quan_jiang_wujiang10.png", "quan_jiang_xiahoushi.png", "nuqi_xiahoushi.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202920, nil, 202930, "999|0,999|0,999|0", 1100, 2, 7900, 8000, 8800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10097, 2, 1000, 5000, 10000, 500, 500, "201,7801,7703,9302,5305,1107", nil, "492,971,972,973,974", nil, },
	id_60098 = {60098, "橙卡糜夫人", "糜夫人", 1, "2", "刘备的夫人之一，家中银钱甚多，被其兄嫁与刘备且随嫁巨额金银以充军资。", "zhan_jiang_mifuren.png", "head_mifuren.png", "quan_jiang_mifuren.png", "quan_jiang_mifuren.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202940, nil, 202950, "999|0,999|0,999|0", 1000, 2, 7400, 7700, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10098, 2, 1000, 5000, 10000, 500, 500, "301,7701,3703,9302,4705,", nil, "851,881,1003,981", nil, },
	id_60099 = {60099, "橙卡蔡氏", "蔡氏", 1, "2", "荆州牧刘表的后妻。喜爱刘表的次子，刘表死后尊刘琮为继承人，劝刘琮携荆州归降曹操，最后却为曹操所杀。", "zhan_jiang_caifuren.png", "head_caifuren.png", "quan_jiang_caifuren.png", "quan_jiang_caifuren.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202960, nil, 202970, "999|0,999|0,999|0", 900, 2, 7400, 7400, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10099, 2, 1000, 5000, 10000, 500, 500, "301,7701,1103,9302,4005,", nil, "891,991,1003,992", nil, },
	id_60100 = {60100, "橙卡大张皇后", "大张皇后", 1, "2", "蜀汉名将张飞之女，嫁与刘备之子刘禅为妻，后被封为皇后。", "zhan_jiang_dazhanghuanghou.png", "head_dazhanghuanghou.png", "quan_jiang_dazhanghuanghou.png", "quan_jiang_dazhanghuanghou.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 202980, nil, 202990, "999|0,999|0,999|0", 900, 2, 7600, 7400, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10100, 2, 1000, 5000, 10000, 500, 500, "301,4301,7703,9302,4905,", nil, "1001,1002,1003,1004", nil, },
	id_60101 = {60101, "橙卡甘夫人", "甘夫人", 1, "2", "三国时代著名的美女之一。刘备的皇后。蜀汉后主刘禅之母。肤白貌美，被刘备的幕僚赞为“神智夫人”。", "zhan_jiang_ganfuren.png", "head_ganfuren.png", "quan_jiang_wujiang9.png", "quan_jiang_ganfuren.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203000, nil, 203010, "999|0,999|0,999|0", 900, 2, 7700, 6800, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10101, 2, 1000, 5000, 10000, 500, 500, "301,7701,9503,9302,3605,", nil, "541,882,731,1011", nil, },
	id_60102 = {60102, "橙卡吴国太", "吴国太", 1, "2", "孙坚的次妻，孙朗、孙尚香的母亲。与乔国老促成了刘备与孙尚香的姻缘。", "zhan_jiang_wuguotai.png", "head_wuguotai.png", "quan_jiang_wujiang9.png", "quan_jiang_wuguotai.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203020, nil, 203030, "999|0,999|0,999|0", 900, 2, 7700, 6700, 8100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10102, 3, 1000, 5000, 10000, 500, 500, "301,9501,3603,9302,5105,", nil, "741,1021,1022,1023", nil, },
	id_60103 = {60103, "橙卡孙鲁育", "孙鲁育", 1, "2", "孙权之女，孙鲁班之妹，朱据之妻。为人谦和，心思单纯。被其姐算计报复，遭到孙峻的冤杀。", "zhan_jiang_sunluyu.png", "head_sunluyu.png", "quan_jiang_wujiang7.png", "quan_jiang_sunluyu.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203040, nil, 203050, "999|0,999|0,999|0", 900, 2, 8500, 8400, 7000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10103, 3, 1000, 5000, 10000, 500, 500, "301,7701,4503,9302,,", nil, "10061,10062", nil, },
	id_60104 = {60104, "橙卡孙鲁班", "孙鲁班", 1, "2", "孙权长女，浓眉大眼，心机深沉，狡诈狠毒。曾施计离间孙权和孙和，害死胞妹孙鲁育。", "zhan_jiang_sunluban.png", "head_sunluban.png", "quan_jiang_wujiang8.png", "quan_jiang_sunluban.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203060, nil, 203070, "999|0,999|0,999|0", 900, 2, 8700, 8200, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10104, 3, 1000, 5000, 10000, 500, 500, "301,7701,1103,9302,,", nil, "10071,10072", nil, },
	id_60105 = {60105, "橙卡祖茂", "祖茂", 1, "1", "孙坚心腹将领。善使双刀。孙坚被董卓追杀时，将自己的头巾与孙坚更换，引开追兵，护主逃脱。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203080, nil, 203090, "999|0,999|0,999|0", 600, 2, 7400, 6800, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10105, 3, 1000, 5000, 10000, 500, 500, "200,3801,1103,9302,,", nil, "10081,10082", nil, },
	id_60106 = {60106, "橙卡淳于琼", "淳于琼", 1, "1", "汉右军校尉，后为袁绍部将。官渡之战，守乌巢之时整日酗酒无备，以致袁绍部队因曹操偷袭成功而惨败。", "zhan_jiang_chunyuqiong.png", "head_chunyuqiong.png", "quan_jiang_chunyuqiong.png", "quan_jiang_chunyuqiong.png", "nuqi_chunyuqiong.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203100, nil, 203110, "999|0,999|0,999|0", 900, 2, 8400, 8900, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10106, 4, 1000, 5000, 10000, 500, 500, "200,9401,5603,9302,1205,", nil, "792,1061,1062,1063", nil, },
	id_60107 = {60107, "橙卡袁尚", "袁尚", 1, "1", "汉末群雄之一袁绍的第三子，受到袁绍的偏爱，勇猛善战。于袁绍去世后继承了袁绍的官位和爵位。", "zhan_jiang_yuanshang.png", "head_yuanshang.png", "quan_jiang_wenpin.png", "quan_jiang_yuanshang.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203120, nil, 203130, "999|0,999|0,999|0", 700, 2, 7100, 6500, 7700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10107, 4, 1000, 5000, 10000, 500, 500, "200,5601,1203,9302,,", nil, "10091,10092", nil, },
	id_60108 = {60108, "橙卡李肃", "李肃", 1, "1", "骑都尉，吕布同郡之人，协同吕布刺杀董卓，随后受派遣，前往陕地征讨牛辅，被牛辅击败，退至弘农，为吕布所杀。", "zhan_jiang_lisu.png", "head_lisu.png", "quan_jiang_lisu.png", "quan_jiang_lisu.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203140, nil, 203150, "999|0,999|0,999|0", 600, 2, 6700, 6100, 7300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10108, 4, 1000, 5000, 10000, 500, 500, "400,1101,3203,9302,,", nil, "10101,10102", nil, },
	id_60109 = {60109, "橙卡卢植", "卢植", 1, "1", "身长八尺二寸，声如洪钟。性格刚毅，有高尚品德，常有匡扶社稷，救济世人之志，能饮酒一石。曾为刘备之师。", "zhan_jiang_luzhi.png", "head_luzhi.png", "quan_jiang_luzhi.png", "quan_jiang_luzhi.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203160, nil, 203170, "999|0,999|0,999|0", 900, 2, 7800, 7200, 8600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10109, 4, 1000, 5000, 10000, 500, 500, "300,1201,3603,9302,5105,", nil, "1061,1091,1092,1093", nil, },
	id_60110 = {60110, "橙卡张绣", "张绣", 1, "1", "汉末军阀之一。人称“北地枪王”，使一杆虎头金枪。他的“百鸟朝凰枪”威震天下，与赵云大战三百回合，被赵云的七探蛇盘枪杀死。", "zhan_jiang_zhangxiu.png", "head_zhangxiu.png", "quan_jiang_zhangxiu.png", "quan_jiang_zhangxiu.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203180, nil, 203190, "999|0,999|0,999|0", 900, 2, 8500, 9000, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10110, 4, 1000, 5000, 10000, 500, 500, "100,3601,7803,9302,1105,", nil, "971,1002,691,1101", nil, },
	id_60111 = {60111, "橙卡何氏", "何氏", 1, "2", "天生丽质，性情强忌。本是屠户出身，因被选入宫而发迹，生皇子刘辩，后立为皇后。她曾毒杀刘协的生母王美人。", "zhan_jiang_wujiang7.png", "head_wujiang7.png", "quan_jiang_heshi.png", "quan_jiang_wujiang7.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203200, nil, 203210, "999|0,999|0,999|0", 900, 2, 7300, 7400, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10111, 4, 1000, 5000, 10000, 500, 500, "301,9501,4703,9302,4005,", nil, "1021,1062,1111,1112", nil, },
	id_60112 = {60112, "橙卡王允", "王允", 1, "1", "汉献帝初年任司徒、尚书令，录尚书事、总朝政。出身于官僚家庭，自幼聪颖过人，为人秉直，成功策划了对董卓的刺杀。", "zhan_jiang_wangyun.png", "head_wangyun.png", "quan_jiang_wangyun.png", "quan_jiang_wangyun.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203220, nil, 203230, "999|0,999|0,999|0", 900, 2, 7900, 7300, 8700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10112, 4, 1000, 5000, 10000, 500, 500, "301,3601,5703,9302,5105,", nil, "991,1022,1091,1121", nil, },
	id_60113 = {60113, "橙卡樊氏", "樊氏", 1, "2", "东汉桂阳太守赵范之嫂。有国色天香之貌。赵范归降，要把其的寡嫂樊氏许嫁赵云，赵云婉拒，成就了赵云的一段美名。", "zhan_jiang_wenguan6.png", "head_wenguan6.png", "quan_jiang_wenguan6.png", "quan_jiang_wenguan6.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203240, nil, 203250, "999|0,999|0,999|0", 1000, 2, 7500, 7600, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10113, 4, 1000, 5000, 10000, 500, 500, "301,3601,9503,9302,7705,", nil, "662,1001,653,1131", nil, },
	id_60114 = {60114, "橙卡严政", "严政", 1, "1", "张角军将领，张宝部将。他随张宝在阳城与汉军作战时，因情势危急而刺杀张宝，同张宝首级投降汉军。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_wujiang5.png", "quan_jiang_wujiang5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203260, nil, 203270, "999|0,999|0,999|0", 700, 2, 8000, 7500, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10114, 4, 1000, 5000, 10000, 500, 500, "200,3201,3603,9302,,", nil, "10131,10132", nil, },
	id_60115 = {60115, "橙卡裴元绍", "裴元绍", 1, "1", "张角军之武将。张角起义失败之后，与周仓一同率领残部在山中落草当山贼。因其欲夺偶然路过的赵云之马，反遭讨伐战败身死。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203280, nil, 203290, "999|0,999|0,999|0", 800, 2, 7800, 8100, 5900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10115, 4, 1000, 5000, 10000, 500, 500, "200,1201,4203,9302,,", nil, "10141,10142", nil, },
	id_60116 = {60116, "橙卡高升", "高升", 1, "1", "张角军将领，跟随地公将军张宝与汉将朱儁交战，在受到朱儁和刘备的攻击时，作为张宝的先锋迎击。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203300, nil, 203310, "999|0,999|0,999|0", 700, 2, 7900, 7700, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10116, 4, 1000, 5000, 10000, 500, 500, "200,3601,403,9302,,", nil, "10151,10152", nil, },
	id_60117 = {60117, "橙卡张曼成", "张曼成", 1, "1", "南阳张角军首领。自称“神上使”，聚众数万，斩杀了南阳郡守褚贡。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203320, nil, 203330, "999|0,999|0,999|0", 800, 2, 7900, 8200, 6100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10117, 4, 1000, 5000, 10000, 500, 500, "200,1201,4403,9302,,", nil, "10161,10162", nil, },
	id_60118 = {60118, "橙卡张燕", "张燕", 1, "1", "东汉末黑山军首领。身手矫捷，剽悍过人，有“飞燕”的绰号。官渡之战时投降曹操，被封为平北将军、安国亭侯。", "zhan_jiang_wujiang4.png", "head_wujiang4.png", "quan_jiang_wujiang4.png", "quan_jiang_wujiang4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203340, nil, 203350, "999|0,999|0,999|0", 900, 2, 8600, 8500, 7000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10118, 4, 1000, 5000, 10000, 500, 500, "200,5601,1103,9302,3505,", nil, "841,852,1092,1181", nil, },
	id_60119 = {60119, "橙卡波才", "波才", 1, "1", "张角军中级将领。灵帝派人镇压，波才大破朱儁、皇甫嵩之军。", "zhan_jiang_wenguan4.png", "head_wenguan4.png", "quan_jiang_wenguan4.png", "quan_jiang_wenguan4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203360, nil, 203370, "999|0,999|0,999|0", 800, 2, 7500, 7000, 8300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10119, 4, 1000, 5000, 10000, 500, 500, "200,1101,5103,9302,,", nil, "10171,10172", nil, },
	id_60120 = {60120, "橙卡胡轸", "胡轸", 1, "1", "董卓的五大中郎将之一，后属王允、李傕部下，官拜东郡太守。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203380, nil, 203390, "999|0,999|0,999|0", 700, 2, 7200, 6600, 7800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10120, 4, 1000, 5000, 10000, 500, 500, "200,5601,1203,9302,,", nil, "10181,10182", nil, },
	id_60121 = {60121, "橙卡张济", "张济", 1, "1", "原为董卓部将。董卓死后，伙同原董卓部将攻向长安，杀死王允等人，占领长安。官至骠骑将军、平阳侯。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203400, nil, 203410, "999|0,999|0,999|0", 700, 2, 7800, 7600, 6400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10121, 4, 1000, 5000, 10000, 500, 500, "200,3801,1103,9302,,", nil, "10191,10192", nil, },
	id_60122 = {60122, "橙卡徐荣", "徐荣", 1, "1", "董卓的中郎将，作为一个优秀统帅，于汴水之战中击败曹操，以及在梁东之战中击败孙坚。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203420, nil, 203430, "999|0,999|0,999|0", 700, 2, 7900, 7400, 5900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10122, 4, 1000, 5000, 10000, 500, 500, "200,1101,3203,9302,,", nil, "10201,10202", nil, },
	id_60123 = {60123, "橙卡董旻", "董旻", 1, "1", "董卓之弟，随董卓趁乱进京，后董卓迁都长安，任命董旻为左将军，封鄠侯，统领部队，为其爪牙。", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_wenguan1.png", "quan_jiang_wenguan1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203440, nil, 203450, "999|0,999|0,999|0", 600, 2, 6800, 6200, 7400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10123, 4, 1000, 5000, 10000, 500, 500, "200,5601,403,9302,,", nil, "10211,10212", nil, },
	id_60124 = {60124, "橙卡董承", "董承", 1, "1", "汉献帝的岳父。曾任东汉车骑将军。董卓乱京时，董承送献帝回洛阳。曹操专权后，董承密谋除掉曹操，将献帝的密诏带给刘备。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_wujiang5.png", "quan_jiang_wujiang5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203460, nil, 203470, "999|0,999|0,999|0", 700, 2, 7500, 7300, 6100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10124, 2, 1000, 5000, 10000, 500, 500, "200,4801,3603,9302,,", nil, "10221,10222", nil, },
	id_60125 = {60125, "橙卡李榷", "李榷", 1, "1", "原为董卓的部将。董卓被杀后伙同李傕、张济、樊稠等人，占领长安，杀死王允等人，把持朝廷大权。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203480, nil, 203490, "999|0,999|0,999|0", 700, 2, 7600, 7800, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10125, 4, 1000, 5000, 10000, 500, 500, "200,3301,7803,9302,,", nil, "10231,10232", nil, },
	id_60126 = {60126, "橙卡郭汜", "郭汜", 1, "1", "原为董卓的部将。董卓被杀后，采贾诩之谋，与原董卓部将，杀死王允等人，占领长安，把持朝廷大权。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203500, nil, 203510, "999|0,999|0,999|0", 700, 2, 7400, 7800, 5900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10126, 4, 1000, 5000, 10000, 500, 500, "200,7801,5803,9302,,", nil, "10241,10242", nil, },
	id_60127 = {60127, "橙卡樊稠", "樊稠", 1, "1", "东汉末年军阀。官至右将军、万年侯。原为董卓部将，与吕布镇守虎牢关。董卓被杀后攻往长安，把持朝政。", "zhan_jiang_wujiang4.png", "head_wujiang4.png", "quan_jiang_wujiang4.png", "quan_jiang_wujiang4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203520, nil, 203530, "999|0,999|0,999|0", 600, 2, 7500, 7300, 6000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10127, 4, 1000, 5000, 10000, 500, 500, "200,1101,4503,9302,,", nil, "10251,10252", nil, },
	id_60128 = {60128, "橙卡曹豹", "曹豹", 1, "1", "先为徐州牧陶谦的部将。后归顺刘备。其女嫁给了吕布。因与张飞结怨后叛刘归顺于吕布。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203540, nil, 203550, "999|0,999|0,999|0", 600, 2, 7500, 7000, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10128, 4, 1000, 5000, 10000, 500, 500, "200,1201,3603,9302,,", nil, "10261,10262", nil, },
	id_60129 = {60129, "橙卡成廉", "成廉", 1, "1", "吕布八健将之一，被张辽统领。在濮阳打败曹操，却被乐进一箭射死。", "zhan_jiang_wenguan4.png", "head_wenguan4.png", "quan_jiang_wenguan4.png", "quan_jiang_wenguan4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203560, nil, 203570, "999|0,999|0,999|0", 600, 2, 6500, 5900, 7600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10129, 4, 1000, 5000, 10000, 500, 500, "200,1201,4203,9302,,", nil, "10271,10272", nil, },
	id_60130 = {60130, "橙卡张邈", "张邈", 1, "1", "汉末群雄之一，家中富裕，疏财仗义，广交朋友，曾参与讨伐董卓。本来与曹操是好友，经陈宫劝说后，背叛曹操。", "zhan_jiang_wenguan2.png", "head_wenguan2.png", "quan_jiang_wenguan2.png", "quan_jiang_wenguan2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203580, nil, 203590, "999|0,999|0,999|0", 600, 2, 6700, 6100, 7300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10130, 4, 1000, 5000, 10000, 500, 500, "200,3601,5303,9302,,", nil, "10281,10282", nil, },
	id_60131 = {60131, "橙卡牛辅", "牛辅", 1, "1", "董卓的女婿，牛辅被吕布打败，于是带着金银珠宝出逃，部署胡赤儿等人谋财害命，于途中将其斩首送往长安。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_wujiang5.png", "quan_jiang_wujiang5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203600, nil, 203610, "999|0,999|0,999|0", 600, 2, 7200, 7600, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10131, 4, 1000, 5000, 10000, 500, 500, "200,7801,3703,9302,,", nil, "10291,10292", nil, },
	id_60132 = {60132, "橙卡阎象", "阎象", 1, "1", "袁术的主簿。袁术要称帝时，他引用周文王虽拥有三分之二的天下还向殷称臣的故事进行劝谏。却未被采纳。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_wujiang5.png", "quan_jiang_wujiang5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203620, nil, 203630, "999|0,999|0,999|0", 600, 2, 7200, 7400, 5200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10132, 4, 1000, 5000, 10000, 500, 500, "200,1101,7803,9302,,", nil, "10301,10302", nil, },
	id_60133 = {60133, "橙卡乐就", "乐就", 1, "1", "袁术麾下之将。袁术攻打曹操之陈时，担任联络的任务。袁术军不幸战败，他也在寿春被曹操军逮捕并遭到斩首。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203640, nil, 203650, "999|0,999|0,999|0", 600, 2, 7500, 7000, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10133, 4, 1000, 5000, 10000, 500, 500, "200,3601,3903,9302,,", nil, "10311,10312", nil, },
	id_60134 = {60134, "橙卡李丰", "李丰", 1, "1", "袁术部将，随袁术进攻曹操之陈时，遭到曹操亲自抵御。与梁刚、乐就、陈纪一同防守寿春。后被曹操攻破，四人皆被杀。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_wenguan3.png", "quan_jiang_wenguan3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203660, nil, 203670, "999|0,999|0,999|0", 600, 2, 6500, 5900, 7000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10134, 4, 1000, 5000, 10000, 500, 500, "200,3401,5703,9302,,", nil, "10321,10322", nil, },
	id_60135 = {60135, "橙卡张勋", "张勋", 1, "1", "袁术麾下的将领。袁术称帝，张勋被封为大将军。受袁术所命攻打吕布，因己方中离间之计而败退。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203680, nil, 203690, "999|0,999|0,999|0", 600, 2, 7300, 6700, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10135, 4, 1000, 5000, 10000, 500, 500, "200,5301,3803,9302,,", nil, "10331,10332", nil, },
	id_60136 = {60136, "橙卡陈纪", "陈纪", 1, "1", "袁术部将，曾任九江太守。后在曹袁寿春之战中战败，被曹操斩首。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203700, nil, 203710, "999|0,999|0,999|0", 600, 2, 6600, 6000, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10136, 4, 1000, 5000, 10000, 500, 500, "200,1201,5603,9302,,", nil, "10341,10342", nil, },
	id_60137 = {60137, "橙卡陈兰", "陈兰", 1, "1", "原为袁术部下。袁术称帝后，以陈兰为副将，作为讨伐吕布的七军之一。后来陈兰因袁术奢侈太过而与雷薄逃入嵩山。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_wenguan3.png", "quan_jiang_wenguan3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203720, nil, 203730, "999|0,999|0,999|0", 500, 2, 6200, 5600, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10137, 4, 1000, 5000, 10000, 500, 500, "200,4701,1103,9302,,", nil, "10351,10352", nil, },
	id_60138 = {60138, "橙卡胡车儿", "胡车儿", 1, "1", "张绣的偏将，据说其“力能负五百斤，日行七百里”。张绣与曹操作战时，胡车儿把典韦灌醉并盗取了典韦的双戟，致使典韦死于宛城之战。", "zhan_jiang_wujiang4.png", "head_wujiang4.png", "quan_jiang_wujiang4.png", "quan_jiang_wujiang4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203740, nil, 203750, "999|0,999|0,999|0", 600, 2, 7000, 7300, 5300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10138, 2, 1000, 5000, 10000, 500, 500, "200,1101,7803,9302,,", nil, "10361,10362", nil, },
	id_60139 = {60139, "橙卡吕翔", "吕翔", 1, "1", "袁绍部下武将。袁绍死后追随袁尚，后投降曹操，封列侯。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203760, nil, 203770, "999|0,999|0,999|0", 500, 2, 7200, 6600, 5400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10139, 1, 1000, 5000, 10000, 500, 500, "200,5601,3603,9302,,", nil, "10371,10372", nil, },
	id_60140 = {60140, "橙卡吕旷", "吕旷", 1, "1", "与吕翔同是袁绍属下，袁绍去世后，为袁尚守东平，后来投降曹操，并被封为列侯。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203780, nil, 203790, "999|0,999|0,999|0", 500, 2, 6900, 7100, 5200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10140, 1, 1000, 5000, 10000, 500, 500, "200,3601,4803,9302,,", nil, "10381,10382", nil, },
	id_60141 = {60141, "橙卡郭图", "郭图", 1, "1", "袁绍帐下谋士。官渡之战时力主趁机偷袭曹营，在此计失败后为免于责罚而归罪于率军偷袭曹营的张郃、高览，致使二人背袁投曹。", "zhan_jiang_wenguan5.png", "head_wenguan5.png", "quan_jiang_wenguan5.png", "quan_jiang_wenguan5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203800, nil, 203810, "999|0,999|0,999|0", 500, 2, 7200, 6600, 5300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10141, 4, 1000, 5000, 10000, 500, 500, "200,7801,1103,9302,,", nil, "10391,10392", nil, },
	id_60142 = {60142, "橙卡高览", "高览", 1, "1", "本为袁绍部将，后在官渡之战因遭郭图谗言，与张郃一同投降曹操，官至偏将军、东莱侯。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203820, nil, 203830, "999|0,999|0,999|0", 500, 2, 7000, 6700, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10142, 1, 1000, 5000, 10000, 500, 500, "200,1101,3303,9302,,", nil, "10401,10402", nil, },
	id_60143 = {60143, "橙卡陈琳", "陈琳", 1, "1", "建安七子之一，著名文学家，诗、文、赋俱佳。有高明的政治见解。", "zhan_jiang_wenguan2.png", "head_wenguan2.png", "quan_jiang_wenguan2.png", "quan_jiang_wenguan2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203840, nil, 203850, "999|0,999|0,999|0", 700, 2, 6500, 6500, 7000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10143, 2, 1000, 5000, 10000, 500, 500, "300,4201,3403,9302,,", nil, "10411,10412", nil, },
	id_60144 = {60144, "橙卡苏由", "苏由", 1, "1", "袁尚部下。袁尚攻打平原，曹操趁机攻邺。苏由为邺城守将，想叛袁归曹，被审配发现后兵败出逃，投奔曹操。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203860, nil, 203870, "999|0,999|0,999|0", 600, 2, 7500, 7000, 5600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10144, 1, 1000, 5000, 10000, 500, 500, "200,7801,1103,9302,,", nil, "10421,10422", nil, },
	id_60145 = {60145, "橙卡袁谭", "袁谭", 1, "1", "袁绍长子，青州刺史。与其弟袁尚因争夺继承人之位而决裂。被曹操族弟曹纯所率虎豹骑所杀。", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_wenguan1.png", "quan_jiang_wenguan1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203880, nil, 203890, "999|0,999|0,999|0", 600, 2, 6600, 6000, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10145, 4, 1000, 5000, 10000, 500, 500, "200,101,1103,9302,,", nil, "10431,10432", nil, },
	id_60146 = {60146, "橙卡吕威璜", "吕威璜", 1, "1", "袁绍下蜀，任骑督。官渡之战中，随淳于琼运送粮草至乌巢遇袭，粮草尽焚。", "zhan_jiang_wujiang4.png", "head_wujiang4.png", "quan_jiang_wujiang4.png", "quan_jiang_wujiang4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203900, nil, 203910, "999|0,999|0,999|0", 600, 2, 7300, 6700, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10146, 4, 1000, 5000, 10000, 500, 500, "200,1201,4303,9302,,", nil, "10441,10442", nil, },
	id_60147 = {60147, "橙卡韩当", "韩当", 1, "1", "韩当因为长于弓箭、骑术并且膂力过人而被孙坚赏识，追随他四处征伐周旋，数次冒险犯难，攻陷敌人、擒拿俘虏。对江东基业的逐渐稳固和吴国的建立有着重要影响。", "zhan_jiang_wujiang4.png", "head_wujiang4.png", "quan_jiang_wujiang4.png", "quan_jiang_wujiang4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203920, nil, 203930, "999|0,999|0,999|0", 600, 2, 7300, 6700, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10147, 3, 1000, 5000, 10000, 500, 500, "200,5601,403,9302,,", nil, "10451,10452", nil, },
	id_60148 = {60148, "橙卡孟坦", "孟坦", 1, "1", "孟坦，，曹操手下洛阳太守韩福的牙将，曾出计谋害关羽，被关羽一刀剁成两段。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203940, nil, 203950, "999|0,999|0,999|0", 500, 2, 7200, 6600, 5300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10148, 1, 1000, 5000, 10000, 500, 500, "200,3301,5703,9302,,", nil, "10461,10462", nil, },
	id_60149 = {60149, "橙卡秦琪", "秦琪", 1, "1", "曹操麾下将领，属夏侯惇部、蔡阳外甥，据守黄河渡口关隘。", "zhan_jiang_wujiang4.png", "head_wujiang4.png", "quan_jiang_wujiang4.png", "quan_jiang_wujiang4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203960, nil, 203970, "999|0,999|0,999|0", 600, 2, 7300, 6700, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10149, 1, 1000, 5000, 10000, 500, 500, "200,3901,3303,9302,,", nil, "10471,10472", nil, },
	id_60150 = {60150, "橙卡韩浩", "韩浩", 1, "1", "曹操部下的武将。官至中护军。韩浩与史涣因忠勇而闻名，都是曹操的心腹将领，被委以执掌禁军的重责。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 203980, nil, 203990, "999|0,999|0,999|0", 500, 2, 6900, 7100, 5200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10150, 1, 1000, 5000, 10000, 500, 500, "200,5601,4403,9302,,", nil, "10481,10482", nil, },
	id_60151 = {60151, "橙卡张景", "张景", 1, "1", "魏国文官，却因奸臣当道屡屡不得志，只得在小地方做一名算命先生。", "zhan_jiang_wenguan5.png", "head_wenguan5.png", "quan_jiang_wenguan5.png", "quan_jiang_wenguan5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204000, nil, 204010, "999|0,999|0,999|0", 500, 2, 6100, 5500, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10151, 1, 1000, 5000, 10000, 500, 500, "200,1101,5103,9302,,", nil, "10491,10492", nil, },
	id_60152 = {60152, "橙卡陈宁", "陈宁", 1, "1", "魏国文官，曾向董卓买官而获得一席官位。怎料当官不久却逢乱世，每日发愁如何转移家产。", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_wenguan1.png", "quan_jiang_wenguan1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204020, nil, 204030, "999|0,999|0,999|0", 500, 2, 6200, 5600, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10152, 1, 1000, 5000, 10000, 500, 500, "200,3801,1203,9302,,", nil, "10501,10502", nil, },
	id_60153 = {60153, "橙卡李丹", "李丹", 1, "1", "魏国女将，从小喜爱射箭，最大的愿望就是能找到一名神射手来当自己的夫君。", "zhan_jiang_wujiang9.png", "head_wujiang9.png", "quan_jiang_wujiang9.png", "quan_jiang_wujiang9.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204040, nil, 204050, "999|0,999|0,999|0", 400, 2, 6500, 6600, 4800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10153, 1, 1000, 5000, 10000, 500, 500, "200,3601,1103,9302,,", nil, "10511,10512", nil, },
	id_60154 = {60154, "橙卡王文", "王文", 1, "1", "蜀国文官，原为一方势力，受到刘备号召后决定帮助刘备成就大业。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_wenguan3.png", "quan_jiang_wenguan3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204060, nil, 204070, "999|0,999|0,999|0", 500, 2, 6000, 5400, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10154, 2, 1000, 5000, 10000, 500, 500, "300,401,3303,9302,,", nil, "10521,10522", nil, },
	id_60155 = {60155, "橙卡黄鸣", "黄鸣", 1, "1", "蜀国文官，看出天下即将大乱，唯有投向明主才可有所作为。阅人无数，心思缜密。", "zhan_jiang_wenguan4.png", "head_wenguan4.png", "quan_jiang_wenguan4.png", "quan_jiang_wenguan4.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204080, nil, 204090, "999|0,999|0,999|0", 400, 2, 6100, 5500, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10155, 2, 1000, 5000, 10000, 500, 500, "300,301,3403,9302,,", nil, "10531,10532", nil, },
	id_60156 = {60156, "橙卡杨勇", "杨勇", 1, "1", "蜀国文官，年轻时饱读兵书，怎奈无处施展才华，年老时得遇明主，聊以慰籍。", "zhan_jiang_wenguan5.png", "head_wenguan5.png", "quan_jiang_wenguan5.png", "quan_jiang_wenguan5.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204100, nil, 204110, "999|0,999|0,999|0", 500, 2, 6100, 5500, 6500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10156, 2, 1000, 5000, 10000, 500, 500, "300,5701,3603,9302,,", nil, "10541,10542", nil, },
	id_60157 = {60157, "橙卡刘光", "刘光", 1, "1", "吴国武将，江东养马世家，孙策起兵时向孙策无偿献马千匹，因此被孙策带在身边。", "zhan_jiang_wujiang1.png", "head_wujiang1.png", "quan_jiang_wujiang1.png", "quan_jiang_wujiang1.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204120, nil, 204130, "999|0,999|0,999|0", 500, 2, 6800, 6500, 5500, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10157, 3, 1000, 5000, 10000, 500, 500, "300,5601,7803,9302,,", nil, "10551,10552", nil, },
	id_60158 = {60158, "橙卡赵华", "赵华", 1, "1", "吴国女将，从小便食量惊人，善使两柄巨锤，最讨厌欺善怕恶之徒。", "zhan_jiang_wujiang11.png", "head_wujiang11.png", "quan_jiang_wujiang11.png", "quan_jiang_wujiang11.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204140, nil, 204150, "999|0,999|0,999|0", 500, 2, 6600, 6700, 5000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10158, 3, 1000, 5000, 10000, 500, 500, "200,7801,5803,9302,,", nil, "10561,10562", nil, },
	id_60159 = {60159, "橙卡郑成", "郑成", 1, "1", "吴国武将，台湾人士，从小修习弓术，只为能谋得功名，好为家乡尽一份力。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_wujiang2.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204160, nil, 204170, "999|0,999|0,999|0", 500, 2, 6800, 6200, 5100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10159, 3, 1000, 5000, 10000, 500, 500, "300,5701,4503,9302,,", nil, "10571,10572", nil, },
	id_60160 = {60160, "橙卡吴权", "吴权", 1, "1", "吴国武将，善于练兵治军，在他手下的士兵勇猛善战，临危不乱，皆可以一挡十。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_wujiang3.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204180, nil, 204190, "999|0,999|0,999|0", 400, 2, 6600, 6300, 5300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10160, 3, 1000, 5000, 10000, 500, 500, "300,1101,6103,9302,,", nil, "10581,10582", nil, },
	id_60161 = {60161, "橙卡孙休", "孙休", 1, "1", "吴太宗景皇帝孙休，字子烈，中国三国时期吴国的第三位皇帝，大帝孙权的第六子，公元258－264年在位。十八岁时，受封为琅琊王。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_ceshi.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204200, nil, 204210, "999|0,999|0,999|0", 900, 2, 8700, 8200, 6600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10161, 3, 1000, 5000, 10000, 500, 500, "200,3801,5903,9302,,", nil, nil, nil, },
	id_60162 = {60162, "橙卡凌操", "凌操", 1, "1", "凌操，东汉末年将领，吴郡余杭人，凌统之父，随孙策转战江东。孙权统军后，凌操随其征伐黄祖，被甘宁射杀。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_ceshi.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204220, nil, 204230, "999|0,999|0,999|0", 1000, 2, 9100, 9000, 7600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10162, 3, 1000, 5000, 10000, 500, 500, "200,3601,1803,9302,3705,", nil, "1623,1621,1622,1624", nil, },
	id_60163 = {60163, "橙卡邢道荣", "邢道荣", 1, "1", "邢茂，字道荣，零陵太守刘度部下上将。邢道荣据称能力敌万人。奉刘度之命与前来收取荆南的刘备军作战，失败遭擒。", "zhan_jiang_handang.png", "head_handang.png", "quan_jiang_wujiang1.png", "quan_jiang_handang.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204240, nil, 204250, "999|0,999|0,999|0", 1000, 2, 8900, 9600, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10163, 3, 1000, 5000, 10000, 500, 500, "200,401,9503,9302,5805,", nil, "1631,1632,1633,1634", nil, },
	id_60164 = {60164, "橙卡张允", "张允", 1, "1", "张允。东汉末年人物，本为荆州牧刘表的部将，同时也是刘表少子刘琮的支党。后归曹操。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_ceshi.png", "quan_jiang_wenguan3.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204260, nil, 204270, "999|0,999|0,999|0", 1000, 2, 8200, 7700, 9000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10164, 1, 1000, 5000, 10000, 500, 500, "300,301,4503,9302,2505,", nil, "1642,1631,1641,1643", nil, },
	id_60165 = {60165, "橙卡黄祖", "黄祖", 1, "1", "黄祖，东汉末年荆州牧刘表部下的江夏太守。在与长沙太守孙坚交战时其部下将孙坚射死，因此与孙家结下仇怨；之后黄祖在208年与孙权的交战中败北，被杀。", "zhan_jiang_wenguan4.png", "head_wenguan4.png", "quan_jiang_ceshi.png", "quan_jiang_wenguan4.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204280, nil, 204290, "999|0,999|0,999|0", 900, 2, 8500, 7100, 8400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10165, 4, 1000, 5000, 10000, 500, 500, "200,1201,4003,9302,6105,", nil, "1632,1651,1652", nil, },
	id_60166 = {60166, "橙卡蔡瑁", "蔡瑁", 1, "1", "蔡瑁，字德珪，襄阳蔡州人。东汉末年荆州名族，蔡讽之子，姑母是太尉张温之妻，长姐与二姐先后嫁给黄承彦与刘表成为继室。", "zhan_jiang_caimao.png", "head_caimao.png", "quan_jiang_ceshi.png", "quan_jiang_caimao.png", "nuqi_caimao.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204300, nil, 204310, "999|0,999|0,999|0", 1000, 2, 9300, 8800, 7100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10166, 4, 1000, 5000, 10000, 500, 500, "200,4701,5603,9302,,", nil, "1661,1633,1641,1662", nil, },
	id_60167 = {60167, "橙卡丁奉", "丁奉", 1, "1", "丁奉，字承渊，庐江安丰人，三国时期吴国重要将领。年少时以骁勇为小将，经常奋勇杀敌，屡立功勋，东兴之战中“雪中奋短兵”，大破进犯东吴的魏军。", "zhan_jiang_dingfeng.png", "head_dingfeng.png", "quan_jiang_ceshi.png", "quan_jiang_dingfeng.png", "nuqi_dingfeng.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204320, nil, 204330, "999|0,999|0,999|0", 1100, 2, 9400, 9600, 7600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10167, 3, 1000, 5000, 10000, 500, 500, "200,1101,5603,9302,1105,5207", nil, "1621,1671,1672,1673,1674", nil, },
	id_60168 = {60168, "橙卡潘璋", "潘璋", 1, "1", "潘璋，字文珪，东郡发干人。三国时期吴国将领。潘璋年轻时家贫，跟随孙权后得到其赏识，加上其作战勇猛，不断升迁，其一生为孙权东征西讨，在合肥战役、追擒关羽、夷陵之战、江陵保卫战中多次立下战功。", "zhan_jiang_panzhang.png", "head_panzhang.png", "quan_jiang_ceshi.png", "quan_jiang_panzhang.png", "nuqi_panzhang.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204340, nil, 204350, "999|0,999|0,999|0", 1100, 2, 9200, 9800, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10168, 3, 1000, 5000, 10000, 500, 500, "200,1201,9003,9302,3805,5607", nil, "1622,1671,1681,1682,1683", nil, },
	id_60169 = {60169, "橙卡董袭", "董袭", 1, "1", "董袭，字元代，会稽馀姚人。东汉末年江东孙氏部将。跟随孙策讨伐山阴宿贼黄龙罗、周勃等，策自出讨，袭身斩罗、勃首，迁扬武都尉。", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_ceshi.png", "quan_jiang_wenguan1.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204360, nil, 204370, "999|0,999|0,999|0", 800, 2, 7300, 6700, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10169, 4, 1000, 5000, 10000, 500, 500, "200,3601,7803,9302,,", nil, nil, nil, },
	id_60170 = {60170, "橙卡孙桓", "孙桓", 1, "1", "三国时期吴国的建武将军。孙河的第三子，仪容端正，器怀聪明，博学强记，能论议应对，孙权常称为宗室颜渊，擢为武卫都尉。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_ceshi.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204380, nil, 204390, "999|0,999|0,999|0", 1000, 2, 8700, 9200, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10170, 3, 1000, 5000, 10000, 500, 500, "200,9101,1103,9302,3505,", nil, "1703,1701,1702", nil, },
	id_60171 = {60171, "橙卡朱异", "朱异", 1, "1", "朱异，字季文，吴郡吴县人，是朱桓之子。因而被任命为郎官，后为骑都尉，代父领兵。在攻打樊城和文钦期间先后任偏将军、扬武将军。", "zhan_jiang_zhuyi.png", "head_zhuyi.png", "quan_jiang_ceshi.png", "quan_jiang_zhuyi.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204400, nil, 204410, "999|0,999|0,999|0", 700, 2, 7100, 6500, 7700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10171, 3, 1000, 5000, 10000, 500, 500, "200,1101,2303,9302,,", nil, nil, nil, },
	id_60172 = {60172, "橙卡朱桓", "朱桓", 1, "1", "朱桓，字休穆，吴郡吴县人，三国时期吴国名将，官至前将军、青州牧，假节，封为嘉兴侯。", "zhan_jiang_zhuhuan.png", "head_zhuhuan.png", "quan_jiang_ceshi.png", "quan_jiang_zhuhuan.png", "nuqi_zhuhuan.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204420, nil, 204430, "999|0,999|0,999|0", 1200, 2, 10100, 10000, 7800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10172, 3, 1000, 5000, 10000, 500, 500, "201,4201,9503,9302,3205,4307", nil, "1672,1681,1721,1722,1723", nil, },
	id_60173 = {60173, "橙卡张武", "张武", 1, "1", "荆州边患的主将，为刘表心腹大患。后刘备依附刘表后被刘备铲除。同时张武也是的卢马第一任主人。", "zhan_jiang_wujiang5.png", "head_wujiang5.png", "quan_jiang_ceshi.png", "quan_jiang_wujiang5.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204440, nil, 204450, "999|0,999|0,999|0", 900, 2, 8300, 8800, 6400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10173, 4, 1000, 5000, 10000, 500, 500, "200,3601,9403,9302,4805,", nil, "1702,1731,1732", nil, },
	id_60174 = {60174, "橙卡贾逵", "贾逵", 1, "1", "贾逵，字梁道，本名衢，河东襄陵人。他历仕曹操、曹丕、曹叡三世，是曹魏政权中具有政治、军事才干的人物，终其一生为曹魏的统一事业作出贡献。", "zhan_jiang_wenguan2.png", "head_wenguan2.png", "quan_jiang_ceshi.png", "quan_jiang_wenguan2.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204460, nil, 204470, "999|0,999|0,999|0", 1000, 2, 7900, 7300, 9300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10174, 1, 1000, 5000, 10000, 500, 500, "200,1101,9503,9302,,", nil, nil, nil, },
	id_60175 = {60175, "橙卡夏侯渊", "夏侯渊", 1, "1", "夏侯渊，字妙才，沛国谯人，东汉末年名将，擅长千里奔袭作战，官至征西将军，封博昌亭侯。率军驻凉州，逐马超、破韩遂、灭宋建、横扫羌、氐，虎步关右。", "zhan_jiang_xiahouyuan.png", "head_xiahouyuan.png", "quan_jiang_ceshi.png", "quan_jiang_xiahouyuan.png", "nuqi_xiahouyuan.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204480, nil, 204490, "999|0,999|0,999|0", 1200, 2, 9700, 9700, 8100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10175, 1, 1000, 5000, 10000, 500, 500, "101,5601,3503,9302,6005,7807", nil, "1751,1752,1753,1754,1755", nil, },
	id_60176 = {60176, "橙卡备用武将30", "备用武将30", 1, "1", "备用武将30", "zhan_jiang_guanyu.png", "head_guanyu.png", "quan_jiang_wujiang1.png", "quan_jiang_guanyu.png", "nuqi_guanyu.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204500, nil, 204510, "999|0,999|0,999|0", 600, 2, 7000, 6800, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10176, nil, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_60177 = {60177, "橙卡备用武将31", "备用武将31", 1, "1", "备用武将31", "zhan_jiang_zhangfei.png", "head_zhangfei.png", "quan_jiang_zhangfei.png", "quan_jiang_zhangfei.png", "nuqi_zhangfei.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204520, nil, 204530, "999|0,999|0,999|0", 600, 2, 7000, 6800, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10177, nil, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_60178 = {60178, "橙卡备用武将32", "备用武将32", 1, "1", "备用武将32", "zhan_jiang_liubei.png", "head_liubei.png", "quan_jiang_wenguan3.png", "quan_jiang_liubei.png", "nuqi_liubei.png", "16", nil, nil, nil, nil, 2, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204540, nil, 204550, "999|0,999|0,999|0", 600, 2, 7000, 6800, 6800, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10178, nil, 1000, 5000, 10000, 500, 500, nil, nil, nil, nil, },
	id_60179 = {60179, "橙卡法正", "法正", 1, "1", "法正，字孝直，扶风郡人。原为刘璋部下，刘备围成都时劝说刘璋投降，而后又与刘备进取汉中，献计将曹操大将夏侯渊斩首。法正善奇谋，深受刘备信任和敬重。", "zhan_jiang_fazheng.png", "head_fazheng.png", "quan_jiang_ceshi.png", "quan_jiang_fazheng.png", "nuqi_fazheng.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204560, nil, 204570, "999|0,999|0,999|0", 1100, 2, 8900, 8100, 10100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10179, 2, 1000, 5000, 10000, 500, 500, "901,5701,6003,9302,1105,4007", nil, "81,92,322,1791,1792", nil, },
	id_60180 = {60180, "橙卡蒯良", "蒯良", 1, "1", "蒯良，字子柔，南郡中庐人。归刘表。蒯良为刘表定下安抚荆楚的政治方向，佐其成业，被刘表誉为“雍季之论”。", "zhan_jiang_wenguan1.png", "head_wenguan1.png", "quan_jiang_ceshi.png", "quan_jiang_wenguan1.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204580, nil, 204590, "999|0,999|0,999|0", 900, 2, 7800, 7200, 9300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10180, 4, 1000, 5000, 10000, 500, 500, "300,3601,9403,9302,4703,", nil, "1651,1731,1801,1802", nil, },
	id_60181 = {60181, "橙卡蒯越", "蒯越", 1, "1", "蒯越，字异度，襄阳中庐人。东汉末期人物，为蒯良之弟。原本是荆州牧刘表的部下，曾经在刘表初上任时帮助刘表铲除荆州一带的宗贼。", "zhan_jiang_wenguan2.png", "head_wenguan2.png", "quan_jiang_ceshi.png", "quan_jiang_wenguan2.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204600, nil, 204610, "999|0,999|0,999|0", 1000, 2, 8200, 7700, 9000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10181, 4, 1000, 5000, 10000, 500, 500, "201,1101,5103,9302,4205,", nil, "1811,1801,1812", nil, },
	id_60182 = {60182, "橙卡刘琮", "刘琮", 1, "1", "东汉末年荆州牧刘表次子，刘琦之弟。刘表死后继承刘表官爵，当曹操大军南下之时，他在蔡瑁等人的劝说之下举荆州而降，被曹操封为青州刺史，封列侯。", "zhan_jiang_wenguan3.png", "head_wenguan3.png", "quan_jiang_ceshi.png", "quan_jiang_wenguan3.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204620, nil, 204630, "999|0,999|0,999|0", 900, 2, 7800, 7200, 8600, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10182, 4, 1000, 5000, 10000, 500, 500, "400,5701,3203,9302,,", nil, nil, nil, },
	id_60183 = {60183, "橙卡荀彧", "荀彧", 1, "1", "曹操统一北方的首席谋臣和功臣，自小即有人称其为“王佐之才”。荀彧在战略上为曹操制定并规划了统一北方的蓝图和军事路线，曾多次修正曹操的战略方针而得到曹操的赞赏。", "zhan_jiang_xunyu.png", "head_xunyu.png", "quan_jiang_ceshi.png", "quan_jiang_xunyu.png", "nuqi_xunyu.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204640, nil, 204650, "999|0,999|0,999|0", 1200, 2, 9200, 8600, 10200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10183, 1, 1000, 5000, 10000, 500, 500, "1001,5701,3603,9302,1805,5807", nil, "1832,1833,1831,1834,1835", nil, },
	id_60184 = {60184, "橙卡司马昭", "司马昭", 1, "1", "三国时期曹魏权臣，西晋王朝的奠基人之一。他是宣帝司马懿与张春华的次子，司马师的弟弟，西晋开国皇帝司马炎的父亲。早年随父抗蜀，多有战功。景初二年，封新城乡侯。", "zhan_jiang_simazhao.png", "head_simazhao.png", "quan_jiang_ceshi.png", "quan_jiang_simazhao.png", "nuqi_simazhao.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204660, nil, 204670, "999|0,999|0,999|0", 1100, 2, 8700, 8400, 10000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10184, 1, 1000, 5000, 10000, 500, 500, "901,1101,3903,9302,10105,3807", nil, "1842,1843,1841,1844,1845", nil, },
	id_60185 = {60185, "橙卡杨修", "杨修", 1, "1", "东汉末年的文学家。杨修学问渊博，极聪慧，任丞相府主簿。因太过了解曹操，曹操恼羞成怒借“鸡肋”之事将其杀害。", "zhan_jiang_yangxiu.png", "head_yangxiu.png", "quan_jiang_ceshi.png", "quan_jiang_yangxiu.png", "nuqi_yangxiu.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204680, nil, 204690, "999|0,999|0,999|0", 1100, 2, 8500, 7500, 9100, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10185, 1, 1000, 5000, 10000, 500, 500, "1001,9501,3603,9302,4305,4707", nil, "1851,1852,1841,1853,1854", nil, },
	id_60186 = {60186, "橙卡蒋干", "蒋干", 1, "1", "曹操手下的名士，被曹操派去说降周瑜，却被周瑜用反间计将计就计，令曹操误杀了蔡瑁、张允。", "zhan_jiang_jianggan.png", "head_jianggan.png", "quan_jiang_ceshi.png", "quan_jiang_jianggan.png", "nuqi_jianggan.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204700, nil, 204710, "999|0,999|0,999|0", 900, 2, 8000, 7400, 8900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10186, 1, 1000, 5000, 10000, 500, 500, "301,1101,4203,9302,6005,9207", nil, "1862,1861,1863,1864", nil, },
	id_60187 = {60187, "橙卡关索", "关索", 1, "1", "关羽第三子，在荆州与关羽相认，父子相拥而泣。为人肝胆侠义，多受百姓爱戴。", "zhan_jiang_guansuo.png", "head_guansuo.png", "quan_jiang_ceshi.png", "quan_jiang_guansuo.png", "nuqi_guansuo.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204720, nil, 204730, "999|0,999|0,999|0", 1100, 2, 9200, 9800, 7200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10187, 2, 1000, 5000, 10000, 500, 500, "901,3701,3203,9302,9005,5907", nil, "1872,1873,1871,1874,1875", nil, },
	id_60188 = {60188, "橙卡马谡", "马谡", 1, "1", "三国时期蜀汉大臣，侍中马良之弟。蜀汉丞相诸葛亮用为参军。马谡“才器过人”，好论军计。诸葛亮向来对他倍加器重，每引见谈论，自昼达夜。", "zhan_jiang_masu.png", "head_masu.png", "quan_jiang_ceshi.png", "quan_jiang_masu.png", "nuqi_masu.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204740, nil, 204750, "999|0,999|0,999|0", 1100, 2, 8300, 7800, 9900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10188, 2, 1000, 5000, 10000, 500, 500, "1001,4201,5903,9302,3605,2507", nil, "1882,1883,1881,1884,1885", nil, },
	id_60189 = {60189, "橙卡费祎", "费祎", 1, "1", "三国时蜀汉名臣，与诸葛亮、蒋琬、董允并称为蜀汉四相。深得诸葛亮所器重，曾出使东吴，孙权、诸葛恪、羊茞等人以辞锋论难，而费祎据理以答，辞义兼至，始终不为所屈。", "zhan_jiang_feiyi.png", "head_feiyi.png", "quan_jiang_ceshi.png", "quan_jiang_feiyi.png", "nuqi_feiyi.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204760, nil, 204770, "999|0,999|0,999|0", 1200, 2, 9100, 8400, 10000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10189, 2, 1000, 5000, 10000, 500, 500, "1001,9501,3603,9302,5805,2007", nil, "1891,1892,1871,1893,1894", nil, },
	id_60190 = {60190, "橙卡严颜", "严颜", 1, "1", "东汉末年武将，初为刘璋部下，担任巴郡太守。江州之战中被张飞所擒，不畏生死，令张飞大为敬佩并将其释放。", "zhan_jiang_yanyan.png", "head_yanyan.png", "quan_jiang_ceshi.png", "quan_jiang_yanyan.png", "nuqi_yanyan.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204780, nil, 204790, "999|0,999|0,999|0", 1100, 2, 9600, 9500, 7900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10190, 2, 1000, 5000, 10000, 500, 500, "201,1101,3503,9302,6105,4807", nil, "1901,302,1881,1902,1903", nil, },
	id_60191 = {60191, "橙卡马腾", "马腾", 1, "1", "身长八尺馀，身体洪大，面鼻雄异，性格贤良忠厚，受到众人的尊敬。灵帝末年，被州郡署为军从事，掌领部众。因讨贼功，拜军司马，后迁偏将军。", "zhan_jiang_mateng.png", "head_mateng.png", "quan_jiang_ceshi.png", "quan_jiang_mateng.png", "nuqi_mateng.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204800, nil, 204810, "999|0,999|0,999|0", 1200, 2, 9500, 10100, 7400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10191, 4, 1000, 5000, 10000, 500, 500, "101,1101,5803,9302,5605,3607", nil, "1912,1913,1911,1914,1915", nil, },
	id_60192 = {60192, "橙卡诸葛恪", "诸葛恪", 1, "1", "三国时期东吴重臣，吴大将军诸葛瑾长子，蜀丞相诸葛亮之侄。从小就以神童著称，深受孙权赏识，弱冠拜骑都尉，孙登为太子时，诸葛恪为左辅都尉，为东宫幕僚领袖。", "zhan_jiang_zhugeke.png", "head_zhugeke.png", "quan_jiang_ceshi.png", "quan_jiang_zhugeke.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204820, nil, 204830, "999|0,999|0,999|0", 1000, 2, 8100, 7600, 9700, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10192, 3, 1000, 5000, 10000, 500, 500, "201,1101,4203,9302,5805,4907", nil, "1923,1921,1922,1924,1925", nil, },
	id_60193 = {60193, "橙卡蒋钦", "蒋钦", 1, "1", "汉末东吴名将。早年随孙策平定丹阳、吴郡、会稽和豫章四郡。平盗贼，迁西部都尉。讨会稽贼吕合、秦狼等，徙讨越中郎将。又与贺齐并力讨平黟贼。", "zhan_jiang_wujiang2.png", "head_wujiang2.png", "quan_jiang_ceshi.png", "quan_jiang_wujiang2.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204840, nil, 204850, "999|0,999|0,999|0", 900, 2, 8000, 7400, 8900, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10193, 3, 1000, 5000, 10000, 500, 500, "200,1101,5103,9302,6105,", nil, "1931,1932", nil, },
	id_60194 = {60194, "橙卡朱治", "朱治", 1, "1", "三国时期吴国武将，东吴名将朱然之嗣父。早年随从孙坚、孙策征伐，又辅助孙权，稳定江东，功勋卓著。", "zhan_jiang_wujiang3.png", "head_wujiang3.png", "quan_jiang_ceshi.png", "quan_jiang_wujiang3.png", nil, "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204860, nil, 204870, "999|0,999|0,999|0", 900, 2, 8300, 7000, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10194, 3, 1000, 5000, 10000, 500, 500, "200,3801,1203,9302,1105,", nil, nil, nil, },
	id_60195 = {60195, "橙卡左慈", "左慈", 1, "1", "东汉末方士，精通五经，懂占星，精于奇门遁甲，可御使鬼神。黄天魔教的创始者之一，立志复活黄天魔皇扰乱天下曾使用道术戏弄于曹操。传说丹鼎派道术是从他一脉相传。", "zhan_jiang_zuoci_1.png", "head_zuoci_1.png", "quan_jiang_zuoci.png", "quan_jiang_zuoci_1.png", "nuqi_zuoci_1.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201380, nil, 201390, "999|0,999|0,999|0", 1200, 2, 9900, 8600, 10400, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10020, 4, 1000, 5000, 10000, 500, 500, "1001,3601,8901,9302,31600,10209", "2|0|3800,2|3|9301,2|5|14304,2|5|16109", "201,202,93,203,204", nil, },
	id_60196 = {60196, "橙卡于吉", "于吉", 1, "1", "著名道士，黄天魔教的创始者之一，立志复活黄天魔皇扰乱天下，精通奇门遁甲，鬼神莫测。道教经典《太平经》的作者。", "zhan_jiang_yuji.png", "head_yuji.png", "quan_jiang_wenguan2.png", "quan_jiang_yuji.png", "nuqi_yuji.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 201400, nil, 201410, "999|0,999|0,999|0", 1200, 2, 9800, 8600, 10300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10021, 4, 1000, 5000, 10000, 500, 500, "1001,1101,10005,9302,31600,10109", "2|3|9301,2|5|9304,2|5|9309", "211,212,93,213,214", nil, },
	id_60197 = {60197, "橙卡公孙瓒", "公孙瓒", 1, "1", "字伯珪，辽西令支人，出身贵族。因母地位卑贱，只当了郡中小吏。他貌美，声音洪亮，机智善辩。涿郡刘太守很赏识，将女儿许配给他。后逐步做到中郎将，以强硬的态度对抗北方游牧民族，作战勇猛，威震边疆。", "zhan_jiang_gongsunzan.png", "head_gongsunzan.png", "quan_jiang_ceshi.png", "quan_jiang_gongsunzan.png", "nuqi_gongsunzan.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204920, nil, 204930, "999|0,999|0,999|0", 1100, 2, 9600, 9600, 8000, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10197, 4, 1000, 5000, 10000, 500, 500, "201,5601,10203,9302,1105,3707", nil, "1971,1972,1911,1973,1974", nil, },
	id_60198 = {60198, "橙卡邓艾", "邓艾", 1, "1", "字士载，义阳棘阳人。三国时期魏国杰出的军事家、将领。其人文武全才，深谙兵法，对内政也颇有建树。邓艾多年在曹魏西边战线防备蜀汉姜维。", "zhan_jiang_dengai.png", "head_dengai.png", "quan_jiang_ceshi.png", "quan_jiang_dengai.png", "nuqi_dengai.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204940, nil, 204950, "999|0,999|0,999|0", 1200, 2, 9800, 10100, 8200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10198, 1, 1000, 5000, 10000, 500, 500, "101,1101,5603,9302,3805,1807", nil, "1982,1983,1981,1984,1985", nil, },
	id_60199 = {60199, "橙卡庞统", "庞统", 1, "1", "字士元，号凤雏，汉时荆州襄阳人。刘备手下的重要谋士，曾在赤壁之战以连环计协助孙刘联军，与诸葛亮同拜为军师中郎将。与刘备一同入川，于刘备与刘璋决裂之际，献上上中下三条计策，刘备用其中计。", "zhan_jiang_pangtong.png", "head_pangtong.png", "quan_jiang_ceshi.png", "quan_jiang_pangtong.png", "nuqi_pangtong.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204960, nil, 204970, "999|0,999|0,999|0", 1200, 2, 8600, 8100, 10300, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10199, 2, 1000, 5000, 10000, 500, 500, "101,1201,1103,9302,2005,10207", nil, "1992,1991,83,1993,1994", nil, },
	id_60200 = {60200, "橙卡程昱", "程昱", 1, "1", "程昱，字仲德，兖州东郡东阿人，三国时魏国名臣。本名程立，因梦中於泰山捧日，更名程昱。曾于东阿率领民众抗击黄巾。初平三年，被曹操征辟为寿张令，从此为其出谋献策、征战四方。", "zhan_jiang_chengyu.png", "head_chengyu.png", "quan_jiang_ceshi.png", "quan_jiang_chengyu.png", "nuqi_chengyu.png", "16", nil, nil, nil, nil, 3, 9, 7, nil, nil, nil, nil, nil, nil, nil, 204980, nil, 204990, "999|0,999|0,999|0", 1100, 2, 8500, 8000, 10200, nil, nil, nil, nil, nil, nil, nil, nil, nil, 6, nil, nil, 6, nil, 10200, 1, 1000, 5000, 10000, 500, 500, "301,3601,5903,9302,6605,2007", nil, "1831,1861,1981,2001,2002", nil, },
}

local mt = {}
mt.__index = function (table, key)
	for i = 1, #keys do
		if (keys[i] == key) then
			return table[i]
		end
	end
end

function getDataById(key_id)
	local id_data = Monsters_tmpl["id_" .. key_id]
	if id_data == nil then
		print("don't find data by id " .. key_id)
		return nil
	end
	if getmetatable(id_data) ~= nil then
		return id_data
	end
	setmetatable(id_data, mt)

	return id_data
end

function getArrDataByField(fieldName, fieldValue)
	local arrData = {}
	local fieldNo = 1
	for i=1, #keys do
		if keys[i] == fieldName then
			fieldNo = i
			break
		end
	end
	for k, v in pairs(Monsters_tmpl) do
		if v[fieldNo] == fieldValue then
			setmetatable (v, mt)
			arrData[#arrData+1] = v
		end
	end

	return arrData
end

